import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    DateDebut: '',
    DateFin: '',
    motif: '',
    statut: 'En attente', // Le statut est défini par défaut
  });
  
  const [userInfo, setUserInfo] = useState({
    nom: '',
    prenom: '',
    email: ''
  });
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ show: false, type: '', message: '' });

  useEffect(() => {
    // Récupérer les informations utilisateur depuis le localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUserInfo({
        nom: userData.nom || '',
        prenom: userData.prenom || '',
        email: userData.email || ''
      });
    }
  }, []);

  const calculateDays = () => {
    if (!formData.DateDebut || !formData.DateFin) return 0;

    const start = new Date(formData.DateDebut);
    const end = new Date(formData.DateFin);
    
    if (start > end) {
      setAlert({
        show: true,
        type: 'error',
        message: 'La date de début ne peut pas être après la date de fin.'
      });
      return 0;
    }

    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    return diffDays;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Validation des champs obligatoires
    if (!formData.DateDebut || !formData.DateFin || !formData.motif) {
      setAlert({
        show: true,
        type: 'error',
        message: 'Tous les champs obligatoires doivent être remplis'
      });
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      
      const requestData = {
        ...formData,
        ...userInfo
      };

      const response = await axios.post('/adddemandeconge', requestData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setConfirmationMessage(response.data.message);
      setShowConfirmation(true);
      
      // Réinitialisez le formulaire après succès
      setFormData({
        DateDebut: '',
        DateFin: '',
        motif: '',
        statut: 'En attente'
      });

    } catch (error) {
      console.error('Erreur:', error.response?.data || error.message);
      setAlert({
        show: true,
        type: 'error',
        message: error.response?.data?.message || 'Une erreur s\'est produite'
      });
    } finally {
      setLoading(false);
    }
  };

  // Styles (identique à votre version précédente)
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: '"Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      color: '#334155'
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      paddingTop: '8rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '2.2rem', 
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '17px',
      color: '#64748b',
      marginTop: '10px',
      margin: '0',
      letterSpacing: '0.3px'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '35px',
      marginBottom: '35px',
      boxShadow: '0 4px 10px rgba(0,0,0,0.06)',
      border: '1px solid rgba(0,0,0,0.04)'
    },
    formHeader: {
      marginBottom: '25px',
      borderBottom: '1px solid #e2e8f0',
      paddingBottom: '15px'
    },
    formTitle: {
      fontSize: '22px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 8px 0'
    },
    formDescription: {
      fontSize: '15px',
      color: '#64748b',
      margin: '0'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      fontSize: '15px',
      fontWeight: '600',
      color: '#475569',
      marginBottom: '8px'
    },
    input: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '20px'
    },
    selectContainer: {
      position: 'relative'
    },
    select: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease',
      appearance: 'none',
      paddingRight: '40px',
      backgroundColor: '#ffffff'
    },
    selectArrow: {
      position: 'absolute',
      right: '15px',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#64748b',
      pointerEvents: 'none',
      width: '12px',
      height: '12px',
      borderLeft: '2px solid #64748b',
      borderBottom: '2px solid #64748b',
      transform: 'translateY(-70%) rotate(-45deg)'
    },
    congeInfoTag: {
      display: 'block',
      fontSize: '14px',
      color: '#64748b',
      marginTop: '5px'
    },
    textarea: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease',
      minHeight: '100px',
      resize: 'vertical'
    },
    alert: {
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      transition: 'opacity 0.3s',
      opacity: 1
    },
    alertError: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      borderLeft: '4px solid #b91c1c'
    },
    alertSuccess: {
      backgroundColor: '#dcfce7',
      color: '#16a34a',
      borderLeft: '4px solid #16a34a'
    },
    alertIcon: {
      fontSize: '18px',
      marginRight: '10px'
    },
    alertContent: {
      flex: 1
    },
    alertClose: {
      backgroundColor: 'transparent',
      border: 'none',
      cursor: 'pointer',
      fontSize: '18px',
      color: 'inherit',
      padding: '0 10px'
    },
    buttonContainer: {
      marginTop: '25px'
    },
    button: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      boxShadow: '0 2px 5px rgba(37, 99, 235, 0.3)'
    },
    buttonDisabled: {
      backgroundColor: '#93c5fd',
      cursor: 'not-allowed'
    },
    toastMessage: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      borderRadius: '12px',
      padding: '5px',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
      zIndex: '1000',
      animation: 'slideUp 0.3s ease, fadeOut 0.5s ease 2.5s',
      minWidth: '300px',
      maxWidth: '600px'
    },
    toastContent: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Congé
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>Remplissez les informations ci-dessous pour soumettre votre demande de congé.</p>
        </div>
        <div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Détails de votre demande de congé</h2>
            <p style={styles.formDescription}>
              Veuillez remplir tous les champs nécessaires avant de soumettre votre demande.
            </p>
          </div>
          <form onSubmit={handleSubmit}>
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de début</label>
                <input
                  type="date"
                  name="DateDebut"
                  value={formData.DateDebut}
                  onChange={handleInputChange}
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de fin</label>
                <input
                  type="date"
                  name="DateFin"
                  value={formData.DateFin}
                  onChange={handleInputChange}
                  style={styles.input}
                />
                <div style={styles.congeInfoTag}>
                  Nombre de jours: {calculateDays()}
                </div>
              </div>
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Motif de congé</label>
              <textarea
                name="motif"
                value={formData.motif}
                onChange={handleInputChange}
                style={styles.textarea}
              />
            </div>
            <div style={styles.buttonContainer}>
              <button
                type="submit"
                style={{
                  ...styles.button,
                  ...(loading ? styles.buttonDisabled : {})
                }}
                disabled={loading}
              >
                {loading ? 'Soumission en cours...' : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
        {alert.show && (
          <div
            style={{
              ...styles.alert,
              ...(alert.type === 'error' ? styles.alertError : styles.alertSuccess)
            }}
          >
            <div style={styles.alertContent}>
              <strong>{alert.type === 'error' ? 'Erreur' : 'Succès'}:</strong> {alert.message}
            </div>
            <button
              style={styles.alertClose}
              onClick={() => setAlert({ show: false, type: '', message: '' })}
            >
              &times;
            </button>
          </div>
        )}
        {showConfirmation && (
          <div style={styles.toastMessage}>
            <div style={styles.toastContent}>
              <strong>Succès! </strong>
              {confirmationMessage}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CardDemandeConge;
