      nom,
      prenom,
      email,
      nomFormation,
      idFormation,
      idUser,
      statut: 'En attente'
    });
    await nouvelleDemande.save();
    res.status(201).json({
      message: "Demande créée avec succès",
      demande: nouvelleDemande
    });
  } catch (error) {
    res.status(500).json({
      message: "Erreur serveur",
      error: error.message
    });
  }
};
