import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X, Eye } from "lucide-react";
import axios from "axios";

// CSS encapsulé avec préfixe "gf-" (Gestion Formations)
const encapsulatedStyles = `
  .gf-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    position: relative;
    width: 100%;
  }
  
  .gf-header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .gf-title {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
    color: #1f2937;
  }
  
  .gf-search-container {
    position: relative;
    margin-bottom: 1rem;
  }
  
  .gf-search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .gf-search-input {
    width: 100%;
    padding: 0.5rem 0.75rem 0.5rem 2.5rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
    font-size: 0.875rem;
  }
  
  .gf-search-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gf-table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  .gf-table {
    width: 100%;
    border-collapse: collapse;
  }
  
  .gf-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gf-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gf-tr {
    transition: background-color 0.2s ease;
  }
  
  .gf-tr:hover {
    background-color: #f9fafb;
  }
  
  .gf-nom {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gf-prenom {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gf-formation-titre {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gf-date {
    font-size: 0.75rem;
