import { useState, useEffect } from 'react';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [salaire, setSalaire] = useState(0);
  const [montantMax, setMontantMax] = useState(0);

  useEffect(() => {
    // Debug: Afficher toutes les clés dans localStorage pour savoir où chercher le token
    console.log("Contenu de localStorage:", Object.keys(localStorage));
    
    // Approche 1: Vérifier le token directement
    const storedToken = localStorage.getItem('token');
    
    // Approche 2: Vérifier dans userInfo
    let userInfoToken = null;
    let userSalaire = 0;
    try {
      const userInfo = localStorage.getItem('userInfo');
      if (userInfo) {
        const parsedUserInfo = JSON.parse(userInfo);
        if (parsedUserInfo && parsedUserInfo.token) {
          userInfoToken = parsedUserInfo.token;
        }
        if (parsedUserInfo && parsedUserInfo.salaire) {
          userSalaire = parseFloat(parsedUserInfo.salaire);
        }
      }
    } catch (e) {
      console.error("Erreur dans la lecture de userInfo:", e);
    }
    
    // Approche 3: Rechercher dans d'autres clés courantes
    const authToken = localStorage.getItem('authToken');
    const jwtToken = localStorage.getItem('jwt');
    const accessToken = localStorage.getItem('accessToken');
    
    // Rechercher le salaire dans d'autres clés potentielles
    const storedSalaire = localStorage.getItem('salaire');
    const userSalaireRaw = localStorage.getItem('userSalaire');
    
    // Utiliser le premier salaire trouvé
    const finalSalaire = userSalaire || 
                        (storedSalaire ? parseFloat(storedSalaire) : 0) || 
                        (userSalaireRaw ? parseFloat(userSalaireRaw) : 0);
    
    // Calculer le montant maximum (30% du salaire)
    const maxAmount = finalSalaire * 0.3;
    
    // Utiliser le premier token trouvé
    const finalToken = storedToken || userInfoToken || authToken || jwtToken || accessToken;
    
    if (finalToken) {
      console.log("Token trouvé dans localStorage");
      setToken(finalToken);
      setIsAuthenticated(true);
    } else {
      console.warn("Aucun token trouvé dans localStorage");
      
      // Forcer l'authentification pour le développement si nécessaire
      // Commenter cette ligne en production
      setIsAuthenticated(true); // Pour tester sans token valide
    }
    
    setSalaire(finalSalaire);
    setMontantMax(maxAmount);
    console.log("Salaire récupéré:", finalSalaire, "Montant max:", maxAmount);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Réinitialiser l'erreur lorsque l'utilisateur modifie le montant
    if (name === 'montant') {
      setError(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }
      
      // Vérifier que le montant est inférieur ou égal à 30% du salaire
      const montantDemande = parseFloat(formData.montant);
      if (montantDemande > montantMax) {
        throw new Error(`Le montant demandé dépasse 30% de votre salaire. Maximum autorisé: ${montantMax.toFixed(2)} TND`);
      }

      // Simuler une soumission pour la démonstration
      // Dans un cas réel, vous utiliseriez fetch ou une autre méthode pour envoyer au serveur
      setTimeout(() => {
        setSuccess(true);
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
        setLoading(false);
      }, 1000);
    } catch (err) {
      setError(err.message || 'Une erreur est survenue');
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-w-xl mx-auto p-5 bg-gray-50 font-sans">
      <div className="text-center mb-5 py-5">
        <h1 className="text-2xl font-bold text-blue-800 mb-2 relative inline-block pb-4">
          Demande d'avance sur salaire
          <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
        </h1>
        <p className="text-sm text-gray-500 mt-2 tracking-wide">Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {error && <div className="text-red-600 mb-3 text-sm">{error}</div>}
      {success && <div className="text-green-600 mb-3 text-sm">Votre demande a été soumise avec succès!</div>}

      <div className="bg-white rounded-lg p-6 shadow-sm">
        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            className="w-full p-3 rounded border border-gray-200 text-sm"
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
            max={montantMax || undefined}
          />
          <p className="text-xs text-gray-500 mt-1 italic">
            Le montant maximum est de 30% de votre salaire mensuel
            {montantMax > 0 && ` (${montantMax.toFixed(2)} TND)`}
          </p>
        </div>

        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            className="w-full p-3 rounded border border-gray-200 text-sm bg-white"
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            className="w-full p-3 rounded border border-gray-200 text-sm min-h-24 resize-y"
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          onClick={handleSubmit}
          className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white rounded font-medium text-base mt-3 transition-colors duration-300"
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </div>
    </div>
  );
};

export default CardDemandeAvance;
