import React from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";

export default function Index() {
  return (
    <>
      <IndexNavbar fixed />
      <div className="home-container">
        {/* Hero Section */}
        <section className="hero-section">
          <div className="content-wrapper">
            <div className="hero-content">
              <h1 className="hero-title">
                <span className="primary-text">UBCI</span> HR MANAGER
              </h1>
              <p className="hero-description">
                UBCI RH est une plateforme dédiée à la gestion des ressources humaines de la banque UBCI, 
                facilitant le suivi du personnel, la gestion des congés et l'automatisation des tâches administratives.
              </p>
              <div className="action-buttons">
                <Link to="/postule" className="btn-primary">
                  Postuler
                </Link>
                <Link to="/signin" className="btn-secondary">
                  Mon Espace
                </Link>
              </div>
            </div>
            <div className="hero-image-container">
              <img
                src={require("assets/img/ubci.jpg").default}
                alt="UBCI Banque"
                className="hero-image"
              />
              <div className="image-overlay"></div>
            </div>
          </div>
          <div className="background-shape shape1"></div>
          <div className="background-shape shape2"></div>
          <div className="background-shape shape3"></div>
        </section>

        {/* Features Section */}
        <section className="features-section">
          <div className="section-container">
            <h2 className="section-title">Nos services RH</h2>
            <div className="features-grid">
              {/* Feature 1 */}
              <div className="feature-card">
                <div className="feature-icon personnel-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                    <circle cx="9" cy="7" r="4"></circle>
                    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
                  </svg>
                </div>
                <h3 className="feature-title">Gestion du personnel</h3>
                <p className="feature-description">
                  Suivez les informations personnelles, les performances et l'évolution de carrière de chaque employé.
                </p>
              </div>
              
              {/* Feature 2 */}
              <div className="feature-card">
                <div className="feature-icon calendar-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect>
                    <line x1="16" y1="2" x2="16" y2="6"></line>
                    <line x1="8" y1="2" x2="8" y2="6"></line>
                    <line x1="3" y1="10" x2="21" y2="10"></line>
                  </svg>
                </div>
                <h3 className="feature-title">Gestion des congés</h3>
                <p className="feature-description">
                  Gérez les demandes de congés, les validations et le suivi des soldes de façon efficace.
                </p>
              </div>
              
              {/* Feature 3 */}
              <div className="feature-card">
                <div className="feature-icon document-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                    <polyline points="14 2 14 8 20 8"></polyline>
                    <line x1="16" y1="13" x2="8" y2="13"></line>
                    <line x1="16" y1="17" x2="8" y2="17"></line>
                    <polyline points="10 9 9 9 8 9"></polyline>
                  </svg>
                </div>
                <h3 className="feature-title">Documentation RH</h3>
                <p className="feature-description">
                  Accédez facilement à tous les documents administratifs et formulaires RH nécessaires.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* CTA Section */}
        <section className="cta-section">
          <div className="cta-container">
            <h2 className="cta-title">Rejoignez l'équipe UBCI</h2>
            <p className="cta-description">
              Découvrez les opportunités de carrière au sein d'une institution financière de premier plan
            </p>
            <div className="cta-buttons">
              <Link to="/postule" className="cta-btn-primary">
                Voir les offres d'emploi
              </Link>
              <Link to="/signin" className="cta-btn-outline">
                Espace employé
              </Link>
            </div>
          </div>
          <div className="cta-bg-shape"></div>
        </section>

