        <section className="stats-section">
          <div className="stats-container">
            <div className="stats-item">
              <span className="stats-number">1500+</span>
              <span className="stats-label">Employés</span>
            </div>
            <div className="stats-item">
              <span className="stats-number">60+</span>
              <span className="stats-label">Agences</span>
            </div>
            <div className="stats-item">
              <span className="stats-number">25+</span>
              <span className="stats-label">Années d'expertise</span>
            </div>
            <div className="stats-item">
              <span className="stats-number">95%</span>
              <span className="stats-label">Satisfaction</span>
            </div>
          </div>
        </section>

        {/* Footer */}
