import React, { useState, useRef } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // Déclaration de tous les états nécessaires
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState(''); // Ajouté
  const [submitError, setSubmitError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };
  
  const handleFileChange = (e) => {
    if (e.target.files?.[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };
  
  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSubmitError(null);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const response = await axios.post('/demandes', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setConfirmationMessage("Demande envoyée avec succès !");
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error);
      setSubmitError(
        error.response?.data?.message || 
        error.message || 
        "Erreur lors de l'envoi de la demande"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h1 style={{ textAlign: 'center', color: '#2563eb' }}>Demande de congé</h1>
      
      {submitError && (
        <div style={{ 
          color: 'red', 
          padding: '10px',
          margin: '10px 0',
          border: '1px solid red',
          borderRadius: '4px'
        }}>
          {submitError}
        </div>
      )}

      <form onSubmit={handleSubmit} style={{
        backgroundColor: '#f8fafc',
        padding: '20px',
        borderRadius: '8px'
      }}>
        <div style={{ marginBottom: '15px' }}>
          <label>Type de congé *</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '8px',
              marginTop: '5px'
            }}
          >
            <option value="congé payé">Congé payé</option>
            <option value="congé maladie">Congé maladie</option>
            <option value="RTT">RTT</option>
            <option value="congé sans solde">Congé sans solde</option>
          </select>
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label>Date de début *</label>
          <input
            type="date"
            name="startDate"
            value={formData.startDate}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '8px',
              marginTop: '5px'
            }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label>Date de fin *</label>
          <input
            type="date"
            name="endDate"
            value={formData.endDate}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '8px',
              marginTop: '5px'
            }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label>Motif</label>
          <input
            type="text"
            name="reason"
            value={formData.reason}
            onChange={handleChange}
            style={{
              width: '100%',
              padding: '8px',
              marginTop: '5px'
            }}
          />
        </div>

        {formData.type === 'congé maladie' && (
          <div style={{ marginBottom: '15px' }}>
            <label>Justificatif médical *</label>
            <div>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept=".pdf,.jpg,.jpeg,.png"
                required={formData.type === 'congé maladie'}
                style={{ marginTop: '5px' }}
              />
              {fileName && (
                <div>
                  <span>{fileName}</span>
                  <button 
                    type="button" 
                    onClick={removeFile}
                    style={{ 
                      marginLeft: '10px',
                      color: 'red',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer'
                    }}
                  >
                    Supprimer
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        <div style={{ marginBottom: '15px' }}>
          <label>Commentaires</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            style={{
              width: '100%',
              padding: '8px',
              marginTop: '5px',
              minHeight: '80px'
            }}
          />
        </div>

        <button 
          type="submit" 
          disabled={loading}
          style={{
            width: '100%',
            padding: '10px',
            backgroundColor: '#2563eb',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {loading ? 'Envoi en cours...' : 'Envoyer la demande'}
        </button>
      </form>

      {showConfirmation && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          backgroundColor: '#4ade80',
          color: 'white',
          padding: '10px 20px',
          borderRadius: '4px',
          animation: 'fadeIn 0.3s'
        }}>
          {confirmationMessage}
        </div>
      )}

      <style>
        {`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        `}
      </style>
    </div>
  );
};

export default CardDemandeConge;
