import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard',
    statut: 'En attente'
  });
  const [loading, setLoading] = useState(false);
  const [token, setToken] = useState('');
  const [userSalaire, setUserSalaire] = useState(2000); // Valeur par défaut pour le test
  const [userData, setUserData] = useState(null);
  const [toast, setToast] = useState({
    show: false,
    message: '',
    type: 'success'
  });

  useEffect(() => {
    const userDataStr = localStorage.getItem('userData');
    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData(userData);
        
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) setToken(authToken);
        
        if (userData.salaire) {
          setUserSalaire(userData.salaire);
        } else {
          showToast("Impossible de déterminer votre salaire. Contactez l'administration.", 'error');
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        showToast("Erreur de chargement de vos données. Veuillez rafraîchir la page.", 'error');
      }
    }
  }, []);

  const showToast = (message, type = 'success') => {
    console.log(`Toast: ${type} - ${message}`);
    
    // Déterminer si c'est le message spécifique pour l'erreur d'avance existante
    const isExistingAdvanceError = message.includes("Vous avez déjà une demande d'avance ce mois-ci");
    
    setToast({
      show: true,
      message,
      type: isExistingAdvanceError ? 'error' : type
    });
    setTimeout(() => {
      setToast(prev => ({ ...prev, show: false }));
    }, 5000);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'montant') {
      const montant = parseFloat(value);
      if (montant > userSalaire) {
        showToast(`Le montant demandé (${montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`, 'error');
      }
    }
    
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.montant || !formData.motif) {
      showToast("Veuillez remplir tous les champs obligatoires", 'error');
      return;
    }
    
    const montant = parseFloat(formData.montant);
    if (isNaN(montant)) {
      showToast("Veuillez entrer un montant valide", 'error');
      return;
    }
    
    if (montant <= 0) {
      showToast("Le montant doit être supérieur à 0", 'error');
      return;
    }
    
    if (montant > userSalaire) {
      showToast(`Le montant demandé (${montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`, 'error');
      return;
    }
    
    setLoading(true);

    try {
      // Simulation d'une requête réussie
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Pour les tests, on simule une réponse réussie
      showToast('Demande créée avec succès');
      setFormData({
        montant: '',
        motif: '',
        type: 'standard',
        statut: 'En attente'
      });
      
    } catch (err) {
      console.error("Erreur:", err);
      showToast("Une erreur est survenue lors de la soumission", 'error');
    } finally {
      setLoading(false);
    }
  };

  // Styles améliorés
  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif',
      position: 'relative'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
      marginBottom: '20px'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '12px',
      borderRadius: '6px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box',
      transition: 'border-color 0.3s',
      ':focus': {
        borderColor: '#3b82f6',
        outline: 'none'
      }
    },
    select: {
      width: '100%',
      padding: '12px',
      borderRadius: '6px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '12px',
      borderRadius: '6px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '6px',
      fontWeight: '600',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s, transform 0.2s',
      marginTop: '10px',
      ':hover': {
        backgroundColor: '#1d4ed8',
        transform: 'translateY(-1px)'
      },
      ':active': {
        transform: 'translateY(0)'
      },
      ':disabled': {
        backgroundColor: '#94a3b8',
        cursor: 'not-allowed',
        transform: 'none'
      }
    },
    toast: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      padding: '16px 24px',
      borderRadius: '8px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      minWidth: '300px',
      maxWidth: '90%',
      zIndex: 1000,
      animation: 'slideUp 0.3s ease forwards'
    },
    toastError: {
      backgroundColor: '#ef4444'
    },
    toastMessage: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      fontSize: '15px'
    },
    toastClose: {
      background: 'none',
      border: 'none',
      color: 'white',
      cursor: 'pointer',
      fontSize: '20px',
      marginLeft: '15px',
      padding: '0',
      display: 'flex',
      alignItems: 'center'
    },
    '@keyframes slideUp': {
      from: {
        opacity: 0,
        transform: 'translate(-50%, 100%)'
      },
      to: {
        opacity: 1,
        transform: 'translate(-50%, 0)'
      }
    },
    '@keyframes fadeOut': {
      from: {
        opacity: 1
      },
      to: {
        opacity: 0
      }
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
            max={userSalaire}
            step="0.01"
            placeholder="Ex: 700"
          />
          <p style={styles.infoText}>
            Le montant maximum est de {userSalaire} TND (votre salaire mensuel).
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
            minLength="10"
          />
        </div>

        <button 
          type="submit" 
          style={{
            ...styles.submitBtn,
            ...(loading && { 
              backgroundColor: '#94a3b8',
              ':hover': {
                backgroundColor: '#94a3b8'
              }
            })
          }}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>

      {toast.show && (
        <div 
          style={{
            ...styles.toast,
            ...(toast.type === 'error' ? styles.toastError : {}),
            animation: toast.show ? 'slideUp 0.3s ease forwards' : 'fadeOut 0.5s ease forwards'
          }}
        >
          <span style={styles.toastMessage}>
            {toast.type === 'error' ? '⚠️' : '✓'} {toast.message}
          </span>
          <button 
            style={styles.toastClose}
            onClick={() => setToast(prev => ({ ...prev, show: false }))}
            aria-label="Fermer"
          >
            &times;
          </button>
        </div>
      )}

      {/* Styles globaux */}
      <style>{`
        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translate(-50%, 100%);
          }
          to {
            opacity: 1;
            transform: translate(-50%, 0);
          }
        }
        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }
      `}</style>
    </div>
  );
};

export default CardDemandeAvance;
