import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X } from "lucide-react";
import axios from "axios";

const GestionFormations = () => {
  const [demandes, setDemandes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Chargement initial des données
  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const response = await axios.get('/alldemandeformation');
        setDemandes(response.data);
      } catch (err) {
        setError("Erreur lors du chargement des demandes");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchDemandes();
  }, []);

  // Filtrage des demandes
  const filteredDemandes = demandes.filter(demande => {
    const searchLower = searchTerm.toLowerCase();
    return (
      demande.nom.toLowerCase().includes(searchLower) ||
      demande.prenom.toLowerCase().includes(searchLower) ||
      demande.nomFormation.toLowerCase().includes(searchLower)
    );
  });

  // Mise à jour du statut
  const updateStatut = async (_id, newStatus) => {
    try {
      // Conversion des statuts frontend → backend
      const statusMap = {
        accepte: 'Approuvée',
        refuse: 'Rejetée',
        en_cours: 'En attente'
      };

      const backendStatus = statusMap[newStatus];
      if (!backendStatus) throw new Error("Statut invalide");

      // Appel API pour mettre à jour le statut
      await axios.put(`/demandeformation/${_id}`, { statut: backendStatus });

      // Mise à jour locale optimiste
      setDemandes(prev => prev.map(item => 
        item._id === _id ? { ...item, statut: backendStatus } : item
      ));

      alert(`Statut mis à jour avec succès !`);
    } catch (err) {
      console.error("Erreur de mise à jour:", err);
      alert("Échec de la mise à jour: " + (err.response?.data?.message || err.message));
    }
  };

  // Formatage de date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR');
  };

  // Rendu du badge de statut
  const renderStatusBadge = (status) => {
    const statusComponents = {
      'Approuvée': {
        className: 'bg-green-100 text-green-800',
        icon: <Check size={14} className="mr-1" />,
        text: 'Accepté'
      },
      'En attente': {
        className: 'bg-yellow-100 text-yellow-800',
        icon: <AlertCircle size={14} className="mr-1" />,
        text: 'En cours'
      },
      'Rejetée': {
        className: 'bg-red-100 text-red-800',
        icon: <X size={14} className="mr-1" />,
        text: 'Rejeté'
      }
    };

    const component = statusComponents[status] || { className: '', icon: null, text: status };

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${component.className}`}>
        {component.icon}
        {component.text}
      </span>
    );
  };

  if (loading) return <div className="p-4 text-center">Chargement en cours...</div>;
  if (error) return <div className="p-4 text-center text-red-500">{error}</div>;

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold mb-6">Gestion des Demandes de Formation</h1>
      
      <div className="relative mb-6 max-w-md">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          placeholder="Rechercher par nom, prénom ou formation..."
          className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nom</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Prénom</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Formation</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Statut</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredDemandes.length === 0 ? (
              <tr>
                <td colSpan={6} className="px-6 py-4 text-center text-sm text-gray-500">
                  Aucune demande trouvée
                </td>
              </tr>
            ) : (
              filteredDemandes.map(demande => (
                <tr key={demande._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{demande.nom}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{demande.prenom}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{demande.nomFormation}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {renderStatusBadge(demande.statut)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {formatDate(demande.dateDemande)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {demande.statut === 'En attente' && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => updateStatut(demande._id, 'accepte')}
                          className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded shadow-sm text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                        >
                          <Check className="mr-1 h-3 w-3" /> Accepter
                        </button>
                        <button
                          onClick={() => updateStatut(demande._id, 'refuse')}
                          className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                        >
                          <X className="mr-1 h-3 w-3" /> Rejeter
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default GestionFormations;
