import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userSalary, setUserSalary] = useState(0);

  useEffect(() => {
    // Récupérer les informations d'authentification et le salaire
    const storedToken = localStorage.getItem('token');
    let userInfo = null;
    
    try {
      const userInfoStr = localStorage.getItem('userInfo');
      if (userInfoStr) {
        userInfo = JSON.parse(userInfoStr);
      }
    } catch (e) {
      console.error("Erreur dans la lecture de userInfo:", e);
    }
    
    const finalToken = storedToken || (userInfo?.token);
    
    if (finalToken) {
      setToken(finalToken);
      setIsAuthenticated(true);
      
      // Récupérer le salaire depuis userInfo ou localStorage
      if (userInfo?.salary) {
        setUserSalary(parseFloat(userInfo.salary));
      } else {
        const salaryFromStorage = localStorage.getItem('salary') || localStorage.getItem('userSalary');
        if (salaryFromStorage) {
          setUserSalary(parseFloat(salaryFromStorage));
        }
      }
    } else {
      console.warn("Aucun token trouvé dans localStorage");
      setIsAuthenticated(true); // Pour tester sans token valide
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Validation des champs obligatoires
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }

      const montantDemande = parseFloat(formData.montant);
      
      // Validation du montant par rapport au salaire
      if (isNaN(montantDemande)) {
        throw new Error('Veuillez entrer un montant valide');
      }
      
      if (montantDemande <= 0) {
        throw new Error('Le montant doit être supérieur à zéro');
      }
      
      if (userSalary > 0) {
        const montantMaxAutorise = userSalary * 0.3;
        
        if (montantDemande > userSalary) {
          throw new Error(`Le montant demandé (${montantDemande} TND) ne peut pas dépasser votre salaire mensuel (${userSalary} TND)`);
        }
        
        if (montantDemande > montantMaxAutorise) {
          throw new Error(`Le montant demandé (${montantDemande} TND) ne peut pas dépasser 30% de votre salaire (${montantMaxAutorise.toFixed(2)} TND)`);
        }
      }

      // Envoi de la demande
      const response = await axios.post(
        '/demandes/avance',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    error: {
      color: '#dc2626',
      margin: '10px 0',
      fontSize: '14px'
    },
    success: {
      color: '#16a34a',
      margin: '10px 0',
      fontSize: '14px'
    },
    authError: {
      textAlign: 'center',
      backgroundColor: '#fee2e2',
      padding: '15px',
      borderRadius: '6px',
      margin: '20px 0',
      color: '#b91c1c'
    },
    salaryInfo: {
      fontSize: '16px',
      color: '#1e40af',
      fontWeight: 'bold',
      margin: '10px 0'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
        {userSalary > 0 && (
          <div style={styles.salaryInfo}>
            <p>Votre salaire mensuel: {userSalary.toFixed(2)} TND</p>
            <p>Maximum autorisé (30%): {(userSalary * 0.3).toFixed(2)} TND</p>
          </div>
        )}
      </div>

      {error && <div style={styles.error}>{error}</div>}
      {success && <div style={styles.success}>Votre demande a été soumise avec succès!</div>}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="0.01"
            step="0.01"
            max={userSalary > 0 ? userSalary : undefined}
            placeholder="Entrez le montant"
          />
          <p style={styles.infoText}>
            {userSalary > 0 
              ? `Le montant maximum autorisé est ${(userSalary * 0.3).toFixed(2)} TND (30% de votre salaire)`
              : 'Veuillez entrer un montant valide'}
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
