import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Row, Col, Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const CardDemandeAvance = () => {
  // États du formulaire
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    montant: '',
    type: '',
    motif: ''
  });

  // États pour la gestion des erreurs et des alertes
  const [errors, setErrors] = useState({});
  const [showAlert, setShowAlert] = useState(false);
  const [alertConfig, setAlertConfig] = useState({
    variant: 'success',
    message: '',
    dismissible: true
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Chargement des données utilisateur au montage du composant
  useEffect(() => {
    const loadUserData = () => {
      const user = JSON.parse(localStorage.getItem('user'));
      if (user) {
        setFormData(prev => ({
          ...prev,
          nom: user.nom || '',
          prenom: user.prenom || '',
          email: user.email || ''
        }));
      }
    };

    loadUserData();
  }, []);

  // Gestion des changements dans les champs du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Effacer l'erreur du champ quand l'utilisateur modifie la valeur
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: null
      }));
    }
  };

  // Validation du formulaire
  const validateForm = () => {
    const newErrors = {};
    const requiredFields = ['nom', 'prenom', 'email', 'montant', 'type', 'motif'];
    
    requiredFields.forEach(field => {
      if (!formData[field]) {
        newErrors[field] = `Le champ ${field} est requis`;
      }
    });

    // Validation spécifique pour le montant
    if (formData.montant && isNaN(formData.montant)) {
      newErrors.montant = 'Le montant doit être un nombre';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Affichage d'une alerte
  const displayAlert = (variant, message) => {
    setAlertConfig({
      variant,
      message,
      dismissible: true
    });
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 5000);
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    if (!validateForm()) {
      setIsSubmitting(false);
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/demandeAvance/add', {
        ...formData,
        statut: 'En attente'
      });

      if (response.data.message) {
        displayAlert('success', 'Demande d\'avance soumise avec succès!');
        
        // Réinitialisation du formulaire (sauf les infos utilisateur)
        setFormData(prev => ({
          ...prev,
          montant: '',
          type: '',
          motif: ''
        }));
      }
    } catch (error) {
      console.error('Erreur lors de la soumission:', error);
      const errorMessage = error.response?.data?.message || 
                          "Une erreur s'est produite lors de la soumission";
      displayAlert('danger', errorMessage);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container mt-4">
      <Card className="shadow-sm">
        <Card.Header className="bg-primary text-white">
          <h5 className="mb-0">Demande d'avance</h5>
        </Card.Header>
        
        <Card.Body>
          {/* Affichage des alertes */}
          {showAlert && (
            <Alert 
              variant={alertConfig.variant} 
              onClose={() => setShowAlert(false)} 
              dismissible
              className="mt-3"
            >
              {alertConfig.message}
            </Alert>
          )}

          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group controlId="formNom" className="mb-3">
                  <Form.Label>Nom</Form.Label>
                  <Form.Control
                    type="text"
                    name="nom"
                    value={formData.nom}
                    onChange={handleChange}
                    isInvalid={!!errors.nom}
                    readOnly
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.nom}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
              
              <Col md={6}>
                <Form.Group controlId="formPrenom" className="mb-3">
                  <Form.Label>Prénom</Form.Label>
                  <Form.Control
                    type="text"
                    name="prenom"
                    value={formData.prenom}
                    onChange={handleChange}
                    isInvalid={!!errors.prenom}
                    readOnly
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.prenom}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group controlId="formEmail" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                isInvalid={!!errors.email}
                readOnly
              />
              <Form.Control.Feedback type="invalid">
                {errors.email}
              </Form.Control.Feedback>
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group controlId="formMontant" className="mb-3">
                  <Form.Label>Montant</Form.Label>
                  <Form.Control
                    type="number"
                    name="montant"
                    value={formData.montant}
                    onChange={handleChange}
                    isInvalid={!!errors.montant}
                    min="0"
                    step="0.01"
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.montant}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
              
              <Col md={6}>
                <Form.Group controlId="formType" className="mb-3">
                  <Form.Label>Type d'avance</Form.Label>
                  <Form.Select
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    isInvalid={!!errors.type}
                  >
                    <option value="">Sélectionner un type</option>
                    <option value="Avance sur salaire">Avance sur salaire</option>
                    <option value="Avance exceptionnelle">Avance exceptionnelle</option>
                  </Form.Select>
                  <Form.Control.Feedback type="invalid">
                    {errors.type}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group controlId="formMotif" className="mb-3">
              <Form.Label>Motif</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="motif"
                value={formData.motif}
                onChange={handleChange}
                isInvalid={!!errors.motif}
                placeholder="Décrivez la raison de votre demande d'avance"
              />
              <Form.Control.Feedback type="invalid">
                {errors.motif}
              </Form.Control.Feedback>
            </Form.Group>

            <div className="d-grid gap-2 mt-4">
              <Button 
                variant="primary" 
                type="submit"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <Spinner
                      as="span"
                      animation="border"
                      size="sm"
                      role="status"
                      aria-hidden="true"
                      className="me-2"
                    />
                    Envoi en cours...
                  </>
                ) : 'Soumettre la demande'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default CardDemandeAvance;
