import React, { useState, useEffect } from 'react';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [salaire, setSalaire] = useState(0);
  const [montantMax, setMontantMax] = useState(0);

  useEffect(() => {
    // Debug: Afficher toutes les clés dans localStorage pour savoir où chercher
    console.log("Contenu de localStorage:", Object.keys(localStorage));
    
    // Tenter de récupérer le token et le salaire
    let userToken = null;
    let userSalaire = 0;
    
    try {
      // Chercher dans userInfo
      const userInfo = localStorage.getItem('userInfo');
      if (userInfo) {
        const parsedUserInfo = JSON.parse(userInfo);
        if (parsedUserInfo && parsedUserInfo.token) {
          userToken = parsedUserInfo.token;
        }
        
        // Le salaire se trouve dans le token
        if (parsedUserInfo && parsedUserInfo.token && parsedUserInfo.token.salaire) {
          userSalaire = parseFloat(parsedUserInfo.token.salaire);
        }
      }
      
      // Si pas dans userInfo.token, chercher directement dans le token
      const directToken = localStorage.getItem('token');
      if (directToken && !userToken) {
        try {
          const parsedToken = JSON.parse(directToken);
          userToken = parsedToken;
          
          if (parsedToken.salaire) {
            userSalaire = parseFloat(parsedToken.salaire);
          }
        } catch (e) {
          // Le token n'est peut-être pas au format JSON
          userToken = directToken;
        }
      }
      
      // Si toujours pas de salaire, vérifier si le salaire est stocké séparément
      if (userSalaire === 0) {
        const directSalaire = localStorage.getItem('salaire');
        if (directSalaire) {
          userSalaire = parseFloat(directSalaire);
        }
      }
      
      // Calculer le montant maximum (30% du salaire)
      const maxAmount = userSalaire * 0.3;
      
      setToken(userToken || '');
      setSalaire(userSalaire);
      setMontantMax(maxAmount);
      setIsAuthenticated(!!userToken);
      
      console.log("Token récupéré:", userToken ? "Oui" : "Non");
      console.log("Salaire récupéré:", userSalaire, "Montant max:", maxAmount);
      
      // Forcer l'authentification pour le développement
      if (!userToken) {
        console.warn("Aucun token trouvé, authentification forcée pour le développement");
        setIsAuthenticated(true);
      }
    } catch (e) {
      console.error("Erreur lors de la récupération des données:", e);
      setIsAuthenticated(true); // Pour le développement
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Réinitialiser l'erreur lorsque l'utilisateur modifie le montant
    if (name === 'montant') {
      setError(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }
      
      // Vérifier que le montant est inférieur ou égal à 30% du salaire
      const montantDemande = parseFloat(formData.montant);
      if (montantMax > 0 && montantDemande > montantMax) {
        throw new Error(`Le montant demandé dépasse 30% de votre salaire. Maximum autorisé: ${montantMax.toFixed(2)} TND`);
      }

      // Dans un environnement réel, cette partie serait un appel à une API
      // Simulation d'un appel API réussi
      setTimeout(() => {
        setSuccess(true);
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
        setLoading(false);
      }, 1000);
    } catch (err) {
      setError(err.message || 'Une erreur est survenue');
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    error: {
      color: '#dc2626',
      margin: '10px 0',
      fontSize: '14px'
    },
    success: {
      color: '#16a34a',
      margin: '10px 0',
      fontSize: '14px'
    },
    authError: {
      textAlign: 'center',
      backgroundColor: '#fee2e2',
      padding: '15px',
      borderRadius: '6px',
      margin: '20px 0',
      color: '#b91c1c'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {error && <div style={styles.error}>{error}</div>}
      {success && <div style={styles.success}>Votre demande a été soumise avec succès!</div>}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
            max={montantMax || ''}
          />
          <p style={styles.infoText}>
            Le montant maximum est de 30% de votre salaire mensuel
            {montantMax > 0 && ` (${montantMax.toFixed(2)} TND)`}
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
