import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [submitError, setSubmitError] = useState(null);
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });

  // Styles
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: '"Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      color: '#334155'
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      paddingTop: '8rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '2.2rem', 
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    // ... (autres styles conserv√©s de votre version originale)
  };

  // Types de cong√©s
  const congeTypes = [
    {
      type: 'cong√© pay√©',
      title: 'Cong√© Pay√© Standard',
      description: 'Cong√© annuel r√©mun√©r√©',
      remainingDays: userData.soldeConge,
      icon: 'üèñÔ∏è'
    },
    // ... (autres types de cong√©s conserv√©s)
  ];

  // R√©cup√©ration des donn√©es utilisateur
  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log("Token retrieved from localStorage:", token);
    
    if (token) {
      try {
        const decoded = jwtDecode(token);
        console.log("Decoded token data:", decoded);
        
        // V√©rification expiration
        if (decoded.exp * 1000 < Date.now()) {
          console.warn("Token has expired!");
          localStorage.removeItem('token');
          setSubmitError("Session expir√©e. Veuillez vous reconnecter.");
          return;
        }

        setUserData({
          nom: decoded.nom || '',
          prenom: decoded.prenom || '',
          email: decoded.email || '',
          soldeConge: decoded.soldeConge || 0
        });
      } catch (error) {
        console.error("Error decoding token:", error);
        setSubmitError("Session invalide. Veuillez vous reconnecter.");
      }
    } else {
      setSubmitError("Authentification requise. Veuillez vous connecter.");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setFormData(prev => ({
        ...prev,
        medicalFile: file
      }));
      setFileName(file.name);
    }
  };
  
  const removeFile = () => {
    setFormData(prev => ({
      ...prev,
      medicalFile: null
    }));
    setFileName('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError(null);
    
    // Validation
    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      setSubmitError('Veuillez joindre votre justificatif m√©dical');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setSubmitError("Session expir√©e. Veuillez vous reconnecter.");
      return;
    }
  
    try {
      const formDataToSend = new FormData();
      
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      console.log("Sending request to backend...");
      const response = await axios.post(
        'http://localhost:5000/api/adddemandeconge',
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          },
          withCredentials: true
        }
      );

      console.log("Backend response:", response.data);

      setConfirmationMessage(`Demande de ${formData.type} enregistr√©e (R√©f: ${response.data.data._id})`);
      setShowConfirmation(true);
      
      // Reset form
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');

      setTimeout(() => setShowConfirmation(false), 5000);

    } catch (error) {
      console.error('Request failed:', error);
      let errorMessage = "Erreur lors de la soumission";
      
      if (error.response) {
        console.error("Server response error:", error.response.data);
        errorMessage = error.response.data?.message || 
                      error.response.data?.error || 
                      "Erreur serveur";
      } else if (error.request) {
        console.error("No response received:", error.request);
        errorMessage = "Le serveur ne r√©pond pas";
      }
      
      setSubmitError(errorMessage);
    }
  };

  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'cong√© maladie';
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  
  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Cong√©
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}, d√©couvrez et soumettez vos demandes de cong√©s en quelques clics</p>
        </div>

        <div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Formulaire de demande de cong√©</h2>
            <p style={styles.formDescription}>Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          {submitError && (
            <div style={styles.errorMessage}>
              {submitError}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="type">
                Type de cong√©
              </label>
              <div style={styles.selectContainer}>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={styles.select}
                  required
                >
                  {congeTypes.map((conge, index) => (
                    <option key={index} value={conge.type}>
                      {conge.icon} {conge.title}
                    </option>
                  ))}
                </select>
                <div style={styles.selectArrow}></div>
              </div>
              <span style={styles.congeInfoTag}>
                {selectedCongeType?.description} - 
                {typeof selectedCongeType?.remainingDays === 'number' 
                  ? ` ${selectedCongeType.remainingDays} jours disponibles` 
                  : ` ${selectedCongeType?.remainingDays}`}
              </span>
            </div>
            
            {isMaladieType ? (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="medicalFile">
                  Justificatif m√©dical *
                </label>
                <div style={styles.fileInputContainer}>
                  <label style={styles.fileInputLabel} htmlFor="medicalFile">
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif m√©dical'}
                  </label>
                  <input
                    type="file"
                    id="medicalFile"
                    ref={fileInputRef}
                    style={styles.fileInput}
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    required
                  />
                </div>
                {formData.medicalFile && (
                  <div style={styles.selectedFile}>
                    <span style={styles.fileIcon}>üìÑ</span>
                    <span style={styles.fileInfo}>{fileName}</span>
                    <button 
                      type="button" 
                      style={styles.removeButton}
                      onClick={removeFile}
                    >
                      ‚úï
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="reason">
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder={needsJustification 
                    ? "Pr√©cisez le motif (obligatoire)" 
                    : "Pr√©cisez le motif de votre demande (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}
            
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="startDate">
                  Date de d√©but
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
              
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="endDate">
                  Date de fin
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
            </div>
            
            {formData.startDate && formData.endDate && (
              <div style={styles.alert}>
                <span style={styles.alertIcon}>‚ÑπÔ∏è</span>
                <p style={styles.alertText}>
                  Dur√©e: <strong>{calculateDays()} jours</strong>
                  {formData.type === 'cong√© pay√©' && (
                    <span> - Solde restant: {userData.soldeConge - calculateDays()} jours</span>
                  )}
                </p>
              </div>
            )}
            
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="notes">
                Notes compl√©mentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                style={styles.textarea}
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            <div style={styles.buttonContainer}>
              <button type="submit" style={styles.button}>
                Soumettre la demande
              </button>
            </div>
          </form>
        </div>
      </div>

      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={styles.toastContent}>
            <span style={styles.toastIcon}>‚úì</span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CardDemandeConge;
