import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footer from "components/Footers/Footer.js";

export default function Index() {
  // UBCI bank branches data
  const [branches, setBranches] = useState([
    { name: "UBCI Siège", lat: 36.8065, lng: 10.1815, address: "Tunis, Avenue Habib Bourguiba" },
    { name: "UBCI Lac", lat: 36.8433, lng: 10.2548, address: "Les Berges du Lac, Tunis" },
    { name: "UBCI Sousse", lat: 35.8245, lng: 10.6382, address: "Avenue Habib Bourguiba, Sousse" },
    { name: "UBCI Sfax", lat: 34.7398, lng: 10.7600, address: "Rue Majida Boulila, Sfax" },
    { name: "UBCI Monastir", lat: 35.7785, lng: 10.8262, address: "Avenue Habib Bourguiba, Monastir" },
    { name: "UBCI Hammamet", lat: 36.4022, lng: 10.6217, address: "Avenue de la République, Hammamet" },
    { name: "UBCI Nabeul", lat: 36.4563, lng: 10.7348, address: "Avenue Habib Thameur, Nabeul" },
    { name: "UBCI Djerba", lat: 33.8075, lng: 10.8451, address: "Houmt Souk, Djerba" }
  ]);

  const [selectedBranch, setSelectedBranch] = useState(null);
  const [mapLoaded, setMapLoaded] = useState(false);

  useEffect(() => {
    // Function to initialize map after component mounts
    const initMap = () => {
      if (window.google && !mapLoaded) {
        const mapElement = document.getElementById('map');
        if (mapElement) {
          // Center on Tunisia
          const map = new window.google.maps.Map(mapElement, {
            center: { lat: 36.8065, lng: 10.1815 },
            zoom: 7,
            styles: [
              {
                "featureType": "water",
                "elementType": "geometry",
                "stylers": [{ "color": "#4682B4" }]
              },
              {
                "featureType": "landscape",
                "elementType": "geometry",
                "stylers": [{ "color": "#f5f5f5" }]
              },
              {
                "featureType": "road",
                "elementType": "geometry",
                "stylers": [{ "color": "#ffffff" }]
              }
            ]
          });

          // Add markers for each branch
          branches.forEach(branch => {
            const marker = new window.google.maps.Marker({
              position: { lat: branch.lat, lng: branch.lng },
              map: map,
              title: branch.name,
              icon: {
                url: "https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png",
                scaledSize: new window.google.maps.Size(30, 30)
              }
            });

            // Add click event to markers
            marker.addListener('click', () => {
              setSelectedBranch(branch);
            });
          });

          setMapLoaded(true);
        }
      }
    };

    // Load Google Maps script if not already loaded
    if (!window.google) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY`; // Replace with your actual API key
      script.async = true;
      script.defer = true;
      script.onload = initMap;
      document.head.appendChild(script);
    } else {
      initMap();
    }
  }, [branches]);

  return (
    <>
      <IndexNavbar fixed />
      <section className="header relative pt-16 items-center flex h-screen max-h-860-px">
        <div className="container mx-auto items-center flex flex-wrap">
          <div className="w-full md:w-8/12 lg:w-6/12 xl:w-6/12 px-4">
            <div className="pt-32 sm:pt-0">
              <h2 className="font-semibold text-4xl text-blueGray-600">
               BIENVENUE SUR UBCI HR MANAGER
              </h2>
              <p className="mt-4 text-lg leading-relaxed text-blueGray-500">
              UBCI RH est un site web dédié à la gestion des ressources humaines de la banque UBCI, facilitant le suivi du personnel, la gestion des congés et l'automatisation des tâches administratives.                
              </p>
              <div className="mt-12">
                <a
                  href="https://www.creative-tim.com/learning-lab/tailwind/react/overview/notus?ref=nr-index"
                  target="_blank"
                  className="get-started text-white font-bold px-6 py-4 rounded outline-none focus:outline-none mr-1 mb-1 bg-lightBlue-500 active:bg-lightBlue-600 uppercase text-sm shadow hover:shadow-lg ease-linear transition-all duration-150"
                >
                  Get started
                </a>
                <a
                  href="https://github.com/creativetimofficial/notus-react?ref=nr-index"
                  className="github-star ml-1 text-white font-bold px-6 py-4 rounded outline-none focus:outline-none mr-1 mb-1 bg-blueGray-700 active:bg-blueGray-600 uppercase text-sm shadow hover:shadow-lg ease-linear transition-all duration-150"
                  target="_blank"
                >
                  Github Star
                </a>
              </div>
            </div>
          </div>
        </div>

        <img
          className="absolute top-0 right-0 pt-1 sm:w-6/12 sm:max-h-full -mt-48 sm:mt-0 w-full h-full object-cover"
          src={require("assets/img/ubci.jpg").default}
          alt="..."
        />
      </section>

      {/* UBCI Branch Network Map Section */}
      <section className="pb-20 bg-blueGray-100 relative">
        <div className="container mx-auto px-4">
          <div className="flex flex-wrap items-center mt-32">
            <div className="w-full md:w-5/12 px-4 mr-auto ml-auto">
              <div className="text-blueGray-500 p-3 text-center inline-flex items-center justify-center w-16 h-16 mb-6 shadow-lg rounded-full bg-white">
                <i className="fas fa-map-marker-alt text-xl"></i>
              </div>
              <h3 className="text-3xl mb-2 font-semibold leading-normal">
                Réseau des Agences UBCI
              </h3>
              <p className="text-lg font-light leading-relaxed mt-4 mb-4 text-blueGray-600">
                Découvrez notre réseau d'agences à travers la Tunisie. UBCI est présente dans les principales villes pour vous offrir des services bancaires de qualité.
              </p>
              
              {/* Branch List */}
              <div className="mt-6 bg-white rounded-lg shadow-md p-4 max-h-72 overflow-y-auto">
                <h4 className="text-xl font-semibold mb-3 text-blueGray-700">Nos Agences</h4>
                <ul className="space-y-2">
                  {branches.map((branch, index) => (
                    <li 
                      key={index} 
                      className={`p-2 rounded-md cursor-pointer transition-all duration-200 ${selectedBranch === branch ? 'bg-lightBlue-100 shadow-sm' : 'hover:bg-blueGray-50'}`}
                      onClick={() => setSelectedBranch(branch)}
                    >
                      <h5 className="font-medium text-blueGray-700">{branch.name}</h5>
                      <p className="text-sm text-blueGray-500">{branch.address}</p>
                    </li>
                  ))}
                </ul>
              </div>
              
              {/* Selected Branch Info */}
              {selectedBranch && (
                <div className="mt-4 p-4 bg-white rounded-lg shadow-md">
                  <h4 className="font-semibold text-lg text-blueGray-700">{selectedBranch.name}</h4>
                  <p className="text-blueGray-500">{selectedBranch.address}</p>
                  <div className="mt-2">
                    <button className="text-lightBlue-500 hover:text-lightBlue-700 text-sm font-medium">
                      Obtenir l'itinéraire
                    </button>
                    <button className="ml-4 text-lightBlue-500 hover:text-lightBlue-700 text-sm font-medium">
                      Plus d'informations
                    </button>
                  </div>
                </div>
              )}
            </div>

            {/* Map Container */}
            <div className="w-full md:w-6/12 px-4 mr-auto ml-auto mt-6 md:mt-0">
              <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded-lg">
                <div id="map" className="w-full h-96 rounded-lg"></div>
              </div>
              <div className="text-center bg-white p-4 rounded-lg shadow-sm">
                <p className="text-sm text-blueGray-500">
                  Pour plus d'informations sur nos agences, veuillez contacter notre service client au 
                  <span className="font-medium"> +216 71 123 456</span>
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </>
  );
}
