import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États du formulaire
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [submitError, setSubmitError] = useState(null);
  const [loading, setLoading] = useState(false);
  
  // États d'authentification
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });

  // Styles simplifiés mais fonctionnels
  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px'
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#1e40af',
      marginBottom: '10px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '20px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '15px'
    },
    label: {
      display: 'block',
      marginBottom: '5px',
      fontWeight: '500'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #ddd',
      marginBottom: '5px'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #ddd'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #ddd',
      minHeight: '100px'
    },
    button: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
      marginTop: '10px'
    },
    errorMessage: {
      color: '#b91c1c',
      backgroundColor: '#fee2e2',
      padding: '10px',
      borderRadius: '4px',
      marginBottom: '15px'
    },
    successMessage: {
      color: '#16a34a',
      backgroundColor: '#dcfce7',
      padding: '10px',
      borderRadius: '4px',
      marginBottom: '15px'
    },
    fileInputContainer: {
      border: '1px dashed #3b82f6',
      padding: '15px',
      borderRadius: '4px',
      textAlign: 'center',
      marginBottom: '10px'
    }
  };

  // Types de congés disponibles
  const congeTypes = [
    { type: 'congé payé', title: 'Congé Payé', remainingDays: userData.soldeConge },
    { type: 'rtt', title: 'RTT', remainingDays: 8 },
    { type: 'congé maladie', title: 'Congé Maladie', remainingDays: 'Illimité' },
    { type: 'congé parental', title: 'Congé Parental', remainingDays: 'Selon droits' },
    { type: 'congé familial', title: 'Événement Familial', remainingDays: 'Selon événement' },
    { type: 'congé sans solde', title: 'Congé Sans Solde', remainingDays: 'Illimité' }
  ];

  // Vérification initiale de l'authentification
  useEffect(() => {
    const userDataStr = localStorage.getItem('userData');
    const authToken = localStorage.getItem('token');

    if (userDataStr && authToken) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData({
          nom: userData.nom || '',
          prenom: userData.prenom || '',
          email: userData.email || '',
          soldeConge: userData.soldeConge || 0
        });
        setToken(authToken);
        setIsAuthenticated(true);
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        localStorage.removeItem('userData');
        localStorage.removeItem('token');
      }
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };
  
  const handleFileChange = (e) => {
    if (e.target.files?.[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };
  
  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!isAuthenticated || !token) {
      setSubmitError("Session expirée. Veuillez vous reconnecter.");
      return;
    }

    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      setSubmitError("Un justificatif médical est requis pour les congés maladie");
      return;
    }

    setLoading(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const response = await axios.post('/demandes-conge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        }
      });

      setConfirmationMessage(`Demande enregistrée (Réf: ${response.data.data._id || ''})`);
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      if (error.response?.status === 401) {
        setSubmitError("Session expirée. Veuillez vous reconnecter.");
        localStorage.removeItem('token');
        setIsAuthenticated(false);
      } else {
        setSubmitError(error.response?.data?.message || "Erreur lors de la soumission");
      }
    } finally {
      setLoading(false);
    }
  };

  if (!isAuthenticated) {
    return (
      <div style={styles.container}>
        <div style={{ 
          ...styles.formContainer,
          textAlign: 'center',
          padding: '40px'
        }}>
          <h3 style={{ color: '#ef4444', marginBottom: '20px' }}>
            Authentification requise
          </h3>
          <p>Veuillez vous connecter pour accéder à cette fonctionnalité.</p>
          <button 
            style={styles.button}
            onClick={() => window.location.href = '/login'}
          >
            Se connecter
          </button>
        </div>
      </div>
    );
  }

  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'congé maladie';
  const needsJustification = ['congé maladie', 'congé familial'].includes(formData.type);
  
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Demande de Congé</h1>
        <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}</p>
      </div>

      <div style={styles.formContainer}>
        {submitError && <div style={styles.errorMessage}>{submitError}</div>}
        {showConfirmation && <div style={styles.successMessage}>{confirmationMessage}</div>}

        <form onSubmit={handleSubmit}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Type de congé</label>
            <select
              name="type"
              value={formData.type}
              onChange={handleChange}
              style={styles.select}
              required
            >
              {congeTypes.map((type, index) => (
                <option key={index} value={type.type}>
                  {type.title} ({type.remainingDays} jours)
                </option>
              ))}
            </select>
          </div>

          {isMaladieType ? (
            <div style={styles.formGroup}>
              <label style={styles.label}>Justificatif médical *</label>
              <div style={styles.fileInputContainer}>
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleFileChange}
                  accept=".pdf,.jpg,.jpeg,.png"
                  required
                  style={{ display: 'none' }}
                  id="medicalFile"
                />
                <label htmlFor="medicalFile" style={{ cursor: 'pointer' }}>
                  {fileName || 'Cliquez pour sélectionner un fichier'}
                </label>
                {fileName && (
                  <div>
                    <span>{fileName}</span>
                    <button type="button" onClick={removeFile} style={{ marginLeft: '10px' }}>
                      ×
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div style={styles.formGroup}>
              <label style={styles.label}>
                Motif {needsJustification && '*'}
              </label>
              <input
                type="text"
                name="reason"
                value={formData.reason}
                onChange={handleChange}
                style={styles.input}
                required={needsJustification}
              />
            </div>
          )}

          <div style={styles.formGroup}>
            <label style={styles.label}>Date de début</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              style={styles.input}
              required
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Date de fin</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              style={styles.input}
              required
            />
          </div>

          {formData.startDate && formData.endDate && (
            <div style={styles.formGroup}>
              <p>Durée: {calculateDays()} jours</p>
              {formData.type === 'congé payé' && (
                <p>Solde restant: {userData.soldeConge - calculateDays()} jours</p>
              )}
            </div>
          )}

          <div style={styles.formGroup}>
            <label style={styles.label}>Notes complémentaires</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              style={styles.textarea}
              placeholder="Facultatif"
            />
          </div>

          <button 
            type="submit" 
            style={styles.button}
            disabled={loading}
          >
            {loading ? 'Envoi en cours...' : 'Soumettre la demande'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CardDemandeConge;
