import React, { useState, useEffect } from "react";
import axios from "axios";

// Components
import CardStats from "components/Cards/CardStats.js";

export default function HeaderStats() {
  const [statistics, setStatistics] = useState({
    departments: "0",
    personnel: "0",
    formations: "0"
  });
  
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStatistics = async () => {
      setLoading(true);
      
      try {
        // Utilisation de Promise.all pour les requêtes parallèles
        const [personnelResponse, departmentResponse, formationResponse] = await Promise.all([
          axios.get('/allpersonnel'),
          axios.get('/alldepartment'),
          axios.get('/getformation')
        ]);
        
        setStatistics({
          personnel: personnelResponse.data.length.toString(),
          departments: departmentResponse.data.length.toString(),
          formations: formationResponse.data.length.toString()
        });
      } catch (error) {
        console.error("Erreur lors de la récupération des statistiques:", error);
        setStatistics({
          personnel: "Erreur",
          departments: "Erreur", 
          formations: "Erreur"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchStatistics();
  }, []);

  // Style CSS à injecter
  const headerStyle = {
    backgroundColor: "#0891b2", // Une teinte professionnelle de cyan
    backgroundImage: "linear-gradient(to right, #0891b2, #0d9488)", // Dégradé subtil
    boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)"
  };

  return (
    <>
      {/* Header */}
      <div className="relative md:pt-32 pb-32 pt-12" style={headerStyle}>
        <div className="px-4 md:px-10 mx-auto w-full">
          <div>
            {/* Titre de section */}
            <div className="mb-6">
              <h2 className="text-white text-2xl font-bold mb-1">Tableau de bord</h2>
              <p className="text-white opacity-75">Vue d'ensemble de l'organisation</p>
            </div>
            
            {/* Card stats */}
            <div className="flex flex-wrap">
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Effectif Total"
                  statTitle={loading ? "..." : statistics.personnel}
                  statArrow="up"
                  statPercent="3.2"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis le mois dernier"
                  statIconName="fas fa-users"
                  statIconColor="bg-blue-500"
                />
              </div>
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Départements"
                  statTitle={loading ? "..." : statistics.departments}
                  statArrow="up"
                  statPercent="2.1"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis la semaine dernière"
                  statIconName="fas fa-building"
                  statIconColor="bg-purple-500"
                />
              </div>
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Formations Actives"
                  statTitle={loading ? "..." : statistics.formations}
                  statArrow="up"
                  statPercent="1.5"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis hier"
                  statIconName="fas fa-chalkboard-teacher"
                  statIconColor="bg-teal-500"
                />
              </div>
            </div>
          </div>
        </div>
        
        {/* Décoration d'arrière-plan */}
        <div className="absolute bottom-0 left-0 right-0">
          <svg className="absolute bottom-0 overflow-hidden" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="none" version="1.1" viewBox="0 0 2560 100" x="0" y="0">
            <polygon className="text-white fill-current" points="2560 0 2560 100 0 100"></polygon>
          </svg>
        </div>
      </div>
    </>
  );
}
