import React from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footer from "components/Footers/Footer.js";

export default function Index() {
  return (
    <>
      <IndexNavbar fixed />
      
      {/* Hero Section avec style am√©lior√© */}
      <section className="hero-section relative min-h-screen flex items-center">
        <div className="absolute inset-0 z-0">
          <div className="bg-gradient-overlay absolute inset-0 bg-black opacity-60 z-0"></div>
          <img 
            src={require("assets/img/ubci.jpg").default}
            alt="UBCI Background" 
            className="w-full h-full object-cover"
          />
        </div>
        
        <div className="container mx-auto px-4 z-10 relative py-20">
          <div className="flex flex-wrap items-center">
            <div className="w-full lg:w-6/12 px-4">
              <div className="hero-content text-white">
                <h1 className="text-5xl font-bold mb-6">
                  UBCI HR MANAGER
                </h1>
                <div className="separator w-24 h-1 bg-blue-400 mb-8"></div>
                <p className="text-xl mb-10 text-gray-200">
                  Plateforme int√©gr√©e de gestion des ressources humaines pour la banque UBCI.
                  Simplifiez le suivi du personnel, la gestion des cong√©s et les t√¢ches administratives.
                </p>
                <div className="flex flex-wrap">
                  <button className="apply-button px-8 py-4 mr-4 mb-4 rounded-lg font-semibold transition-all duration-300 text-white">
                    Postuler maintenant
                  </button>
                  <button className="info-button px-8 py-4 mb-4 rounded-lg font-semibold transition-all duration-300">
                    En savoir plus
                  </button>
                </div>
              </div>
            </div>
            
            <div className="w-full lg:w-6/12 px-4 mt-12 lg:mt-0">
              <div className="card-container p-6 rounded-lg shadow-xl">
                <h3 className="text-2xl font-bold mb-4 text-blue-400">Acc√®s Rapide</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {[
                    { title: "Gestion des Cong√©s", icon: "üìÖ" },
                    { title: "Recrutement", icon: "üë•" },
                    { title: "Formation", icon: "üéì" },
                    { title: "Suivi des Performances", icon: "üìä" }
                  ].map((item, index) => (
                    <div key={index} className="quick-access-card p-4 rounded-lg flex items-center cursor-pointer">
                      <span className="text-2xl mr-3">{item.icon}</span>
                      <span className="font-medium">{item.title}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
      
      {/* Section Fonctionnalit√©s */}
      <section className="features-section py-20">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold">Nos Solutions RH</h2>
            <div className="separator mx-auto w-24 h-1 bg-blue-400 my-6"></div>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              D√©couvrez les outils qui r√©volutionnent la gestion des ressources humaines chez UBCI
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[
              {
                title: "Gestion Administrative",
                desc: "Simplifiez vos processus administratifs avec notre solution int√©gr√©e",
                icon: "üìù"
              },
              {
                title: "Recrutement Intelligent",
                desc: "Optimisez votre processus de recrutement et trouvez les meilleurs talents",
                icon: "üîç"
              },
              {
                title: "Suivi des Cong√©s",
                desc: "G√©rez efficacement les demandes et suivis des cong√©s de vos collaborateurs",
                icon: "üóìÔ∏è"
              },
              {
                title: "Formation Continue",
                desc: "D√©veloppez les comp√©tences de vos √©quipes avec nos parcours personnalis√©s",
                icon: "üìö"
              },
              {
                title: "√âvaluation des Performances",
                desc: "Suivez les performances et fixez des objectifs clairs pour vos collaborateurs",
                icon: "üìà"
              },
              {
                title: "Tableau de Bord RH",
                desc: "Visualisez vos indicateurs cl√©s de performance RH en temps r√©el",
                icon: "üìä"
              }
            ].map((feature, index) => (
              <div key={index} className="feature-card p-6 rounded-lg transition-all duration-300">
                <div className="feature-icon text-4xl mb-4">{feature.icon}</div>
                <h3 className="text-xl font-bold mb-3">{feature.title}</h3>
                <p className="text-gray-600">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
      
      {/* Call to Action */}
      <section className="cta-section py-20 relative">
        <div className="container mx-auto px-4 relative z-10">
          <div className="flex flex-wrap items-center">
            <div className="w-full lg:w-8/12 px-4">
              <h2 className="text-3xl font-bold text-white mb-4">
                Pr√™t √† transformer votre gestion RH ?
              </h2>
              <p className="text-xl text-gray-200 mb-8">
                Rejoignez UBCI et b√©n√©ficiez d'une solution RH compl√®te et innovante
              </p>
            </div>
            <div className="w-full lg:w-4/12 px-4 text-center lg:text-right">
              <button className="cta-button px-8 py-4 rounded-lg font-semibold text-white">
                Demander une d√©mo
              </button>
            </div>
          </div>
        </div>
      </section>
      
      <Footer />
      
      {/* CSS inject√© directement */}
      <style jsx>{`
        .hero-section {
          position: relative;
          overflow: hidden;
        }
        
        .bg-gradient-overlay {
          background: linear-gradient(135deg, rgba(0,0,0,0.8) 0%, rgba(0,73,116,0.8) 100%);
        }
        
        .separator {
          background: linear-gradient(90deg, #3490dc, #60a5fa);
        }
        
        .apply-button {
          background: linear-gradient(90deg, #3490dc, #0072ff);
          box-shadow: 0 4px 15px rgba(52, 144, 220, 0.4);
          transition: all 0.3s ease;
        }
        
        .apply-button:hover {
          transform: translateY(-3px);
          box-shadow: 0 6px 20px rgba(52, 144, 220, 0.6);
        }
        
        .info-button {
          background: transparent;
          border: 2px solid white;
          transition: all 0.3s ease;
        }
        
        .info-button:hover {
          background: rgba(255, 255, 255, 0.1);
          transform: translateY(-3px);
        }
        
        .card-container {
          background: rgba(255, 255, 255, 0.9);
          backdrop-filter: blur(10px);
        }
        
        .quick-access-card {
          background: white;
          transition: all 0.3s ease;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
        }
        
        .quick-access-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
          background: #f8fafc;
        }
        
        .features-section {
          background-color: #f8fafc;
        }
        
        .feature-card {
          background: white;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
          height: 100%;
        }
        
        .feature-card:hover {
          transform: translateY(-10px);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
        }
        
        .feature-icon {
          color: #3490dc;
        }
        
        .cta-section {
          background: linear-gradient(135deg, #004974 0%, #0072ff 100%);
        }
        
        .cta-button {
          background: white;
          color: #004974;
          box-shadow: 0 4px 15px rgba(255, 255, 255, 0.3);
          transition: all 0.3s ease;
        }
        
        .cta-button:hover {
          transform: translateY(-3px);
          box-shadow: 0 8px 25px rgba(255, 255, 255, 0.4);
        }
      `}</style>
    </>
  );
}
