import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [userData, setUserData] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const congeTypes = [
    { type: 'congé payé', title: 'Congé Payé', remainingDays: userData?.soldeConge || 0 },
    { type: 'rtt', title: 'RTT', remainingDays: 8 },
    { type: 'congé maladie', title: 'Congé Maladie', remainingDays: 'Illimité' },
    { type: 'congé parental', title: 'Congé Parental', remainingDays: 'Selon droits' },
  ];

  useEffect(() => {
    const loadUserData = () => {
      const userDataStr = localStorage.getItem('userData');
      if (userDataStr) {
        const parsedData = JSON.parse(userDataStr);
        setUserData({
          nom: parsedData.nom,
          prenom: parsedData.prenom,
          email: parsedData.email,
          soldeConge: parsedData.soldeConge || 0,
          _id: parsedData._id
        });
      }
    };
    loadUserData();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData({ ...formData, medicalFile: e.target.files[0] });
      setFileName(e.target.files[0].name);
    }
  };

  const removeFile = () => {
    setFormData({ ...formData, medicalFile: null });
    setFileName('');
    fileInputRef.current.value = '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const authToken = localStorage.getItem('token');

    if (!authToken) {
      showAlertMessage('error', 'Authentification requise');
      return;
    }

    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      showAlertMessage('error', 'Justificatif médical requis');
      return;
    }

    setIsLoading(true);

    try {
      // Envoi des données principales
      const response = await axios.post('/adddemandeconge', {
        ...formData,
        nom: userData.nom,
        prenom: userData.prenom,
        email: userData.email,
        idUser: userData._id,
        statut: 'En attente'
      }, {
        headers: {
          Authorization: `Bearer ${authToken}`,
          'Content-Type': 'application/json'
        }
      });

      // Envoi du fichier séparé si nécessaire
      if (formData.medicalFile) {
        const fileFormData = new FormData();
        fileFormData.append('medicalFile', formData.medicalFile);
        fileFormData.append('demandeId', response.data._id);
        
        await axios.post('/uploadMedicalFile', fileFormData, {
          headers: {
            Authorization: `Bearer ${authToken}`
          }
        });
      }

      showAlertMessage('success', 'Demande envoyée avec succès!');
      resetForm();

    } catch (error) {
      const errorMsg = error.response?.data?.message || "Erreur de soumission";
      showAlertMessage('error', errorMsg);
    } finally {
      setIsLoading(false);
    }
  };

  const showAlertMessage = (type, message) => {
    setAlertType(type);
    setAlertMessage(message);
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 5000);
  };

  const resetForm = () => {
    setFormData({
      startDate: '',
      endDate: '',
      reason: '',
      type: 'congé payé',
      notes: '',
      medicalFile: null
    });
    setFileName('');
  };

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  const Alert = () => (
    <div className={`alert ${alertType}`}>
      <div className="alert-content">
        <span>{alertType === 'error' ? '⚠️' : '✅'}</span>
        {alertMessage}
      </div>
      <button onClick={() => setShowAlert(false)}>×</button>
    </div>
  );

  return (
    <div className="container">
      <div className="header">
        <h1>Demande de Congé</h1>
        {userData && (
          <p>Bonjour {userData.prenom} {userData.nom}</p>
        )}
      </div>

      {showAlert && <Alert />}

      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-group">
          <label>Type de congé</label>
          <select 
            name="type" 
            value={formData.type} 
            onChange={handleChange}
            required
          >
            {congeTypes.map((type, index) => (
              <option key={index} value={type.type}>
                {type.title} ({type.remainingDays} jours)
              </option>
            ))}
          </select>
        </div>

        {formData.type === 'congé maladie' ? (
          <div className="form-group">
            <label>Justificatif médical</label>
            <div className="file-upload">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept=".pdf,.jpg,.png"
                required
              />
              {fileName ? (
                <div className="file-info">
                  <span>{fileName}</span>
                  <button type="button" onClick={removeFile}>×</button>
                </div>
              ) : (
                <div className="upload-prompt">
                  Cliquez pour téléverser un fichier
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="form-group">
            <label>Motif</label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              required={['congé familial'].includes(formData.type)}
            />
          </div>
        )}

        <div className="form-row">
          <div className="form-group">
            <label>Date de début</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              min={new Date().toISOString().split('T')[0]}
              required
            />
          </div>
          <div className="form-group">
            <label>Date de fin</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              min={formData.startDate}
              required
            />
          </div>
        </div>

        {formData.startDate && formData.endDate && (
          <div className="days-info">
            Nombre de jours : {calculateDays()}
            {formData.type === 'congé payé' && userData?.soldeConge && (
              <span> (Solde restant : {userData.soldeConge - calculateDays()})</span>
            )}
          </div>
        )}

        <div className="form-group">
          <label>Commentaires</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            rows="3"
          />
        </div>

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Envoi en cours...' : 'Soumettre la demande'}
        </button>
      </form>

      <style jsx>{`
        .container {
          max-width: 800px;
          margin: 2rem auto;
          padding: 20px;
