import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState(null);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Temporairement true pour test

  // Style minimal et fonctionnel
  const styles = {
    container: {
      padding: '20px',
      maxWidth: '600px',
      margin: '0 auto'
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ddd',
      borderRadius: '4px'
    },
    button: {
      backgroundColor: '#2563eb',
      color: 'white',
      padding: '12px',
      border: 'none',
      borderRadius: '4px',
      width: '100%',
      cursor: 'pointer'
    }
  };

  // SOLUTION CLÉ : Récupération des données COMME dans CardDemandeAvance
  useEffect(() => {
    const userDataStr = localStorage.getItem('userData');
    const authToken = localStorage.getItem('authToken') || 
                     localStorage.getItem('token') || 
                     localStorage.getItem('jwt');

    console.log('Données de localStorage:', { userDataStr, authToken }); // Debug

    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        console.log('Données utilisateur parsées:', userData); // Debug
        setUserData(userData);
        setToken(authToken);
        setIsAuthenticated(true);
      } catch (error) {
        console.error("Erreur de parsing:", error);
      }
    } else {
      console.warn('Aucune donnée utilisateur trouvée');
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // TEST : Contournement temporaire de l'authentification
    if (!isAuthenticated) {
      console.warn('Non authentifié, mais continuation pour test');
      // return; // Commenté pour test
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('DateDebut', formData.startDate);
      formDataToSend.append('DateFin', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason);
      
      // Solution de repli si userData est null
      const userNom = userData?.nom || 'Nom par défaut';
      const userPrenom = userData?.prenom || 'Prénom par défaut';
      const userEmail = userData?.email || 'email@defaut.com';
      
      formDataToSend.append('nom', userNom);
      formDataToSend.append('prenom', userPrenom);
      formDataToSend.append('email', userEmail);

      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      console.log('Envoi des données:', { 
        DateDebut: formData.startDate,
        DateFin: formData.endDate,
        type: formData.type,
        nom: userNom,
        prenom: userPrenom,
        email: userEmail
      });

      // Envoi sans token pour test
      const response = await axios.post('/adddemandeconge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          // 'Authorization': `Bearer ${token}` // Temporairement désactivé
        }
      });

      setConfirmationMessage('Demande envoyée avec succès !');
      setShowConfirmation(true);

      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur complète:', error);
      console.error('Réponse erreur:', error.response?.data);
      alert("Erreur: " + (error.response?.data?.message || error.message));
    }
  };

  // TEST : Affichage forcé pour vérification
  return (
    <div style={styles.container}>
      <h2>Demande de Congé (Mode Test)</h2>
      <p>État authentification: {isAuthenticated ? 'Connecté' : 'Non connecté'}</p>
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>Type de congé *</label>
          <select
            name="type"
            value={formData.type}
            onChange={(e) => setFormData({...formData, type: e.target.value})}
            style={styles.input}
            required
          >
            <option value="congé payé">Congé payé</option>
            <option value="congé maladie">Congé maladie</option>
          </select>
        </div>

        {formData.type === 'congé maladie' && (
          <div>
            <label>Justificatif médical *</label>
            <input
              type="file"
              onChange={(e) => {
                setFormData({...formData, medicalFile: e.target.files[0]});
                setFileName(e.target.files[0]?.name);
              }}
              style={styles.input}
              required
            />
          </div>
        )}

        <div>
          <label>Motif *</label>
          <input
            type="text"
            name="reason"
            value={formData.reason}
            onChange={(e) => setFormData({...formData, reason: e.target.value})}
            style={styles.input}
            required
          />
        </div>

        <div style={{ display: 'flex', gap: '15px' }}>
          <div>
            <label>Date de début *</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={(e) => setFormData({...formData, startDate: e.target.value})}
              style={styles.input}
              required
            />
          </div>
          <div>
            <label>Date de fin *</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={(e) => setFormData({...formData, endDate: e.target.value})}
              style={styles.input}
              required
            />
          </div>
        </div>

        <button type="submit" style={styles.button}>
          Soumettre la demande
        </button>
      </form>

      {showConfirmation && (
        <div style={{ 
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          borderRadius: '4px'
        }}>
          {confirmationMessage}
        </div>
      )}

      {/* Debug */}
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f0f0f0' }}>
        <h3>Debug Info:</h3>
        <p>Token: {token ? 'Existe' : 'Manquant'}</p>
        <p>User Data: {userData ? JSON.stringify(userData) : 'Non chargé'}</p>
      </div>
    </div>
  );
};

export default CardDemandeConge;
