import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode'; // Import pour décoder le token JWT

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userSalaire, setUserSalaire] = useState(0);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  useEffect(() => {
    // Debug: Afficher toutes les clés dans localStorage pour savoir où chercher le token
    console.log("Contenu de localStorage:", Object.keys(localStorage));
    
    // Récupérer le token des différentes sources possibles
    const userInfo = localStorage.getItem('userInfo');
    const storedToken = localStorage.getItem('token');
    const authToken = localStorage.getItem('authToken');
    const jwtToken = localStorage.getItem('jwt');
    const accessToken = localStorage.getItem('accessToken');
    
    // Déterminer quel token utiliser (par ordre de priorité)
    let finalToken = null;
    
    if (userInfo) {
      try {
        const parsedUserInfo = JSON.parse(userInfo);
        if (parsedUserInfo && parsedUserInfo.token) {
          finalToken = parsedUserInfo.token;
        }
      } catch (e) {
        console.error("Erreur dans la lecture de userInfo:", e);
      }
    }
    
    // Si pas trouvé dans userInfo, essayer les autres emplacements
    if (!finalToken) {
      finalToken = storedToken || authToken || jwtToken || accessToken;
    }
    
    if (finalToken) {
      console.log("Token trouvé");
      setToken(finalToken);
      setIsAuthenticated(true);
      
      // Extraire le salaire du token JWT
      try {
        const decodedToken = jwt_decode(finalToken);
        console.log("Token décodé:", decodedToken);
        
        // En fonction de la structure de votre token, ajustez ces lignes
        // pour accéder correctement au salaire
        if (decodedToken.user && decodedToken.user.salaire) {
          setUserSalaire(decodedToken.user.salaire);
          console.log("Salaire extrait du token (user.salaire):", decodedToken.user.salaire);
        } else if (decodedToken.salaire) {
          setUserSalaire(decodedToken.salaire);
          console.log("Salaire extrait du token (salaire):", decodedToken.salaire);
        } else if (decodedToken.userInfo && decodedToken.userInfo.salaire) {
          setUserSalaire(decodedToken.userInfo.salaire);
          console.log("Salaire extrait du token (userInfo.salaire):", decodedToken.userInfo.salaire);
        } else {
          console.warn("Salaire non trouvé dans le token. Structure du token:", decodedToken);
          // Si nécessaire, vous pourriez définir une valeur par défaut ici
          // setUserSalaire(2000); // Salaire par défaut si non trouvé
        }
      } catch (e) {
        console.error("Erreur lors du décodage du token:", e);
      }
    } else {
      console.warn("Aucun token trouvé dans localStorage");
      
      // Pour le développement uniquement - à commenter en production
      setIsAuthenticated(true);
      // setUserSalaire(2000); // Valeur par défaut pour développement
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'montant') {
      const montant = parseFloat(value);
      
      if (montant > userSalaire) {
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage(`Le montant demandé (${montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      } else if (showAlert && montant <= userSalaire) {
        setShowAlert(false);
      }
    }
    
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Vérifier si le montant est supérieur au salaire
    if (parseFloat(formData.montant) > userSalaire) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(`Le montant demandé (${formData.montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }

      // Envoyer la demande au backend
      const response = await axios.post(
        '/demandes/avance',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setShowAlert(true);
        setAlertType('success');
        setAlertMessage('Votre demande a été soumise avec succès!');
        
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(err.response?.data?.message || err.message || 'Une erreur est survenue');
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    error: {
      color: '#dc2626',
      margin: '10px 0',
      fontSize: '14px'
    },
    success: {
      color: '#16a34a',
      margin: '10px 0',
      fontSize: '14px'
    },
    authError: {
      textAlign: 'center',
      backgroundColor: '#fee2e2',
      padding: '15px',
      borderRadius: '6px',
      margin: '20px 0',
      color: '#b91c1c'
    },
    alert: {
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      transition: 'opacity 0.3s',
      opacity: 1
    },
    alertError: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      borderLeft: '4px solid #b91c1c'
    },
    alertSuccess: {
      backgroundColor: '#dcfce7',
      color: '#16a34a',
      borderLeft: '4px solid #16a34a'
    },
    alertIcon: {
      fontSize: '18px',
      marginRight: '10px'
    },
    alertContent: {
      flex: 1
    },
    alertClose: {
      backgroundColor: 'transparent',
      border: 'none',
      cursor: 'pointer',
      fontSize: '18px',
      color: 'inherit'
    }
  };

  const Alert = ({ type, message, onClose }) => {
    return (
      <div style={{
        ...styles.alert,
        ...(type === 'error' ? styles.alertError : styles.alertSuccess)
      }}>
        <div style={styles.alertContent}>
          <span style={styles.alertIcon}>
            {type === 'error' ? '⚠️' : '✅'}
          </span>
          {message}
        </div>
        <button style={styles.alertClose} onClick={onClose}>×</button>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {showAlert && (
        <Alert 
          type={alertType} 
          message={alertMessage}
          onClose={() => setShowAlert(false)}
        />
      )}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
          />
          <p style={styles.infoText}>
            Le montant maximum est de 30% de votre salaire mensuel. 
            Votre salaire actuel est de {userSalaire} TND.
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
