import React, { useState } from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Postule from "./Postule";

export default function Index() {
  // État pour contrôler l'affichage du composant Postule
  const [showPostule, setShowPostule] = useState(false);
  
  // Fonction pour faire défiler vers la section des services
  const scrollToServices = () => {
    document.getElementById('services-section').scrollIntoView({ behavior: 'smooth' });
  };
  
  // Fonction pour afficher le composant Postule
  const handlePostuleClick = () => {
    setShowPostule(true);
  };

  // Si showPostule est true, afficher le composant Postule
  if (showPostule) {
    return <Postule />;
  }

  return (
    <>
      {/* IndexNavbar fixed est maintenant rendu à l'extérieur de la section hero */}
      <IndexNavbar fixed />
      
      {/* Section Hero principale, ajustée pour la navbar */}
      <section className="hero-section">
        <div className="hero-overlay"></div>
        <img 
          src={require("assets/img/ubci.jpg").default}
          alt="UBCI Background" 
          className="hero-bg-image"
        />
        
        <div className="hero-container">
          <div className="hero-content">
            <h1 className="hero-title">UBCI HR MANAGER</h1>
            <div className="title-underline"></div>
            <p className="hero-text">
              Une plateforme innovante dédiée à la gestion des ressources humaines de la banque UBCI. 
