import React, { useState } from 'react';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    type: 'congé payé',
    startDate: '',
    endDate: '',
    reason: '',
    notes: ''
  });
  
  const [submitted, setSubmitted] = useState(false);
  
  // Types de congés avec leurs informations
  const congeTypes = [
    {
      type: 'congé payé',
      title: 'Congé Payé Standard',
      description: 'Congé annuel rémunéré',
      remainingDays: 15,
      icon: '🏖️'
    },
    {
      type: 'congé sans solde',
      title: 'Congé Sans Solde',
      description: 'Absence autorisée non rémunérée',
      remainingDays: 'Illimité',
      icon: '📅'
    },
    {
      type: 'congé maladie',
      title: 'Congé Maladie',
      description: 'Absence pour raison médicale',
      remainingDays: 10,
      icon: '🏥'
    }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };
  
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };

  // Trouver les informations du type de congé sélectionné
  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);

  return (
    <div className="conge-container">
      <div className="conge-main-content">
        <div className="conge-header">
          <h1 className="conge-title">
            Demande de Congé
            <span className="conge-title-underline"></span>
          </h1>
          <p className="conge-subtitle">Soumettez votre demande de congé en quelques clics</p>
        </div>

        {submitted ? (
          <div className="conge-form-container">
            <div className="conge-success-alert">
              <span className="conge-success-icon">✓</span>
              <p className="conge-success-text">
                Votre demande de congé a été soumise avec succès!
              </p>
            </div>
            
            <h2 className="conge-summary-title">Récapitulatif de votre demande</h2>
            
            <div className="conge-summary-card">
              <div className="conge-summary-grid">
                <div>
                  <p className="conge-summary-label">Type de congé</p>
                  <p className="conge-summary-value">
                    {selectedCongeType?.icon || ''} {selectedCongeType?.title || formData.type}
                  </p>
                </div>
                <div>
                  <p className="conge-summary-label">Nombre de jours</p>
                  <p className="conge-summary-value">{calculateDays()} jours</p>
                </div>
                <div>
                  <p className="conge-summary-label">Date de début</p>
                  <p className="conge-summary-value">{new Date(formData.startDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div>
                  <p className="conge-summary-label">Date de fin</p>
                  <p className="conge-summary-value">{new Date(formData.endDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div className="conge-col-span-2">
                  <p className="conge-summary-label">Motif</p>
                  <p className="conge-summary-value">{formData.reason}</p>
                </div>
                {formData.notes && (
                  <div className="conge-col-span-2">
                    <p className="conge-summary-label">Notes complémentaires</p>
                    <p className="conge-summary-value">{formData.notes}</p>
                  </div>
                )}
              </div>
            </div>
            
            <div className="conge-button-container">
              <button 
                onClick={() => setSubmitted(false)} 
                className="conge-button"
              >
                Nouvelle demande
              </button>
            </div>
          </div>
        ) : (
          <div className="conge-form-container">
            <form onSubmit={handleSubmit}>
              <div className="conge-form-header">
                <h2 className="conge-form-title">Formulaire de demande</h2>
                <p className="conge-form-description">Veuillez remplir tous les champs obligatoires</p>
              </div>
              
              <div className="conge-form-group">
                <label className="conge-label" htmlFor="type">Type de congé *</label>
                <div className="conge-select-container">
                  <select
                    id="type"
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    className="conge-select"
                    required
                  >
                    {congeTypes.map((conge, index) => (
                      <option key={index} value={conge.type}>
                        {conge.icon} {conge.title} ({typeof conge.remainingDays === 'number' 
                          ? `${conge.remainingDays} jours disponibles` 
                          : conge.remainingDays})
                      </option>
                    ))}
                  </select>
                  <div className="conge-select-arrow">▼</div>
                </div>
                <p className="conge-help-text">{selectedCongeType?.description}</p>
              </div>
              
              <div className="conge-form-row">
                <div className="conge-form-group">
                  <label className="conge-label" htmlFor="startDate">Date de début *</label>
                  <input
                    type="date"
                    id="startDate"
                    name="startDate"
                    value={formData.startDate}
                    onChange={handleChange}
                    className="conge-input"
                    required
                  />
                </div>
                
                <div className="conge-form-group">
                  <label className="conge-label" htmlFor="endDate">Date de fin *</label>
                  <input
                    type="date"
                    id="endDate"
                    name="endDate"
                    value={formData.endDate}
                    onChange={handleChange}
                    className="conge-input"
                    required
                  />
                </div>
              </div>
              
              {formData.startDate && formData.endDate && (
                <div className="conge-info-alert">
                  <span className="conge-info-icon">ℹ️</span>
                  <p className="conge-info-text">
                    Durée: <strong>{calculateDays()} jours</strong>
                  </p>
                </div>
              )}
              
              <div className="conge-form-group">
                <label className="conge-label" htmlFor="reason">Motif *</label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  className="conge-input"
                  placeholder="Précisez le motif de votre demande"
                  required
                />
              </div>
              
              <div className="conge-form-group">
                <label className="conge-label" htmlFor="notes">Notes complémentaires</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={formData.notes}
                  onChange={handleChange}
                  className="conge-textarea"
                  placeholder="Informations additionnelles (facultatif)"
                  rows={4}
                />
              </div>
              
              <div className="conge-button-container">
                <button type="submit" className="conge-button">
                  Soumettre la demande
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
      
      {/* Injection CSS */}
      <style jsx>{`
        .conge-container {
          background-color: #f5f7fa;
          min-height: 100vh;
          padding: 20px;
          font-family: "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
          color: #334155;
        }
        
        .conge-main-content {
          max-width: 800px;
          margin: 0 auto;
          padding-top: 2rem;
        }
        
        .conge-header {
          text-align: center;
          margin-bottom: 40px;
        }
        
        .conge-title {
          font-size: 2rem;
          font-weight: 700;
          color: #2563eb;
          margin: 0 0 10px 0;
          position: relative;
          display: inline-block;
          padding-bottom: 15px;
        }
        
        .conge-title-underline {
          content: "";
          position: absolute;
          bottom: 0;
          left: 50%;
          transform: translateX(-50%);
          width: 60%;
          height: 3px;
          background: linear-gradient(90deg, #2563eb, #60a5fa);
          border-radius: 2px;
          display: block;
        }
        
        .conge-subtitle {
          font-size: 1rem;
          color: #64748b;
          margin-top: 10px;
        }
        
        .conge-form-container {
          background-color: #ffffff;
          border-radius: 12px;
          padding: 30px;
          box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
          margin-bottom: 30px;
        }
        
        .conge-form-header {
          margin-bottom: 25px;
          border-bottom: 1px solid #e2e8f0;
          padding-bottom: 15px;
        }
        
        .conge-form-title {
          font-size: 1.5rem;
          font-weight: 600;
          color: #2563eb;
          margin: 0 0 5px 0;
        }
        
        .conge-form-description {
          font-size: 0.9rem;
          color: #64748b;
          margin: 0;
        }
        
        .conge-form-group {
          margin-bottom: 20px;
        }
        
        .conge-form-row {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          gap: 20px;
        }
        
        .conge-label {
          display: block;
          font-size: 0.9rem;
          font-weight: 600;
          color: #475569;
          margin-bottom: 8px;
        }
        
        .conge-input, .conge-textarea, .conge-select {
          width: 100%;
          padding: 12px 14px;
          border: 1px solid #e2e8f0;
          border-radius: 8px;
          font-size: 1rem;
          transition: all 0.2s ease;
          color: #334155;
          background-color: #ffffff;
        }
        
        .conge-input:focus, .conge-textarea:focus, .conge-select:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.2);
        }
        
        .conge-textarea {
          resize: vertical;
        }
        
        .conge-select-container {
          position: relative;
        }
        
        .conge-select {
          appearance: none;
          padding-right: 40px;
        }
        
        .conge-select-arrow {
          position: absolute;
          right: 15px;
          top: 50%;
          transform: translateY(-50%);
          color: #64748b;
          font-size: 0.8rem;
          pointer-events: none;
        }
        
        .conge-help-text {
          font-size: 0.8rem;
          color: #64748b;
          margin-top: 5px;
        }
        
        .conge-info-alert {
          background-color: #eff6ff;
          border-radius: 8px;
          padding: 14px;
          margin-bottom: 20px;
          display: flex;
          align-items: center;
          gap: 10px;
          border-left: 4px solid #3b82f6;
        }
        
        .conge-info-icon {
          font-size: 1.2rem;
        }
        
        .conge-info-text {
          color: #1e40af;
          font-size: 0.9rem;
          margin: 0;
        }
        
        .conge-button-container {
          margin-top: 25px;
          text-align: center;
        }
        
        .conge-button {
          background-color: #2563eb;
          color: #ffffff;
          border: none;
          border-radius: 8px;
          padding: 14px 30px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
          box-shadow: 0 2px 5px rgba(37, 99, 235, 0.3);
        }
        
        .conge-button:hover {
          background-color: #1d4ed8;
          box-shadow: 0 4px 8px rgba(37, 99, 235, 0.4);
        }
        
        .conge-success-alert {
          background-color: #dcfce7;
          border-radius: 8px;
          padding: 18px;
          margin-bottom: 30px;
          display: flex;
          align-items: center;
          gap: 15px;
          border-left: 4px solid #16a34a;
        }
        
        .conge-success-icon {
          color: #16a34a;
          font-size: 1.5rem;
          font-weight: bold;
        }
        
        .conge-success-text {
          color: #166534;
          font-size: 1rem;
          font-weight: 600;
          margin: 0;
        }
        
        .conge-summary-title {
          font-size: 1.5rem;
          font-weight: 700;
          color: #2563eb;
          margin-bottom: 25px;
          text-align: center;
        }
        
        .conge-summary-card {
          background-color: #f8fafc;
          padding: 25px;
          border-radius: 10px;
          margin-bottom: 30px;
          border: 1px solid #e2e8f0;
        }
        
        .conge-summary-grid {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          gap: 20px;
        }
        
        .conge-summary-label {
          font-size: 0.85rem;
          color: #64748b;
          margin-bottom: 6px;
          margin-top: 0;
        }
        
        .conge-summary-value {
          font-size: 1rem;
          font-weight: 600;
          color: #334155;
          margin: 0;
        }
        
        .conge-col-span-2 {
          grid-column: span 2;
        }
        
        @media (max-width: 768px) {
          .conge-form-row {
            grid-template-columns: 1fr;
            gap: 10px;
          }
          
          .conge-summary-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>
    </div>
  );
};

export default CardDemandeConge;
