import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Notification = ({ message, type, onClose }) => {
  const bgColor = type === 'error' ? 'bg-red-100 border-red-400 text-red-700' 
                 : type === 'success' ? 'bg-green-100 border-green-400 text-green-700' 
                 : 'bg-blue-100 border-blue-400 text-blue-700';

  return (
    <div className={`fixed top-4 right-4 border-l-4 p-4 ${bgColor} rounded shadow-lg min-w-64 z-50`}>
      <div className="flex justify-between items-center">
        <span>{message}</span>
        <button onClick={onClose} className="ml-4 text-lg">&times;</button>
      </div>
    </div>
  );
};

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [notification, setNotification] = useState(null);
  const fileInputRef = useRef(null);

  // Afficher une notification
  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 5000);
  };

  // Récupération des données utilisateur depuis le token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setUserData({
          nom: decoded.nom,
          prenom: decoded.prenom,
          email: decoded.email,
          soldeConge: decoded.soldeConge || 0
        });
      } catch (error) {
        console.error("Erreur de décodage du token", error);
      }
    }
  }, []);

  // Types de congés disponibles
  const congeTypes = [
    { type: 'congé payé', title: 'Congé Payé', remainingDays: userData.soldeConge, icon: '🏖️', description: 'Congé annuel rémunéré' },
    { type: 'rtt', title: 'RTT', remainingDays: 8, icon: '⏱️', description: 'Réduction du temps de travail' },
    { type: 'congé maladie', title: 'Congé Maladie', remainingDays: 'Illimité', icon: '🏥', description: 'Absence pour raison médicale' },
    { type: 'congé parental', title: 'Congé Parental', remainingDays: 'Selon droits', icon: '👶', description: 'Congé pour enfant' },
    { type: 'congé familial', title: 'Événement Familial', remainingDays: 'Selon événement', icon: '👪', description: 'Mariage, naissance, décès' },
    { type: 'congé sans solde', title: 'Congé Sans Solde', remainingDays: 'Illimité', icon: '📅', description: 'Absence non rémunérée' },
    { type: 'télétravail', title: 'Télétravail', remainingDays: 'Selon accord', icon: '🏠', description: 'Travail à distance exceptionnel' }
  ];

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Gestion des fichiers
  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };

  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Calcul du nombre de jours de congé
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Validations
    const daysRequested = calculateDays();
    
    if (daysRequested <= 0) {
      showNotification('La date de fin doit être postérieure à la date de début', 'error');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      showNotification('Veuillez joindre votre justificatif médical', 'error');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'congé payé' && daysRequested > userData.soldeConge) {
      showNotification(`Solde insuffisant. Vous avez ${userData.soldeConge} jours disponibles.`, 'error');
      setIsSubmitting(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const formDataToSend = new FormData();
      
      // Ajout des données au FormData
      Object.entries(formData).forEach(([key, value]) => {
        if (value !== null && key !== 'medicalFile') {
          formDataToSend.append(key, value);
        }
      });
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      // Envoi de la requête
      const response = await axios.post(
        '/adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succès
      showNotification(`Demande de ${formData.type} enregistrée avec succès!`, 'success');
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');
      
      // Mise à jour du solde si congé payé
      if (formData.type === 'congé payé') {
        setUserData(prev => ({
          ...prev,
          soldeConge: prev.soldeConge - daysRequested
        }));
      }

    } catch (error) {
      console.error('Erreur:', error);
      showNotification(error.response?.data?.message || 'Erreur lors de la soumission', 'error');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Variables dérivées
  const selectedCongeType = congeTypes.find(c => c.type === formData.type);
  const isMaladieType = formData.type === 'congé maladie';
  const needsJustification = ['congé maladie', 'congé familial'].includes(formData.type);
  const daysRequested = calculateDays();
  const remainingDays = formData.type === 'congé payé' ? userData.soldeConge - daysRequested : null;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-md relative">
      {/* Notification */}
      {notification && (
        <Notification 
          message={notification.message} 
          type={notification.type}
          onClose={() => setNotification(null)}
        />
      )}

      {/* En-tête */}
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-blue-800 mb-1">Demande de Congé</h1>
        <p className="text-gray-600">Bonjour {userData.prenom} {userData.nom}</p>
      </div>

      {/* Carte d'information */}
      <div className="bg-blue-50 p-4 rounded-lg mb-6 border border-blue-100">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="font-medium text-blue-800">Vos droits à congé</h3>
            <p className="text-sm text-gray-600">Solde actuel: {userData.soldeConge} jours</p>
          </div>
          {formData.type === 'congé payé' && daysRequested > 0 && (
            <div className="bg-blue-100 px-3 py-1 rounded-full">
              <span className="text-blue-800 font-medium">
                Solde après demande: {remainingDays >= 0 ? remainingDays : 0} jours
              </span>
            </div>
          )}
        </div>
      </div>

      {/* Formulaire */}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Type de congé */}
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Type de congé</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            {congeTypes.map((conge, index) => (
              <option key={index} value={conge.type}>
                {conge.icon} {conge.title} - {conge.description}
              </option>
            ))}
          </select>
          <p className="mt-1 text-sm text-gray-500">
            {selectedCongeType?.remainingDays !== 'Illimité' && selectedCongeType?.remainingDays !== 'Selon droits' && selectedCongeType?.remainingDays !== 'Selon événement' && selectedCongeType?.remainingDays !== 'Selon accord'
              ? `Jours disponibles: ${selectedCongeType?.remainingDays}`
              : selectedCongeType?.description}
          </p>
        </div>

        {/* Dates */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Date de début</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-2">Date de fin</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
              min={formData.startDate || new Date().toISOString().split('T')[0]}
            />
          </div>
        </div>

        {/* Résumé de la demande */}
        {formData.startDate && formData.endDate && (
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="flex justify-between items-center">
              <div>
                <h4 className="font-medium text-blue-800">Résumé de votre demande</h4>
                <p className="text-sm text-gray-600">
                  Du {new Date(formData.startDate).toLocaleDateString()} au {new Date(formData.endDate).toLocaleDateString()}
                </p>
              </div>
              <div className="bg-blue-100 px-3 py-1 rounded-full">
                <span className="font-medium text-blue-800">{daysRequested} jours</span>
              </div>
            </div>
          </div>
        )}

        {/* Motif */}
        {!isMaladieType && (
          <div>
            <label className="block text-gray-700 font-medium mb-2">
              Motif {needsJustification && '*'}
            </label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder={needsJustification ? "Précisez le motif (obligatoire)" : "Précisez le motif (facultatif)"}
              required={needsJustification}
            />
          </div>
        )}

        {/* Justificatif médical */}
        {isMaladieType && (
          <div>
            <label className="block text-gray-700 font-medium mb-2">Justificatif médical *</label>
            <div className="border-2 border-dashed border-blue-300 rounded-lg p-4 text-center bg-blue-50">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                className="hidden"
                id="medicalFile"
                accept=".pdf,.jpg,.jpeg,.png"
                required
              />
              <label htmlFor="medicalFile" className="cursor-pointer">
                {fileName ? (
                  <div className="flex items-center justify-between bg-white p-3 rounded-lg border">
                    <div className="flex items-center">
                      <span className="text-blue-600 mr-2">📄</span>
                      <span className="truncate max-w-xs">{fileName}</span>
                    </div>
                    <button 
                      type="button" 
                      onClick={removeFile}
                      className="text-red-500 hover:text-red-700 ml-2"
                    >
                      ✕
                    </button>
                  </div>
                ) : (
                  <div className="py-6">
                    <div className="mx-auto w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mb-2">
                      <span className="text-blue-600 text-xl">📎</span>
                    </div>
                    <p className="text-gray-700">Glissez-déposez votre justificatif ou cliquez pour sélectionner</p>
                    <p className="text-sm text-gray-500 mt-1">Formats acceptés: PDF, JPG, PNG (max 5MB)</p>
                  </div>
                )}
              </label>
            </div>
          </div>
        )}

        {/* Notes complémentaires */}
        <div>
          <label className="block text-gray-700 font-medium mb-2">Notes complémentaires</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="3"
            placeholder="Informations additionnelles (facultatif)"
          />
        </div>

        {/* Bouton de soumission */}
        <div className="pt-2">
          <button
            type="submit"
            disabled={isSubmitting}
            className={`w-full py-3 px-4 rounded-lg font-medium text-white transition duration-200 ${
              isSubmitting 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            {isSubmitting ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Envoi en cours...
              </span>
            ) : 'Soumettre la demande'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CardDemandeConge;
