import React, { useState } from "react";

export default function ProfileForm() {
  const [formData, setFormData] = useState({
    firstName: "Marine",
    lastName: "Durand",
    email: "marine@example.com",
    phone: "06 12 34 56 78",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
    
    // Clear error when field is edited
    if (errors[id]) {
      setErrors({
        ...errors,
        [id]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // Required fields
    ["firstName", "lastName", "email"].forEach(field => {
      if (!formData[field].trim()) {
        newErrors[field] = "Ce champ est obligatoire";
      }
    });
    
    // Email format
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }
    
    // Password confirmation
    if (formData.password && formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }
    
    // Password strength
    if (formData.password && formData.password.length < 8 && formData.password.length > 0) {
      newErrors.password = "Le mot de passe doit contenir au moins 8 caractères";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccessMessage("");
    
    if (validateForm()) {
      // Simuler une requête API
      setTimeout(() => {
        setSuccessMessage("Profil mis à jour avec succès!");
        // Faire défiler vers le haut pour voir le message
        window.scrollTo({ top: 0, behavior: "smooth" });
      }, 800);
    } else {
      // Faire défiler jusqu'à la première erreur
      const firstErrorField = document.getElementById(Object.keys(errors)[0]);
      if (firstErrorField) {
        firstErrorField.scrollIntoView({ behavior: "smooth", block: "center" });
        firstErrorField.focus();
      }
    }
  };

  const togglePasswordVisibility = (field) => {
    if (field === 'password') {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  return (
    <>
      <div className="profile-form-container">
        <div className="form-header">
          <h1>Profil Utilisateur</h1>
        </div>
        
        <div className="form-body">
          {successMessage && (
            <div className="success-message">
              <span className="success-icon">✓</span>
              {successMessage}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="form-section">
              <div className="form-row">
                <div className="form-field">
                  <label htmlFor="firstName">
                    Prénom <span className="required">*</span>
                  </label>
                  <input
                    id="firstName"
                    type="text"
                    className={errors.firstName ? "error" : ""}
                    value={formData.firstName}
                    onChange={handleChange}
                  />
                  {errors.firstName && <div className="error-text">{errors.firstName}</div>}
                </div>
                
                <div className="form-field">
                  <label htmlFor="lastName">
                    Nom <span className="required">*</span>
                  </label>
                  <input
                    id="lastName"
                    type="text"
                    className={errors.lastName ? "error" : ""}
                    value={formData.lastName}
                    onChange={handleChange}
                  />
                  {errors.lastName && <div className="error-text">{errors.lastName}</div>}
                </div>
              </div>

              <div className="form-row">
                <div className="form-field">
                  <label htmlFor="email">
                    Adresse email <span className="required">*</span>
                  </label>
                  <input
                    id="email"
                    type="email"
                    className={errors.email ? "error" : ""}
                    value={formData.email}
                    onChange={handleChange}
                  />
                  {errors.email && <div className="error-text">{errors.email}</div>}
                </div>
                
                <div className="form-field">
                  <label htmlFor="phone">
                    Téléphone
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    className={errors.phone ? "error" : ""}
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Ex: 06 12 34 56 78"
                  />
                  {errors.phone && <div className="error-text">{errors.phone}</div>}
                </div>
              </div>

              <div className="divider"></div>
              
              <div className="form-row">
                <div className="form-field">
                  <label htmlFor="password">
                    Mot de passe
                  </label>
                  <div className="password-input">
                    <input
                      id="password"
                      type={showPassword ? "text" : "password"}
                      className={errors.password ? "error" : ""}
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Laissez vide pour conserver l'actuel"
                    />
                    <button 
                      type="button" 
                      className="toggle-password" 
                      onClick={() => togglePasswordVisibility('password')}
                      aria-label={showPassword ? "Cacher le mot de passe" : "Montrer le mot de passe"}
                    >
                      {showPassword ? "👁️" : "👁️‍🗨️"}
                    </button>
                  </div>
                  {errors.password && <div className="error-text">{errors.password}</div>}
                </div>
                
                <div className="form-field">
