import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    statut: 'En attente'
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [userData, setUserData] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  useEffect(() => {
    // Récupérer les données utilisateur depuis localStorage
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData(userData);
        
        // Vérifier le solde de congé
        if (userData.soldedeconge === undefined || userData.soldedeconge
          === null) {
          setShowAlert(true);
          setAlertType('error');
          setAlertMessage("Impossible de déterminer votre solde de congé. Contactez l'administration.");
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Erreur de chargement des données utilisateur");
      }
    } else {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Authentification requise. Veuillez vous connecter.");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Vérifier les dates
    if (new Date(formData.startDate) > new Date(formData.endDate)) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("La date de fin doit être postérieure à la date de début");
      return;
    }
    
    // Vérifier le solde de congé pour les congés payés
    if (formData.type === 'congé payé') {
      const daysRequested = calculateDays();
      if (daysRequested > userData.soldedeconge
      ) {
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage(`Vous demandez ${daysRequested} jours mais il ne vous reste que ${userData.soldedeconge
        } jours de congé payé`);
        return;
      }
    }
    
    setLoading(true);
    setError(null);
    
    try {
      // Préparer les données à envoyer
      const demandeData = {
        ...formData,
        nom: userData.nom || '',
        prenom: userData.prenom || '',
        email: userData.email || '',
        days: calculateDays(),
        statut: 'En attente'
      };

      // Récupérer le token
      const token = localStorage.getItem('authToken') || 
                   localStorage.getItem('token') || 
                   localStorage.getItem('jwt');

      // Envoyer la demande au backend
      const response = await axios.post(
        '/adddemandeconge',
        demandeData,
        {
          headers: {
           'Content-Type': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setShowAlert(true);
        setAlertType('success');
        setAlertMessage('Votre demande de congé a été soumise avec succès! Statut: En attente');
        
        // Réinitialiser le formulaire après succès
        setFormData({
          startDate: '',
          endDate: '',
          reason: '',
          type: 'congé payé',
          notes: '',
          statut: 'En attente'
        });
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(err.response?.data?.message || err.message || 'Une erreur est survenue');
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    dateGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '15px',
      marginBottom: '20px'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    alert: {
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      transition: 'opacity 0.3s',
      opacity: 1
    },
    alertError: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      borderLeft: '4px solid #b91c1c'
    },
    alertSuccess: {
      backgroundColor: '#dcfce7',
      color: '#16a34a',
      borderLeft: '4px solid #16a34a'
    },
    daysInfo: {
      backgroundColor: '#e0f2fe',
      padding: '10px',
      borderRadius: '4px',
      marginBottom: '20px',
      textAlign: 'center'
    }
  };

  const Alert = ({ type, message, onClose }) => {
    return (
      <div style={{
        ...styles.alert,
        ...(type === 'error' ? styles.alertError : styles.alertSuccess)
      }}>
        <div>
          <span style={{marginRight: '10px'}}>
            {type === 'error' ? '⚠️' : '✅'}
          </span>
          {message}
        </div>
        <button 
          onClick={onClose} 
          style={{ 
            background: 'none', 
            border: 'none', 
            cursor: 'pointer',
            fontSize: '18px'
          }}
        >
          ×
        </button>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande de congé
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>
          Bonjour {userData?.prenom || ''} {userData?.nom || ''} {userData?.soldedeconge || ''}{userData?.email || ''}, remplissez votre demande
        </p>
      </div>

      {showAlert && (
        <Alert 
          type={alertType} 
          message={alertMessage}
          onClose={() => setShowAlert(false)}
        />
      )}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Type de congé</label>
          <select
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="congé payé">Congé payé ({userData?.soldedeconge

 || 0} jours restants)</option>
            <option value="rtt">RTT</option>
            <option value="congé maladie">Congé maladie</option>
            <option value="congé sans solde">Congé sans solde</option>
          </select>
        </div>

        <div style={styles.dateGrid}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Date de début *</label>
            <input
              type="date"
              name="startDate"
              style={styles.input}
              value={formData.startDate}
              onChange={handleChange}
              required
            />
          </div>
          <div style={styles.formGroup}>
            <label style={styles.label}>Date de fin *</label>
            <input
              type="date"
              name="endDate"
              style={styles.input}
              value={formData.endDate}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {formData.startDate && formData.endDate && (
          <div style={styles.daysInfo}>
            Durée: <strong>{calculateDays()} jours</strong>
            {formData.type === 'congé payé' && userData?.soldedeconge
 !== undefined && (
              <span> - Solde restant: {userData.soldedeconge
                - calculateDays()} jours</span>
            )}
          </div>
        )}

        <div style={styles.formGroup}>
          <label style={styles.label}>Motif *</label>
          <input
            type="text"
            name="reason"
            style={styles.input}
            value={formData.reason}
            onChange={handleChange}
            required
            placeholder="Raison de votre demande de congé"
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Notes complémentaires</label>
          <textarea
            name="notes"
            style={styles.textarea}
            value={formData.notes}
            onChange={handleChange}
            placeholder="Informations supplémentaires (facultatif)"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre la demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeConge;
