import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";

export default function Index() {
  // Animation pour les éléments au chargement de la page
  useEffect(() => {
    const animateElements = () => {
      const elements = document.querySelectorAll('.animate-fade-in');
      elements.forEach((element, index) => {
        setTimeout(() => {
          element.classList.add('opacity-100');
          element.classList.remove('opacity-0', 'translate-y-10');
        }, 200 * index);
      });
    };
    
    setTimeout(animateElements, 300);
  }, []);

  return (
    <>
      <IndexNavbar fixed />
      
      {/* Hero Section avec dégradé de couleurs UBCI */}
      <section className="header relative pt-16 items-center flex h-screen max-h-860-px">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-900 to-blue-700 opacity-90"></div>
        
        <div className="container mx-auto items-center flex flex-wrap relative z-10">
          <div className="w-full md:w-8/12 lg:w-6/12 xl:w-6/12 px-4">
            <div className="pt-32 sm:pt-0">
              <div className="animate-fade-in opacity-0 translate-y-10 transition duration-700 ease-out">
                <h1 className="font-bold text-5xl text-white mb-2">
                  UBCI HR MANAGER
                </h1>
                <div className="w-20 h-1 bg-yellow-500 mb-6"></div>
                <h2 className="font-semibold text-3xl text-white">
                  Votre portail de gestion RH
                </h2>
              </div>
              
              <p className="mt-6 text-lg leading-relaxed text-gray-100 animate-fade-in opacity-0 translate-y-10 transition duration-700 ease-out">
                UBCI RH est votre solution intégrée pour la gestion des ressources humaines, 
                facilitant le suivi du personnel, la gestion des congés et 
                l'automatisation des tâches administratives.
              </p>
              
              <div className="mt-12 flex flex-wrap gap-4 animate-fade-in opacity-0 translate-y-10 transition duration-700 ease-out">
                <Link
                  to="/postule"
                  className="group relative overflow-hidden px-8 py-4 rounded-lg bg-yellow-500 hover:bg-yellow-400 transition-all duration-300 shadow-lg"
                >
                  <span className="relative z-10 font-bold text-blue-900 uppercase text-sm flex items-center">
                    Postuler 
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-2 transition-transform duration-300 group-hover:translate-x-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                    </svg>
                  </span>
                  <div className="absolute inset-0 h-full w-full transform scale-0 rounded-lg transition-transform duration-300 group-hover:scale-100 origin-bottom-right bg-yellow-300 opacity-30"></div>
                </Link>
                
                <Link
                  to="/signin"
                  className="group relative overflow-hidden px-8 py-4 rounded-lg bg-white hover:bg-gray-100 transition-all duration-300 shadow-lg"
                >
                  <span className="relative z-10 font-bold text-blue-900 uppercase text-sm flex items-center">
                    Mon Espace
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-2 transition-transform duration-300 group-hover:translate-x-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
                    </svg>
                  </span>
                  <div className="absolute inset-0 h-full w-full transform scale-0 rounded-lg transition-transform duration-300 group-hover:scale-100 origin-bottom-right bg-blue-500 opacity-10"></div>
                </Link>
              </div>
              
              {/* Feature Cards */}
              <div className="mt-16 grid grid-cols-1 md:grid-cols-3 gap-4 animate-fade-in opacity-0 translate-y-10 transition duration-700 ease-out">
                <div className="bg-white bg-opacity-10 p-4 rounded-lg backdrop-blur-sm">
                  <div className="rounded-full bg-blue-800 p-3 w-12 h-12 flex items-center justify-center mb-3">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 018 0z" />
                    </svg>
                  </div>
                  <h3 className="font-semibold text-white text-lg mb-1">Gestion du Personnel</h3>
                  <p className="text-gray-200 text-sm">Suivi complet des employés et de leur carrière</p>
                </div>
                
                <div className="bg-white bg-opacity-10 p-4 rounded-lg backdrop-blur-sm">
                  <div className="rounded-full bg-blue-800 p-3 w-12 h-12 flex items-center justify-center mb-3">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                    </svg>
                  </div>
                  <h3 className="font-semibold text-white text-lg mb-1">Congés et Absences</h3>
                  <p className="text-gray-200 text-sm">Gestion simplifiée des demandes et approbations</p>
                </div>
                
                <div className="bg-white bg-opacity-10 p-4 rounded-lg backdrop-blur-sm">
                  <div className="rounded-full bg-blue-800 p-3 w-12 h-12 flex items-center justify-center mb-3">
