import React, { useState, useRef } from 'react';

const UnifiedRequestForm = () => {
  // Type de demande (avance, congé, formation)
  const [requestType, setRequestType] = useState('avance');
  
  // État commun pour tous les formulaires
  const [formData, setFormData] = useState({
    // Champs communs
    notes: '',
    
    // Avance sur salaire
    amount: '',
    urgency: 'standard',
    reason: '',
    
    // Congé
    startDate: '',
    endDate: '',
    congeType: 'congé payé',
    medicalFile: null,
    
    // Formation
    selectedFormation: null
  });
  
  // Liste des formations (normalement chargée depuis l'API)
  const [formations] = useState([
    {
      id: 1,
      titre: "Formation React Avancée",
      description: "Maîtrisez les hooks et le state management",
      date: "2025-06-15",
      duree: 16,
      places: 15,
      inscrits: 8,
      statut: 'Programmée'
    },
    {
      id: 2,
      titre: "Leadership et management d'équipe",
      description: "Techniques modernes de gestion d'équipe",
      date: "2025-05-20",
      duree: 8,
      places: 12,
      inscrits: 10,
      statut: 'Programmée'
    },
    {
      id: 3,
      titre: "Excel pour la finance",
      description: "Formules et tableaux croisés dynamiques",
      date: "2025-07-10",
      duree: 12,
      places: 20,
      inscrits: 5,
      statut: 'Programmée'
    }
  ]);
  
  // Types de congés
  const congeTypes = [
    { type: 'congé payé', title: 'Congé Payé Standard', icon: '🏖️' },
    { type: 'rtt', title: 'RTT', icon: '⏱️' },
    { type: 'congé maladie', title: 'Congé Maladie', icon: '🏥' },
    { type: 'congé parental', title: 'Congé Parental', icon: '👶' },
    { type: 'congé familial', title: 'Événement Familial', icon: '👪' },
    { type: 'congé sans solde', title: 'Congé Sans Solde', icon: '📅' },
    { type: 'télétravail', title: 'Télétravail Exceptionnel', icon: '🏠' }
  ];
  
  const fileInputRef = useRef(null);
  const [fileName, setFileName] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [selectedFormationId, setSelectedFormationId] = useState(null);
  
  // Gérer les changements dans le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
  
  // Gérer le changement de fichier pour les congés maladie
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setFormData({
        ...formData,
        medicalFile: file
      });
      setFileName(file.name);
    }
  };
  
  // Supprimer le fichier sélectionné
  const removeFile = () => {
    setFormData({
      ...formData,
      medicalFile: null
    });
    setFileName('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  // Sélectionner une formation
  const selectFormation = (formation) => {
    setFormData({
      ...formData,
      selectedFormation: formation
    });
    setSelectedFormationId(formation.id);
  };
  
  // Calculer le nombre de jours de congé
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };
  
  // Formatage de la date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };
  
  // Soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Valider le formulaire selon le type de demande
    if (requestType === 'conge' && formData.congeType === 'congé maladie' && !formData.medicalFile) {
      alert('Veuillez joindre votre justificatif médical');
      return;
    }
    
    // Construct submission data based on request type
    const submissionData = {
      type: requestType,
      date: new Date().toISOString(),
      ...formData
    };
    
    // Send to backend (simulated)
    console.log('Demande soumise:', submissionData);
    
    // Confirmation message based on request type
    let message = '';
    switch(requestType) {
      case 'avance':
        message = `Votre demande d'avance sur salaire de ${formData.amount} TND a été soumise avec succès!`;
        break;
      case 'conge':
        message = `Votre demande de ${formData.congeType} du ${formData.startDate} au ${formData.endDate} a été soumise avec succès!`;
        break;
      case 'formation':
        message = `Votre demande pour la formation "${formData.selectedFormation?.titre}" a été effectuée avec succès!`;
        break;
      default:
        message = "Votre demande a été soumise avec succès!";
    }
    
    // Display confirmation
    setConfirmationMessage(message);
    setShowConfirmation(true);
    
    // Reset form
    setFormData({
      notes: '',
      amount: '',
      urgency: 'standard',
      reason: '',
      startDate: '',
      endDate: '',
      congeType: 'congé payé',
      medicalFile: null,
      selectedFormation: null
    });
    setFileName('');
    setSelectedFormationId(null);
    
    // Hide confirmation after 3 seconds
    setTimeout(() => {
      setShowConfirmation(false);
    }, 3000);
  };
  
  return (
    <div className="bg-gray-100 min-h-screen p-4 font-sans text-gray-800">
      <div className="max-w-4xl mx-auto pt-8">
        <div className="text-center mb-10">
          <h1 className="text-4xl font-bold text-blue-800 mb-2 relative inline-block pb-4">
            Portail de Demandes RH
            <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
          </h1>
          <p className="text-lg text-gray-500">Gérez vos demandes professionnelles en un seul endroit</p>
        </div>
        
        {/* Type Selector Tabs */}
        <div className="bg-white rounded-t-lg shadow-sm border border-gray-200 overflow-hidden">
          <div className="flex">
            <button 
              onClick={() => setRequestType('avance')}
              className={`flex-1 py-4 px-6 text-center font-medium ${
                requestType === 'avance' 
                  ? 'bg-blue-50 text-blue-800 border-b-2 border-blue-600' 
                  : 'text-gray-500 hover:bg-gray-50'
              }`}
            >
              <span className="block text-xl mb-1">💰</span>
              Avance sur salaire
            </button>
            <button 
              onClick={() => setRequestType('conge')}
              className={`flex-1 py-4 px-6 text-center font-medium ${
                requestType === 'conge' 
                  ? 'bg-blue-50 text-blue-800 border-b-2 border-blue-600' 
                  : 'text-gray-500 hover:bg-gray-50'
              }`}
            >
              <span className="block text-xl mb-1">🏖️</span>
              Congé
            </button>
            <button 
              onClick={() => setRequestType('formation')}
              className={`flex-1 py-4 px-6 text-center font-medium ${
                requestType === 'formation' 
                  ? 'bg-blue-50 text-blue-800 border-b-2 border-blue-600' 
                  : 'text-gray-500 hover:bg-gray-50'
              }`}
            >
              <span className="block text-xl mb-1">📚</span>
              Formation
            </button>
          </div>
        </div>
        
        {/* Form Container */}
        <div className="bg-white rounded-b-lg p-6 shadow-md mb-8">
          <form onSubmit={handleSubmit}>
            {/* Avance sur salaire fields */}
            {requestType === 'avance' && (
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="amount">
                    Montant demandé (TND)
                  </label>
                  <input
                    type="number"
                    id="amount"
                    name="amount"
                    value={formData.amount}
                    onChange={handleChange}
                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    required
                    min="1"
                  />
                  <p className="text-xs text-gray-500 mt-1">Le montant maximum est de 30% de votre salaire mensuel</p>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="urgency">
                    Type de demande
                  </label>
                  <div className="relative">
                    <select
                      id="urgency"
                      name="urgency"
                      value={formData.urgency}
                      onChange={handleChange}
                      className="w-full p-3 border border-gray-300 rounded-md appearance-none focus:ring-blue-500 focus:border-blue-500"
                      required
                    >
                      <option value="standard">Standard (traitement sous 3 jours)</option>
                      <option value="urgent">Urgente (traitement sous 24h)</option>
                      <option value="exceptional">Exceptionnelle (évaluation sous 5 jours)</option>
                    </select>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-3">
                      <svg className="h-4 w-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                      </svg>
                    </div>
                  </div>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="reason">
                    Motif de la demande
                  </label>
                  <textarea
                    id="reason"
                    name="reason"
                    value={formData.reason}
                    onChange={handleChange}
                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 min-h-20"
                    required
                  />
                </div>
              </div>
            )}
            
            {/* Congé fields */}
            {requestType === 'conge' && (
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="congeType">
                    Type de congé
                  </label>
                  <div className="relative">
                    <select
                      id="congeType"
                      name="congeType"
                      value={formData.congeType}
                      onChange={handleChange}
                      className="w-full p-3 border border-gray-300 rounded-md appearance-none focus:ring-blue-500 focus:border-blue-500"
                      required
                    >
                      {congeTypes.map((conge, index) => (
                        <option key={index} value={conge.type}>
                          {conge.icon} {conge.title}
                        </option>
                      ))}
                    </select>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-3">
                      <svg className="h-4 w-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                      </svg>
                    </div>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="startDate">
                      Date de début
                    </label>
                    <input
                      type="date"
                      id="startDate"
                      name="startDate"
                      value={formData.startDate}
                      onChange={handleChange}
                      className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                      required
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="endDate">
                      Date de fin
                    </label>
                    <input
                      type="date"
                      id="endDate"
                      name="endDate"
                      value={formData.endDate}
                      onChange={handleChange}
                      className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                      required
                    />
                  </div>
                </div>
                
                {formData.startDate && formData.endDate && (
                  <div className="bg-blue-50 text-blue-700 p-4 rounded-md flex items-center">
                    <span className="mr-2">ℹ️</span>
                    <p className="text-sm font-medium">
                      Durée: <strong>{calculateDays()} jours</strong>
                    </p>
                  </div>
                )}
                
                {formData.congeType === 'congé maladie' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="medicalFile">
                      Justificatif médical *
                    </label>
                    <div className="relative">
                      <label 
                        htmlFor="medicalFile" 
                        className="flex items-center justify-center p-4 border border-dashed border-blue-500 rounded-md bg-gray-50 text-gray-600 cursor-pointer"
                      >
                        {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif médical'}
                      </label>
                      <input
                        type="file"
                        id="medicalFile"
                        ref={fileInputRef}
                        className="absolute inset-0 opacity-0 w-full h-full cursor-pointer"
                        accept=".pdf,.jpg,.jpeg,.png"
                        onChange={handleFileChange}
                        required
                      />
                    </div>
                    {formData.medicalFile && (
                      <div className="flex items-center mt-2 p-2 bg-gray-50 rounded-md">
                        <span className="mr-2">📄</span>
                        <span className="flex-1 truncate">{fileName}</span>
                        <button 
                          type="button" 
                          className="text-red-500"
                          onClick={removeFile}
                        >
                          ✕
                        </button>
                      </div>
                    )}
                  </div>
                )}
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="reason">
                    Motif {['congé maladie', 'congé familial'].includes(formData.congeType) && '*'}
                  </label>
                  <textarea
                    id="reason"
                    name="reason"
                    value={formData.reason}
                    onChange={handleChange}
                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 min-h-20"
                    placeholder={['congé maladie', 'congé familial'].includes(formData.congeType) 
                      ? "Précisez le motif (obligatoire)" 
                      : "Précisez le motif de votre demande (facultatif)"}
                    required={['congé maladie', 'congé familial'].includes(formData.congeType)}
                  />
                </div>
              </div>
            )}
            
            {/* Formation fields */}
            {requestType === 'formation' && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Sélectionnez une formation</h3>
                
                {/* Formation list */}
                <div className="grid grid-cols-1 gap-4">
                  {formations.map(formation => (
                    <div 
                      key={formation.id}
                      className={`rounded-lg border ${
                        selectedFormationId === formation.id 
                          ? 'border-blue-500 ring-2 ring-blue-200' 
                          : 'border-gray-200'
                      } cursor-pointer transition-all`}
                      onClick={() => selectFormation(formation)}
                    >
                      <div className="flex items-center p-4">
                        <div className="flex-1">
                          <div className="flex justify-between items-start">
                            <h3 className="text-base font-medium text-gray-900">{formation.titre}</h3>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                              formation.places - formation.inscrits <= 3
                                ? 'bg-red-100 text-red-800'
                                : 'bg-blue-100 text-blue-800'
                            }`}>
                              {formation.places - formation.inscrits} places restantes
                            </span>
                          </div>
                          <p className="text-sm text-gray-500 mt-1">{formation.description}</p>
                          
                          <div className="grid grid-cols-3 gap-2 mt-3">
                            <div className="flex items-center text-sm text-gray-600">
                              <span className="mr-2">📅</span>
                              <span>{formatDate(formation.date)}</span>
                            </div>
                            <div className="flex items-center text-sm text-gray-600">
                              <span className="mr-2">⏱️</span>
                              <span>{formation.duree} heures</span>
                            </div>
                            <div className="flex items-center text-sm text-gray-600">
                              <span className="mr-2">👥</span>
                              <span>{formation.inscrits}/{formation.places} participants</span>
                            </div>
                          </div>
                        </div>
                        
                        <div className="ml-4">
                          <div className={`w-6 h-6 rounded-full border-2 ${
                            selectedFormationId === formation.id 
                              ? 'border-blue-500 bg-blue-500' 
                              : 'border-gray-300'
                          } flex items-center justify-center`}>
                            {selectedFormationId === formation.id && (
                              <svg className="w-4 h-4 text-white" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7"></path>
                              </svg>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
                
                {selectedFormationId && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="formationReason">
                      Motivation pour cette formation
                    </label>
                    <textarea
                      id="reason"
                      name="reason"
                      value={formData.reason}
                      onChange={handleChange}
                      className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 min-h-20"
                      placeholder="Expliquez comment cette formation s'inscrit dans votre parcours professionnel"
                    />
                  </div>
                )}
              </div>
            )}
            
            {/* Notes communes pour tous les types de demande */}
            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="notes">
                Notes complémentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 min-h-20"
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            {/* Submit button */}
            <div className="mt-6">
              <button 
                type="submit" 
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-md shadow transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              >
                {requestType === 'avance' && 'Soumettre la demande d\'avance'}
                {requestType === 'conge' && 'Soumettre la demande de congé'}
                {requestType === 'formation' && 'Soumettre la demande de formation'}
              </button>
            </div>
          </form>
        </div>
        
        {/* Toast de confirmation */}
        {showConfirmation && (
          <div className="fixed bottom-8 left-1/2 transform -translate-x-1/2 bg-green-500 text-white rounded-lg shadow-lg z-50 min-w-80 max-w-md">
            <div className="flex items-center p-4 gap-3">
              <div className="flex-shrink-0 bg-white rounded-full w-8 h-8 flex items-center justify-center">
                <span className="text-green-500 text-sm">✓</span>
              </div>
              <p className="text-sm">{confirmationMessage}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UnifiedRequestForm;
