import React, { useState } from 'react';
import './Auth.css';
import Navbar from 'components/Navbars/IndexNavbar';

const Signup = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [acceptTerms, setAcceptTerms] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    
    // Basic form validation
    if (!formData.fullName || !formData.email || !formData.password || !formData.confirmPassword) {
      setError('Please fill in all fields.');
      return;
    }
    
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    
    if (!acceptTerms) {
      setError('You must accept the terms and conditions.');
      return;
    }
    
    // Simulate registration request
    setIsLoading(true);
    
    // Mock API call
    setTimeout(() => {
      setIsLoading(false);
      // For demonstration purposes. In real app, you would handle registration logic here
      console.log('Registration attempt:', formData, 'Accept terms:', acceptTerms);
    }, 1500);
  };

  return (
    <>
      <Navbar/>
      <div className="signup-page">
        <div className="signup-content">
          {/* Left panel with only background image */}
          <div className="signup-left-panel">
            {/* No content here - the background image is applied via CSS */}
          </div>
          
          <div className="signup-right-panel">
            <div className="signup-form-wrapper">
              <h3 className="signup-title">Create an account</h3>
              
              {error && <div className="error-alert">{error}</div>}
              
              <form onSubmit={handleSubmit}>
                <div className="input-group">
                  <label htmlFor="fullName">Full Name</label>
                  <div className="input-container">
                    <input
                      type="text"
                      id="fullName"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      placeholder="Enter your full name"
                      disabled={isLoading}
                    />
                  </div>
                </div>
                
                <div className="input-group">
                  <label htmlFor="email">Email Address</label>
                  <div className="input-container">
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Enter your email address"
                      disabled={isLoading}
                    />
                  </div>
                </div>
                
                <div className="input-group">
                  <label htmlFor="password">Password</label>
                  <div className="input-container">
                    <input
                      type="password"
                      id="password"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Create a password"
                      disabled={isLoading}
                    />
                  </div>
                </div>
                
                <div className="input-group">
                  <label htmlFor="confirmPassword">Confirm Password</label>
                  <div className="input-container">
                    <input
                      type="password"
                      id="confirmPassword"
                      name="confirmPassword"
                      value={formData.confirmPassword}
                      onChange={handleChange}
                      placeholder="Confirm your password"
                      disabled={isLoading}
                    />
                  </div>
                </div>
                
                <div className="terms-option">
                  <label className="checkbox-container">
                    <input
                      type="checkbox"
                      checked={acceptTerms}
                      onChange={() => setAcceptTerms(!acceptTerms)}
                      disabled={isLoading}
                    />
                    <span className="checkmark"></span>
                    <span>I agree to the <a href="/terms" className="terms-link">Terms of Service</a> and <a href="/privacy" className="terms-link">Privacy Policy</a></span>
                  </label>
                </div>
                
                <button 
                  type="submit" 
                  className={`signup-btn ${isLoading ? 'loading' : ''}`}
                  disabled={isLoading}
                >
                  {isLoading ? 'Creating account...' : 'Create account'}
                </button>
              </form>
              
              <div className="help-link">
                <p>Already have an account? <a href="/signin">Sign in</a></p>
              </div>
            </div>
            
            <div className="footer">
              <p>&copy; {new Date().getFullYear()} UBCI. All rights reserved.</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Signup;
