import React, { useState } from 'react';
import moment from 'moment';
import 'moment/locale/fr';

const CalendrierJoursFeries = () => {
  const [currentDate, setCurrentDate] = useState(moment());

  // Configuration de la langue franÃ§aise
  moment.locale('fr');

  // Jours fÃ©riÃ©s en Tunisie (dates fixes pour 2025)
  const joursFeries = [
    { date: '2025-01-01', nom: "Jour de l'an" },
    { date: '2025-01-14', nom: "FÃªte de la RÃ©volution et de la Jeunesse" },
    { date: '2025-03-20', nom: "FÃªte de l'IndÃ©pendance" },
    { date: '2025-04-09', nom: "Jour des Martyrs" },
    { date: '2025-05-01', nom: "FÃªte du Travail" },
    { date: '2025-07-25', nom: "FÃªte de la RÃ©publique" },
    { date: '2025-08-13', nom: "FÃªte de la Femme" },
    { date: '2025-10-15', nom: "FÃªte de l'Ã‰vacuation" }
  ];
  
  // Jours fÃ©riÃ©s Ã  dates variables (basÃ©s sur calendriers lunaires)
  // Ces dates sont approximatives pour 2025
  const joursFeriesVariables = [
    { date: '2025-04-10', nom: "AÃ¯d El-Fitr", duree: 3 },
    { date: '2025-06-17', nom: "AÃ¯d El-Idha", duree: 3 },
    { date: '2025-07-07', nom: "Nouvel An HÃ©gire" },
    { date: '2025-09-16', nom: "Mouled" }
  ];

  // Fonction pour vÃ©rifier si une date est un jour fÃ©riÃ©
  const estJourFerie = (date) => {
    const dateFormatee = moment(date).format('YYYY-MM-DD');
    
    // VÃ©rifier les jours fÃ©riÃ©s fixes
    const fixe = joursFeries.find(jour => jour.date === dateFormatee);
    if (fixe) return fixe.nom;
    
    // VÃ©rifier les jours fÃ©riÃ©s variables
    for (const jour of joursFeriesVariables) {
      const dateDebut = moment(jour.date);
      const dateFin = moment(jour.date).add((jour.duree || 1) - 1, 'days');
      
      if (moment(date).isBetween(dateDebut, dateFin, null, '[]')) {
        return jour.nom;
      }
    }
    
    return null;
  };

  // GÃ©nÃ©ration des jours du mois
  const genererJoursDuMois = () => {
    const debutMois = moment(currentDate).startOf('month');
    const finMois = moment(currentDate).endOf('month');
    const debutCalendrier = moment(debutMois).startOf('week');
    const finCalendrier = moment(finMois).endOf('week');
    
    const jours = [];
    let jour = moment(debutCalendrier);
    
    while (jour.isSameOrBefore(finCalendrier)) {
      jours.push(moment(jour));
      jour.add(1, 'day');
    }
    
    return jours;
  };

  // Changement de mois
  const moisPrecedent = () => {
    setCurrentDate(moment(currentDate).subtract(1, 'month'));
  };
  
  const moisSuivant = () => {
    setCurrentDate(moment(currentDate).add(1, 'month'));
  };

  return (
    <div className="calendrier-container">
      <div className="calendrier-header">
        <h2>Calendrier des Jours FÃ©riÃ©s en Tunisie</h2>
        <div className="navigation-mois">
          <button className="btn-navigation" onClick={moisPrecedent}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M15 18l-6-6 6-6"></path>
            </svg>
          </button>
          <h3>{currentDate.format('MMMM YYYY')}</h3>
          <button className="btn-navigation" onClick={moisSuivant}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M9 18l6-6-6-6"></path>
            </svg>
          </button>
        </div>
      </div>
      
      <div className="jours-semaine">
        {['Lun', 'Mar', 'Mer', 'Jeu', 'Ven', 'Sam', 'Dim'].map(jour => (
          <div key={jour} className="jour-semaine">{jour}</div>
        ))}
      </div>
      
      <div className="jours-mois">
        {genererJoursDuMois().map(jour => {
          const estMoisCourant = jour.month() === currentDate.month();
          const estAujourdhui = jour.isSame(moment(), 'day');
          const jourFerie = estJourFerie(jour);
          
          return (
            <div 
              key={jour.format('YYYY-MM-DD')} 
              className={`jour-calendrier ${estMoisCourant ? 'mois-courant' : 'autre-mois'} 
                         ${estAujourdhui ? 'aujourdhui' : ''} 
                         ${jourFerie ? 'jour-ferie' : ''}`}
            >
              <div className="numero-jour">{jour.date()}</div>
              {jourFerie && (
                <div className="indicateur-ferie">
                  <div className="info-ferie">
                    <span className="nom-ferie">{jourFerie}</span>
                    <span className="icone-ferie">ðŸŒ™</span>
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>
      
      <div className="legende-container">
        <h4>LÃ©gende des jours fÃ©riÃ©s</h4>
        <div className="liste-jours-feries">
          <h5>FÃªtes nationales et civiles</h5>
          <ul>
            {joursFeries.map(jour => (
              <li key={jour.date}>
                <span className="date-ferie">{moment(jour.date).format('DD MMMM')}</span>
                <span className="nom-ferie-legende">{jour.nom}</span>
              </li>
            ))}
          </ul>
          
          <h5>FÃªtes religieuses (dates approximatives pour 2025)</h5>
          <ul>
            {joursFeriesVariables.map(jour => (
              <li key={jour.date}>
                <span className="date-ferie">{moment(jour.date).format('DD MMMM')}</span>
                <span className="nom-ferie-legende">{jour.nom}</span>
                {jour.duree > 1 && <span className="duree-ferie">({jour.duree} jours)</span>}
              </li>
            ))}
          </ul>
        </div>
      </div>
      
      <style jsx>{`
        .calendrier-container {
          max-width: 1000px;
          margin: 0 auto;
          font-family: 'Roboto', 'Arial', sans-serif;
          background-color: #fff;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
          padding: 20px;
        }
        
        .calendrier-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 25px;
          padding-bottom: 15px;
          border-bottom: 1px solid #eaeaea;
        }
        
        .calendrier-header h2 {
          font-size: 24px;
          font-weight: 500;
          color: #333;
