import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0 // Chang√© de soldedeconge √† soldeConge pour correspondre au backend
  });
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  // Styles (identique √† votre version originale)
  const styles = {
    // ... (tous les styles restent identiques)
  };

  // Types de cong√©s avec des ic√¥nes
  const congeTypes = [
    {
      type: 'cong√© pay√©',
      title: 'Cong√© Pay√© Standard',
      description: 'Cong√© annuel r√©mun√©r√©',
      remainingDays: userData.soldeConge, // Chang√© de soldedeconge √† soldeConge
      icon: 'üèñÔ∏è'
    },
    // ... (autres types restent identiques)
  ];

  // R√©cup√©ration des donn√©es utilisateur et du token
  useEffect(() => {
    // Essayer d'abord de r√©cup√©rer les donn√©es utilisateur
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userDataObj = JSON.parse(userDataStr);
        setUserData({
          nom: userDataObj.nom || '',
          prenom: userDataObj.prenom || '',
          email: userDataObj.email || '',
          soldeConge: userDataObj.soldeConge || userDataObj.soldedeconge || 0 // G√®re les deux cas
        });
        setIsAuthenticated(!!userDataObj.email);
        
        // R√©cup√©rer le token
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) {
          setToken(authToken);
        } else {
          console.warn("Aucun token trouv√©");
          setShowAlert(true);
          setAlertType('error');
          setAlertMessage("Erreur d'authentification. Veuillez vous reconnecter.");
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Erreur lors de la r√©cup√©ration de vos donn√©es. Veuillez vous reconnecter.");
      }
    } else {
      // Si userData n'existe pas, essayer avec la m√©thode du token
      const savedToken = localStorage.getItem('authToken') || 
                       localStorage.getItem('token') || 
                       localStorage.getItem('jwt');
      if (savedToken) {
        try {
          const decoded = JSON.parse(atob(savedToken.split('.')[1]));
          setUserData({
            nom: decoded.nom || '',
            prenom: decoded.prenom || '',
            email: decoded.email || '',
            soldeConge: decoded.soldeConge || decoded.soldedeconge || 0 // G√®re les deux cas
          });
          setToken(savedToken);
          setIsAuthenticated(true);
        } catch (error) {
          console.error("Erreur de d√©codage du token", error);
          setShowAlert(true);
          setAlertType('error');
          setAlertMessage("Token invalide. Veuillez vous reconnecter.");
        }
      } else {
        console.warn("Aucune donn√©e utilisateur trouv√©e");
        setIsAuthenticated(false);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Veuillez vous connecter pour acc√©der √† ce service.");
      }
    }
  }, []);

  // ... (les autres fonctions handleChange, handleFileChange, removeFile restent identiques)

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // V√©rification de l'authentification
    if (!isAuthenticated || !token) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Veuillez vous connecter pour soumettre une demande de cong√©");
      return;
    }
    
    // Validation
    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage('Veuillez joindre votre justificatif m√©dical');
      return;
    }

    try {
      const formDataToSend = new FormData();
      
      // Utilisez les noms exacts du sch√©ma backend
      formDataToSend.append('dateDebut', formData.startDate); // Chang√© de DateDebut √† dateDebut
      formDataToSend.append('dateFin', formData.endDate); // Chang√© de DateFin √† dateFin
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      formDataToSend.append('nom', userData.nom);
      formDataToSend.append('prenom', userData.prenom);
      formDataToSend.append('email', userData.email);
      
      if (formData.medicalFile) {
        formDataToSend.append('justificatif', formData.medicalFile); // Chang√© de medicalFile √† justificatif
      }

      const response = await axios.post(
        '/adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succ√®s
      setConfirmationMessage(`Demande de ${formData.type} du ${formData.startDate} au ${formData.endDate} enregistr√©e !`);
      setShowConfirmation(true);
      
      // R√©initialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error);
      const errorMessage = error.response?.data?.message || 
                        error.response?.data?.error || 
                        "Erreur lors de la soumission";
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(errorMessage);
    }
  };
  
  // ... (le reste du code reste identique)

  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Cong√©
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}, d√©couvrez et soumettez vos demandes de cong√©s en quelques clics</p>
        </div>

        {showAlert && (
          <Alert 
            type={alertType} 
            message={alertMessage} 
          />
        )}

<div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Formulaire de demande de cong√©</h2>
            <p style={styles.formDescription}>Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="type">
                Type de cong√©
              </label>
              <div style={styles.selectContainer}>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={styles.select}
                  required
                >
                  {congeTypes.map((conge, index) => (
                    <option key={index} value={conge.type}>
                      {conge.icon} {conge.title}
                    </option>
                  ))}
                </select>
                <div style={styles.selectArrow}></div>
              </div>
              <span style={styles.congeInfoTag}>
                {selectedCongeType?.description} - 
                {typeof selectedCongeType?.remainingDays === 'number' 
                  ? ` ${selectedCongeType.remainingDays} jours disponibles` 
                  : ` ${selectedCongeType?.remainingDays}`}
              </span>
            </div>
            
            {isMaladieType ? (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="medicalFile">
                  Justificatif m√©dical *
                </label>
                <div style={styles.fileInputContainer}>
                  <label style={styles.fileInputLabel} htmlFor="medicalFile">
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif m√©dical'}
                  </label>
                  <input
                    type="file"
                    id="medicalFile"
                    ref={fileInputRef}
                    style={styles.fileInput}
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    required
                  />
                </div>
                {formData.medicalFile && (
                  <div style={styles.selectedFile}>
                    <span style={styles.fileIcon}>üìÑ</span>
                    <span style={styles.fileInfo}>{fileName}</span>
                    <button 
                      type="button" 
                      style={styles.removeButton}
                      onClick={removeFile}
                    >
                      ‚úï
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="reason">
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder={needsJustification 
                    ? "Pr√©cisez le motif (obligatoire)" 
                    : "Pr√©cisez le motif de votre demande (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}
            
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="startDate">
                  Date de d√©but
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
              
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="endDate">
                  Date de fin
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
            </div>
            
            {formData.startDate && formData.endDate && (
              <div style={styles.alert}>
                <span style={styles.alertIcon}>‚ÑπÔ∏è</span>
                <p style={styles.alertText}>
                  Dur√©e: <strong>{calculateDays()} jours</strong>
                  {formData.type === 'cong√© pay√©' && (
                    <span> - Solde restant: {userData.soldedeconge - calculateDays()} jours</span>
                  )}
                </p>
              </div>
            )}
            
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="notes">
                Notes compl√©mentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                style={styles.textarea}
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            <div style={styles.buttonContainer}>
              <button type="submit" style={styles.button}>
                Soumettre la demande
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Toast de confirmation */}
      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={styles.toastContent}>
            <span style={styles.toastIcon}>‚úì</span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}

      <style>
        {`
        @keyframes slideUp {
          from {
            transform: translate(-50%, 100%);
            opacity: 0;
          }
          to {
            transform: translate(-50%, 0);
            opacity: 1;
          }
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }
        `}
      </style>
    </div>
  );
};

export default CardDemandeConge;
