import React, { useState, useEffect } from 'react';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [salaire, setSalaire] = useState(0);
  const [montantMax, setMontantMax] = useState(0);

  useEffect(() => {
    // Debug: Afficher toutes les clés dans localStorage pour savoir où chercher le token
    console.log("Contenu de localStorage:", Object.keys(localStorage));
    
    // Approche 1: Vérifier le token directement
    const storedToken = localStorage.getItem('token');
    
    // Approche 2: Vérifier dans userInfo
    let userInfoToken = null;
    let userSalaire = 0;
    try {
      const userInfo = localStorage.getItem('userInfo');
      if (userInfo) {
        const parsedUserInfo = JSON.parse(userInfo);
        if (parsedUserInfo && parsedUserInfo.token) {
          userInfoToken = parsedUserInfo.token;
          // Le salaire est dans le token
          if (parsedUserInfo.token.salaire) {
            userSalaire = parseFloat(parsedUserInfo.token.salaire);
          }
        }
      }
    } catch (e) {
      console.error("Erreur dans la lecture de userInfo:", e);
    }
    
    // Approche 3: Rechercher dans d'autres clés courantes
    const authToken = localStorage.getItem('authToken');
    const jwtToken = localStorage.getItem('jwt');
    const accessToken = localStorage.getItem('accessToken');
    
    // Utiliser le premier token trouvé
    const finalToken = storedToken || userInfoToken || authToken || jwtToken || accessToken;
    
    // Récupérer le salaire depuis le token
    let salaireFromToken = 0;
    if (finalToken && typeof finalToken === 'object' && finalToken.salaire) {
      salaireFromToken = parseFloat(finalToken.salaire);
    }
    
    // Calculer le montant maximum (30% du salaire)
    const maxAmount = salaireFromToken * 0.3;
    
    if (finalToken) {
      console.log("Token trouvé dans localStorage");
      setToken(finalToken);
      setIsAuthenticated(true);
    } else {
      console.warn("Aucun token trouvé dans localStorage");
      
      // Forcer l'authentification pour le développement si nécessaire
      // Commenter cette ligne en production
      setIsAuthenticated(true); // Pour tester sans token valide
    }
    
    setSalaire(salaireFromToken);
    setMontantMax(maxAmount);
    console.log("Salaire récupéré:", salaireFromToken, "Montant max:", maxAmount);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Réinitialiser l'erreur lorsque l'utilisateur modifie le montant
    if (name === 'montant') {
      setError(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }
      
      // Vérifier que le montant est inférieur ou égal à 30% du salaire
      const montantDemande = parseFloat(formData.montant);
      if (montantDemande > montantMax) {
        throw new Error(`Le montant demandé dépasse 30% de votre salaire. Maximum autorisé: ${montantMax.toFixed(2)} TND`);
      }

      // Cette partie serait remplacée par un appel réel à l'API
      // Simuler une soumission pour la démonstration
      setTimeout(() => {
        setSuccess(true);
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
        setLoading(false);
      }, 1000);
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {error && <div style={styles.error}>{error}</div>}
      {success && <div style={styles.success}>Votre demande a été soumise avec succès!</div>}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
            max={montantMax || ''}
          />
          <p style={styles.infoText}>
            Le montant maximum est de 30% de votre salaire mensuel
            {montantMax > 0 && ` (${montantMax.toFixed(2)} TND)`}
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
