import React from "react";

export default function Footer() {
  return (
    <>
      <footer className="footer">
        <div className="wave-container">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            preserveAspectRatio="none"
            version="1.1"
            viewBox="0 0 2560 100"
            x="0"
            y="0"
          >
            <polygon
              className="wave-polygon"
              points="2560 0 2560 100 0 100"
            ></polygon>
          </svg>
        </div>
        <div className="footer-container">
          <div className="footer-content">
            <div className="social-section">
              <h4 className="social-title">UBCI - Suivez-nous</h4>
              <div className="social-icons">
                <button className="social-button facebook" type="button">
                  <i className="fab fa-facebook-f"></i>
                </button>
                <button className="social-button twitter" type="button">
                  <i className="fab fa-twitter"></i>
                </button>
                <button className="social-button instagram" type="button">
                  <i className="fab fa-instagram"></i>
                </button>
                <button className="social-button linkedin" type="button">
                  <i className="fab fa-linkedin-in"></i>
                </button>
              </div>
            </div>
          </div>
          <hr className="footer-divider" />
          <div className="copyright-section">
            <div className="copyright-text">
              Copyright © {new Date().getFullYear()} - Union Bancaire pour le Commerce et l'Industrie
            </div>
          </div>
        </div>

        {/* CSS injecté */}
        <style jsx>{`
          .footer {
            position: relative;
            background-color: #f8f9fa;
            padding-top: 2rem;
            padding-bottom: 1.5rem;
          }

          .wave-container {
            position: absolute;
            bottom: auto;
            top: 0;
            left: 0;
            right: 0;
            width: 100%;
            height: 5rem;
            pointer-events: none;
            overflow: hidden;
            margin-top: -5rem;
            transform: translateZ(0);
          }

          .wave-polygon {
            fill: #f8f9fa;
          }

          .footer-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 1rem;
          }

          .footer-content {
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
          }

          .social-section {
            width: 100%;
            text-align: center;
          }

          .social-title {
            font-size: 1.5rem;
            font-weight: 600;
            color: #005793;
            margin-bottom: 1rem;
          }

          .social-icons {
            display: flex;
            justify-content: center;
            margin-top: 1.5rem;
            margin-bottom: 1.5rem;
          }

          .social-button {
            background: white;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            height: 2.5rem;
            width: 2.5rem;
            display: flex;
            align-items: center;
            justify-content: center;
            border-radius: 50%;
            border: none;
            outline: none;
            margin-right: 0.5rem;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            cursor: pointer;
          }

          .social-button:hover {
            transform: translateY(-3px);
            box-shadow: 0 6px 8px rgba(0, 0, 0, 0.15);
          }

          .facebook {
            color: #3b5998;
          }

          .twitter {
            color: #1da1f2;
          }

          .instagram {
            color: #e1306c;
          }

          .linkedin {
            color: #0077b5;
          }

          .footer-divider {
            border: 0;
            height: 1px;
            background-color: #d1d5db;
            margin: 1.5rem 0;
          }

          .copyright-section {
            display: flex;
            flex-wrap: wrap;
            align-items: center;
            justify-content: center;
          }

          .copyright-text {
            font-size: 0.875rem;
            color: #6b7280;
            font-weight: 500;
            padding: 0.25rem 0;
            text-align: center;
          }

          /* Responsive styles */
          @media (max-width: 768px) {
            .social-icons {
              flex-wrap: wrap;
            }
            
            .social-button {
              margin-bottom: 0.5rem;
            }
          }
        `}</style>
      </footer>
    </>
  );
}
