import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0 // Chang√© de soldedeconge √† soldeConge pour correspondre au backend
  });
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  // Styles (identique √† votre version originale)
  const styles = {
    // ... (tous les styles restent identiques)
  };

  // Types de cong√©s avec des ic√¥nes
  const congeTypes = [
    {
      type: 'cong√© pay√©',
      title: 'Cong√© Pay√© Standard',
      description: 'Cong√© annuel r√©mun√©r√©',
      remainingDays: userData.soldeConge, // Chang√© de soldedeconge √† soldeConge
      icon: 'üèñÔ∏è'
    },
    // ... (autres types restent identiques)
  ];

  // R√©cup√©ration des donn√©es utilisateur et du token
  useEffect(() => {
    // Essayer d'abord de r√©cup√©rer les donn√©es utilisateur
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userDataObj = JSON.parse(userDataStr);
        setUserData({
          nom: userDataObj.nom || '',
          prenom: userDataObj.prenom || '',
          email: userDataObj.email || '',
          soldeConge: userDataObj.soldeConge || userDataObj.soldedeconge || 0 // G√®re les deux cas
        });
        setIsAuthenticated(!!userDataObj.email);
        
        // R√©cup√©rer le token
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) {
          setToken(authToken);
        } else {
          console.warn("Aucun token trouv√©");
          setShowAlert(true);
          setAlertType('error');
          setAlertMessage("Erreur d'authentification. Veuillez vous reconnecter.");
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Erreur lors de la r√©cup√©ration de vos donn√©es. Veuillez vous reconnecter.");
      }
    } else {
      // Si userData n'existe pas, essayer avec la m√©thode du token
      const savedToken = localStorage.getItem('authToken') || 
                       localStorage.getItem('token') || 
                       localStorage.getItem('jwt');
      if (savedToken) {
        try {
          const decoded = JSON.parse(atob(savedToken.split('.')[1]));
          setUserData({
            nom: decoded.nom || '',
            prenom: decoded.prenom || '',
            email: decoded.email || '',
            soldeConge: decoded.soldeConge || decoded.soldedeconge || 0 // G√®re les deux cas
          });
          setToken(savedToken);
          setIsAuthenticated(true);
        } catch (error) {
          console.error("Erreur de d√©codage du token", error);
          setShowAlert(true);
          setAlertType('error');
          setAlertMessage("Token invalide. Veuillez vous reconnecter.");
        }
      } else {
        console.warn("Aucune donn√©e utilisateur trouv√©e");
        setIsAuthenticated(false);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Veuillez vous connecter pour acc√©der √† ce service.");
      }
    }
  }, []);

  // ... (les autres fonctions handleChange, handleFileChange, removeFile restent identiques)

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // V√©rification de l'authentification
    if (!isAuthenticated || !token) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Veuillez vous connecter pour soumettre une demande de cong√©");
      return;
    }
    
    // Validation
    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage('Veuillez joindre votre justificatif m√©dical');
      return;
    }

    try {
      const formDataToSend = new FormData();
      
      // Utilisez les noms exacts du sch√©ma backend
      formDataToSend.append('dateDebut', formData.startDate); // Chang√© de DateDebut √† dateDebut
      formDataToSend.append('dateFin', formData.endDate); // Chang√© de DateFin √† dateFin
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      formDataToSend.append('nom', userData.nom);
      formDataToSend.append('prenom', userData.prenom);
      formDataToSend.append('email', userData.email);
      
      if (formData.medicalFile) {
        formDataToSend.append('justificatif', formData.medicalFile); // Chang√© de medicalFile √† justificatif
      }

      const response = await axios.post(
        '/adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succ√®s
      setConfirmationMessage(`Demande de ${formData.type} du ${formData.startDate} au ${formData.endDate} enregistr√©e !`);
      setShowConfirmation(true);
      
      // R√©initialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error);
      const errorMessage = error.response?.data?.message || 
                        error.response?.data?.error || 
                        "Erreur lors de la soumission";
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(errorMessage);
    }
  };
  
  // ... (le reste du code reste identique)

  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Cong√©
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}, d√©couvrez et soumettez vos demandes de cong√©s en quelques clics</p>
        </div>

        {showAlert && (
          <Alert 
            type={alertType} 
            message={alertMessage} 
          />
        )}

        {/* ... (le reste du JSX reste identique) */}
      </div>
    </div>
  );
};

export default CardDemandeConge;
