        if (process.env.NODE_ENV === 'development') {
          console.log("Mode d√©veloppement d√©tect√©, utilisation de donn√©es utilisateur par d√©faut");
          userData = {
            nom: "NomUtilisateur",
            prenom: "PrenomUtilisateur",
            email: "utilisateur@example.com"
          };
        } else {
          // En production, afficher l'erreur √† l'utilisateur
          setError("Erreur d'authentification. Veuillez vous reconnecter.");
          return;
        }
      }
  
