import React, { useState, useRef } from "react";

export default function ProfileForm() {
  const [formData, setFormData] = useState({
    firstName: "Marine",
    lastName: "Durand",
    email: "marine@example.com",
    phone: "06 12 34 56 78",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
    
    // Clear error when field is edited
    if (errors[id]) {
      setErrors({
        ...errors,
        [id]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // Required fields
    ["firstName", "lastName", "email"].forEach(field => {
      if (!formData[field].trim()) {
        newErrors[field] = "Ce champ est obligatoire";
      }
    });
    
    // Email format
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }
    
    // Password confirmation
    if (formData.password && formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }
    
    // Password strength
    if (formData.password && formData.password.length < 8 && formData.password.length > 0) {
      newErrors.password = "Le mot de passe doit contenir au moins 8 caractères";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccessMessage("");
    
    if (validateForm()) {
      // Simuler une requête API
      setTimeout(() => {
        setSuccessMessage("Profil mis à jour avec succès!");
        // Faire défiler vers le haut pour voir le message
        window.scrollTo({ top: 0, behavior: "smooth" });
      }, 800);
    } else {
      // Faire défiler jusqu'à la première erreur
      const firstErrorField = document.getElementById(Object.keys(errors)[0]);
      if (firstErrorField) {
        firstErrorField.scrollIntoView({ behavior: "smooth", block: "center" });
        firstErrorField.focus();
      }
    }
  };

  const togglePasswordVisibility = (field) => {
    if (field === 'password') {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  return (
    <>
      <div className="form-container">
        <div className="profile-card">
          <div className="profile-body">
            {successMessage && (
              <div className="success-alert">
                <svg className="success-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                <span>{successMessage}</span>
              </div>
            )}
            
            <form onSubmit={handleSubmit}>
              <div className="section-title">
                <span className="section-icon">👤</span>
                <h2>Informations personnelles</h2>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="firstName">
                    Prénom <span className="required-star">*</span>
                  </label>
                  <input
                    id="firstName"
                    type="text"
                    className={errors.firstName ? "input-error" : ""}
                    value={formData.firstName}
                    onChange={handleChange}
                  />
                  {errors.firstName && <div className="error-text">{errors.firstName}</div>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="lastName">
                    Nom <span className="required-star">*</span>
                  </label>
                  <input
                    id="lastName"
                    type="text"
                    className={errors.lastName ? "input-error" : ""}
                    value={formData.lastName}
                    onChange={handleChange}
                  />
                  {errors.lastName && <div className="error-text">{errors.lastName}</div>}
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="email">
                    Adresse email <span className="required-star">*</span>
                  </label>
                  <input
                    id="email"
                    type="email"
                    className={errors.email ? "input-error" : ""}
                    value={formData.email}
                    onChange={handleChange}
                  />
                  {errors.email && <div className="error-text">{errors.email}</div>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="phone">
                    Téléphone
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    className={errors.phone ? "input-error" : ""}
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Ex: 06 12 34 56 78"
                  />
                  {errors.phone && <div className="error-text">{errors.phone}</div>}
                </div>
              </div>

              <div className="section-divider"></div>
              
              <div className="section-title">
                <span className="section-icon">🔒</span>
                <h2>Sécurité</h2>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="password">
                    Mot de passe
                  </label>
                  <div className="password-field">
                    <input
                      id="password"
                      type={showPassword ? "text" : "password"}
                      className={errors.password ? "input-error" : ""}
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Laissez vide pour conserver l'actuel"
                    />
                    <button 
                      type="button" 
                      className="visibility-toggle" 
                      onClick={() => togglePasswordVisibility('password')}
                      aria-label={showPassword ? "Cacher le mot de passe" : "Montrer le mot de passe"}
                    >
                      {showPassword ? (
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                        </svg>
                      ) : (
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                        </svg>
                      )}
                    </button>
                  </div>
                  {errors.password && <div className="error-text">{errors.password}</div>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="confirmPassword">
                    Confirmer le mot de passe
                  </label>
                  <div className="password-field">
                    <input
                      id="confirmPassword"
                      type={showConfirmPassword ? "text" : "password"}
                      className={errors.confirmPassword ? "input-error" : ""}
                      value={formData.confirmPassword}
                      onChange={handleChange}
                      placeholder="Confirmer le mot de passe"
                    />
                    <button 
                      type="button" 
                      className="visibility-toggle" 
                      onClick={() => togglePasswordVisibility('confirmPassword')}
                      aria-label={showConfirmPassword ? "Cacher le mot de passe" : "Montrer le mot de passe"}
                    >
                      {showConfirmPassword ? (
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                        </svg>
                      ) : (
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                        </svg>
                      )}
                    </button>
                  </div>
                  {errors.confirmPassword && <div className="error-text">{errors.confirmPassword}</div>}
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn-cancel">
                  Annuler
                </button>
                <button type="submit" className="btn-save">
                  <svg className="btn-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  Enregistrer
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
      
      <style jsx>{`
