import React, { useState, useEffect } from "react";
import axios from "axios";

// Components
import CardStats from "components/Cards/CardStats.js";

export default function HeaderStats() {
  const [statistics, setStatistics] = useState({
    departments: "0",
    personnel: "0",
    formations: "0"
  });
  
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStatistics = async () => {
      setLoading(true);
      
      try {
        // Utilisation de Promise.all pour les requêtes parallèles
        const [personnelResponse, departmentResponse, formationResponse] = await Promise.all([
          axios.get('/allpersonnel'),
          axios.get('/alldepartment'),
          axios.get('/getformation')
        ]);
        
        setStatistics({
          personnel: personnelResponse.data.length.toString(),
          departments: departmentResponse.data.length.toString(),
          formations: formationResponse.data.length.toString()
        });
      } catch (error) {
        console.error("Erreur lors de la récupération des statistiques:", error);
        setStatistics({
          personnel: "Erreur",
          departments: "Erreur", 
          formations: "Erreur"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchStatistics();
  }, []);

  // Styles personnalisés
  const headerStyle = {
    backgroundColor: "#0f766e", // Une teinte plus foncée pour un look professionnel
    backgroundImage: "linear-gradient(135deg, #0f766e 0%, #0d9488 50%, #0891b2 100%)",
    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)",
    marginTop: "-2rem", // Positionnement plus haut sur la page
    position: "relative",
    zIndex: 10
  };

  const overlayStyle = {
    backgroundImage: "url(\"data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zM32 63c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm57-13c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-9-21c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM60 91c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM35 41c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM12 60c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2z' fill='%23ffffff' fill-opacity='0.05' fill-rule='evenodd'/%3E%3C/svg%3E\")",
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    opacity: 0.3,
    zIndex: -1
  };

  const cardContainerStyle = {
    transform: "translateY(20px)"
  };

  return (
    <>
      {/* Header */}
      <div className="relative md:pt-28 pb-32 pt-10" style={headerStyle}>
        {/* Pattern overlay */}
        <div style={overlayStyle}></div>
        
        <div className="px-4 md:px-10 mx-auto w-full">
          <div>
            {/* Titre de section */}
            <div className="mb-8">
              <h2 className="text-white text-2xl font-bold mb-2">Tableau de bord administratif</h2>
              <div className="w-16 h-1 bg-white opacity-50 rounded mb-4"></div>
              <p className="text-white opacity-80">Vue d'ensemble des ressources organisationnelles</p>
            </div>
            
            {/* Card stats */}
            <div className="flex flex-wrap" style={cardContainerStyle}>
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Effectif Total"
                  statTitle={loading ? "..." : statistics.personnel}
                  statArrow="up"
                  statPercent="3.2"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis le mois dernier"
                  statIconName="fas fa-users"
                  statIconColor="bg-blue-600"
                />
              </div>
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Départements"
                  statTitle={loading ? "..." : statistics.departments}
                  statArrow="up"
                  statPercent="2.1"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis la semaine dernière"
                  statIconName="fas fa-building"
                  statIconColor="bg-indigo-600"
                />
              </div>
              <div className="w-full lg:w-6/12 xl:w-4/12 px-4">
                <CardStats
                  statSubtitle="Formations Actives"
                  statTitle={loading ? "..." : statistics.formations}
                  statArrow="up"
                  statPercent="1.5"
                  statPercentColor="text-emerald-500"
                  statDescripiron="Depuis hier"
                  statIconName="fas fa-chalkboard-teacher"
                  statIconColor="bg-teal-600"
                />
              </div>
            </div>
          </div>
        </div>
        
        {/* Wave separator */}
        <div className="absolute bottom-0 left-0 right-0">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320" preserveAspectRatio="none" className="w-full h-16">
            <path fill="#ffffff" fillOpacity="1" d="M0,64L48,80C96,96,192,128,288,128C384,128,480,96,576,85.3C672,75,768,85,864,122.7C960,160,1056,224,1152,234.7C1248,245,1344,203,1392,181.3L1440,160L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
          </svg>
        </div>
      </div>
    </>
  );
}
