import React, { useState } from "react";

export default function ProfileForm() {
  const [formData, setFormData] = useState({
    username: "marine.durand",
    email: "marine@example.com",
    firstName: "Marine",
    lastName: "Durand",
    address: "15 Avenue des Roses, Apt 203",
    city: "Paris",
    country: "France",
    postalCode: "75008",
    phone: "06 12 34 56 78",
    password: "",
    confirmPassword: "",
    newsletter: true,
    language: "fr",
    theme: "light"
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    const { id, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [id]: type === "checkbox" ? checked : value
    });
    
    // Clear error when field is edited
    if (errors[id]) {
      setErrors({
        ...errors,
        [id]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // Required fields
    ["username", "email", "firstName", "lastName"].forEach(field => {
      if (!formData[field].trim()) {
        newErrors[field] = "Ce champ est obligatoire";
      }
    });
    
    // Email format
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }
    
    // Password confirmation
    if (formData.password && formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }
    
    // Password strength
    if (formData.password && formData.password.length < 8 && formData.password.length > 0) {
      newErrors.password = "Le mot de passe doit contenir au moins 8 caract√®res";
    }
    
    // Postal code format (French)
    if (formData.postalCode && !/^\d{5}$/.test(formData.postalCode)) {
      newErrors.postalCode = "Format de code postal invalide";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccessMessage("");
    
    if (validateForm()) {
      // Simuler une requ√™te API
      setTimeout(() => {
        setSuccessMessage("Profil mis √† jour avec succ√®s!");
        // Faire d√©filer vers le haut pour voir le message
        window.scrollTo({ top: 0, behavior: "smooth" });
      }, 800);
    } else {
      // Faire d√©filer jusqu'√† la premi√®re erreur
      const firstErrorField = document.getElementById(Object.keys(errors)[0]);
      if (firstErrorField) {
        firstErrorField.scrollIntoView({ behavior: "smooth", block: "center" });
        firstErrorField.focus();
      }
    }
  };

  const toggleTheme = () => {
    setFormData({
      ...formData,
      theme: formData.theme === "light" ? "dark" : "light"
    });
  };

  return (
    <>
      <div className={`form-container ${formData.theme === "dark" ? "dark-theme" : ""}`}>
        <div className="form-header">
          <div className="header-content">
            <h3 className="header-title">Profil Utilisateur</h3>
            <div className="header-actions">
              <span className="header-badge">Premium</span>
              <button 
                type="button" 
                className="theme-toggle" 
                onClick={toggleTheme}
                aria-label="Changer le th√®me"
              >
                {formData.theme === "light" ? "üåô" : "‚òÄÔ∏è"}
              </button>
            </div>
          </div>
        </div>
        
        <div className="form-body">
          {successMessage && (
            <div className="message success-message">
              <span className="message-icon">‚úì</span>
              {successMessage}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="form-section">
              <h4 className="section-heading">Informations Personnelles</h4> 
              
              <div className="form-grid">
                <div className="form-column">
                  <div className={`input-group ${errors.username ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="username">
                      Nom d'utilisateur <span className="required">*</span>
                    </label>
                    <input
                      id="username"
                      type="text"
                      className="input-field"
                      value={formData.username}
                      onChange={handleChange}
                    />
                    {errors.username && <div className="error-message">{errors.username}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.email ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="email">
                      Adresse email <span className="required">*</span>
                    </label>
                    <input
                      id="email"
                      type="email"
                      className="input-field"
                      value={formData.email}
                      onChange={handleChange}
                    />
                    {errors.email && <div className="error-message">{errors.email}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.firstName ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="firstName">
                      Pr√©nom <span className="required">*</span>
                    </label>
                    <input
                      id="firstName"
                      type="text"
                      className="input-field"
                      value={formData.firstName}
                      onChange={handleChange}
                    />
                    {errors.firstName && <div className="error-message">{errors.firstName}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.lastName ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="lastName">
                      Nom <span className="required">*</span>
                    </label>
                    <input
                      id="lastName"
                      type="text"
                      className="input-field"
                      value={formData.lastName}
                      onChange={handleChange}
                    />
                    {errors.lastName && <div className="error-message">{errors.lastName}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.phone ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="phone">
                      T√©l√©phone
                    </label>
                    <input
                      id="phone"
                      type="tel"
                      className="input-field"
                      value={formData.phone}
                      onChange={handleChange}
                      placeholder="Ex: 06 12 34 56 78"
                    />
                    {errors.phone && <div className="error-message">{errors.phone}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className="input-group">
                    <label className="input-label" htmlFor="language">
                      Langue pr√©f√©r√©e
                    </label>
                    <select 
                      id="language" 
                      className="input-field select-field"
                      value={formData.language}
                      onChange={handleChange}
                    >
                      <option value="fr">Fran√ßais</option>
                      <option value="en">Anglais</option>
                      <option value="es">Espagnol</option>
                      <option value="de">Allemand</option>
                      <option value="it">Italien</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>

            <div className="form-divider"></div>

            <div className="form-section">
              <h4 className="section-heading">Coordonn√©es</h4>
              
              <div className="form-grid">
                <div className="form-column full-width">
                  <div className={`input-group ${errors.address ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="address">
                      Adresse
                    </label>
                    <input
                      id="address"
                      type="text"
                      className="input-field"
                      value={formData.address}
                      onChange={handleChange}
                    />
                    {errors.address && <div className="error-message">{errors.address}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.city ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="city">
                      Ville
                    </label>
                    <input
                      id="city"
                      type="text"
                      className="input-field"
                      value={formData.city}
                      onChange={handleChange}
                    />
                    {errors.city && <div className="error-message">{errors.city}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.country ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="country">
                      Pays
                    </label>
                    <input
                      id="country"
                      type="text"
                      className="input-field"
                      value={formData.country}
                      onChange={handleChange}
                    />
                    {errors.country && <div className="error-message">{errors.country}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.postalCode ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="postalCode">
                      Code Postal
                    </label>
                    <input
                      id="postalCode"
                      type="text"
                      className="input-field"
                      value={formData.postalCode}
                      onChange={handleChange}
                    />
                    {errors.postalCode && <div className="error-message">{errors.postalCode}</div>}
                  </div>
                </div>
              </div>
            </div>

            <div className="form-divider"></div>
            
            <div className="form-section">
              <h4 className="section-heading">Param√®tres de s√©curit√©</h4>
              
              <div className="form-grid">
                <div className="form-column">
                  <div className={`input-group ${errors.password ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="password">
                      Nouveau mot de passe
                    </label>
                    <input
                      id="password"
                      type="password"
                      className="input-field"
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Laissez vide pour conserver l'actuel"
                    />
                    {errors.password && <div className="error-message">{errors.password}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.confirmPassword ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="confirmPassword">
                      Confirmer le mot de passe
                    </label>
                    <input
                      id="confirmPassword"
                      type="password"
                      className="input-field"
                      value={formData.confirmPassword}
