import React from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";

export default function Index() {
  return (
    <>
      <IndexNavbar fixed />
      <div className="home-container">
        {/* Hero Section with Full-width Image */}
        <section className="hero-section">
          <div className="hero-background">
            <div className="hero-overlay"></div>
            <div className="content-wrapper">
              <div className="hero-content">
                <h1 className="hero-title text-left">
                  <span className="primary-text">UBCI</span> HR MANAGER
                </h1>
                <p className="hero-description">
                  UBCI RH est une plateforme dédiée à la gestion des ressources humaines de la banque UBCI, 
                  facilitant le suivi du personnel, la gestion des congés et l'automatisation des tâches administratives.
                </p>
                <div className="action-buttons">
                  <Link to="/postule" className="btn-primary">
                    Postuler
                  </Link>
                  <Link to="/signin" className="btn-secondary">
                    Mon Espace
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Features Section */}
        <section className="features-section">
          <div className="section-container">
            <h2 className="section-title">Nos services RH</h2>
            <div className="features-grid">
              {/* Feature 1 */}
              <div className="feature-card">
                <div className="feature-icon personnel-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                    <circle cx="9" cy="7" r="4"></circle>
                    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
                  </svg>
                </div>
                <h3 className="feature-title">Gestion du personnel</h3>
                <p className="feature-description">
                  Suivez les informations personnelles, les performances et l'évolution de carrière de chaque employé.
                </p>
              </div>
              
              {/* Feature 2 */}
              <div className="feature-card">
                <div className="feature-icon calendar-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect>
                    <line x1="16" y1="2" x2="16" y2="6"></line>
                    <line x1="8" y1="2" x2="8" y2="6"></line>
                    <line x1="3" y1="10" x2="21" y2="10"></line>
                  </svg>
                </div>
                <h3 className="feature-title">Gestion des congés</h3>
                <p className="feature-description">
                  Gérez les demandes de congés, les validations et le suivi des soldes de façon efficace.
                </p>
              </div>
              
              {/* Feature 3 */}
              <div className="feature-card">
                <div className="feature-icon document-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                    <polyline points="14 2 14 8 20 8"></polyline>
                    <line x1="16" y1="13" x2="8" y2="13"></line>
                    <line x1="16" y1="17" x2="8" y2="17"></line>
                    <polyline points="10 9 9 9 8 9"></polyline>
                  </svg>
                </div>
                <h3 className="feature-title">Documentation RH</h3>
                <p className="feature-description">
                  Accédez facilement à tous les documents administratifs et formulaires RH nécessaires.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* CTA Section */}
     
      </div>

      {/* Feuille de style personnalisée avec couleurs plus claires */}
      <style jsx>{`
        /* Style général - Couleurs UBCI modifiées pour plus de légèreté */
        /* Nouvelle palette:
           - Turquoise principal: #4ECADB (plus clair)
           - Bleu foncé: #175873 (remplace le vert foncé)
           - Turquoise clair: #A7E8F0
           - Bleu très clair: #E9F7FB
           - Blanc: #FFFFFF
        */
        
        .home-container {
          font-family: 'Poppins', sans-serif;
          color: #333;
          overflow-x: hidden;
        }

        /* Hero Section avec image plein écran */
        .hero-section {
          position: relative;
          width: 100%;
          height: 100vh;
          overflow: hidden;
          margin-top: -80px; /* Ajuste pour le navbar */
        }

        .hero-background {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-image: url(${require("assets/img/ubci.jpg").default});
          background-size: cover;
          background-position: center;
          display: flex;
          align-items: center;
        }

        .hero-overlay {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(to right, rgba(23, 88, 115, 0.8) 0%, rgba(23, 88, 115, 0.4) 100%);
        }

        .content-wrapper {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          display: flex;
          align-items: center;
          position: relative;
          z-index: 10;
          height: 100%;
        }

        .hero-content {
          width: 100%;
          max-width: 600px;
          margin-top: 6rem;
          color: white;
        }

        .hero-title {
          font-size: 4.5rem;
          font-weight: 700;
          color: white;
          line-height: 1.1;
          margin-bottom: 1.5rem;
          text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
          text-align: left; /* Alignement à gauche */
        }

        .text-left {
          text-align: left !important;
        }

        .primary-text {
          color: #A7E8F0;
        }

        .hero-description {
          font-size: 1.3rem;
          line-height: 1.6;
          color: white;
          margin-bottom: 2.5rem;
          text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
        }

        .action-buttons {
          display: flex;
          gap: 1.5rem;
          flex-wrap: wrap;
        }

        .btn-primary {
          display: inline-block;
          padding: 1.2rem 2.5rem;
          background: #4ECADB;
          color: white;
          font-weight: 600;
          border-radius: 30px;
          text-decoration: none;
          box-shadow: 0 5px 15px rgba(78, 202, 219, 0.4);
          transition: all 0.3s ease;
          border: none;
        }

        .btn-primary:hover {
          transform: translateY(-3px);
          box-shadow: 0 8px 20px rgba(78, 202, 219, 0.5);
          background: #3cbed0;
        }

        .btn-secondary {
          display: inline-block;
          padding: 1.2rem 2.5rem;
          background: transparent;
          color: white;
          font-weight: 600;
          border-radius: 30px;
          text-decoration: none;
          border: 2px solid white;
          transition: all 0.3s ease;
        }

        .btn-secondary:hover {
          transform: translateY(-3px);
          background: rgba(255, 255, 255, 0.2);
          box-shadow: 0 5px 15px rgba(255, 255, 255, 0.2);
        }

        /* Features Section */
        .features-section {
          padding: 6rem 0;
          background-color: #E9F7FB;
        }

        .section-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
        }

        .section-title {
          font-size: 2.5rem;
          font-weight: 700;
          text-align: center;
          color: #175873;
          margin-bottom: 3rem;
          position: relative;
        }

        .section-title:after {
          content: '';
          position: absolute;
          bottom: -10px;
          left: 50%;
          transform: translateX(-50%);
          width: 80px;
          height: 4px;
          background: linear-gradient(90deg, #4ECADB 0%, #175873 100%);
          border-radius: 2px;
        }

        .features-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
          gap: 2rem;
        }

        .feature-card {
          background-color: #fff;
          border-radius: 16px;
          padding: 2.5rem 2rem;
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
          transition: all 0.3s ease;
          text-align: center;
          position: relative;
          overflow: hidden;
          z-index: 1;
        }

        .feature-card:hover {
          transform: translateY(-10px);
          box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1);
        }

        .feature-card:before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 5px;
          background: linear-gradient(90deg, #4ECADB 0%, #175873 100%);
          z-index: -1;
        }

        .feature-icon {
          width: 80px;
          height: 80px;
          margin: 0 auto 1.5rem;
          display: flex;
          align-items: center;
          justify-content: center;
          border-radius: 50%;
          background-color: #E9F7FB;
          color: #4ECADB;
          font-size: 2rem;
          transition: all 0.3s ease;
        }

        .feature-card:hover .feature-icon {
          background-color: #4ECADB;
          color: white;
          transform: rotateY(180deg);
        }

        .feature-icon svg {
          width: 40px;
          height: 40px;
          transition: all 0.3s ease;
        }

        .feature-title {
          font-size: 1.5rem;
          font-weight: 600;
          color: #175873;
          margin-bottom: 1rem;
        }

        .feature-description {
          color: #546e7a;
          line-height: 1.6;
        }

        /* CTA Section */
        .cta-section {
          position: relative;
          background: linear-gradient(90deg, #4ECADB 0%, #175873 100%);
          padding: 5rem 0;
          color: white;
          overflow: hidden;
        }

        .cta-container {
          max-width: 1000px;
          margin: 0 auto;
          padding: 0 2rem;
          text-align: center;
          position: relative;
          z-index: 2;
        }

        .cta-title {
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 1.5rem;
        }

        .cta-description {
          font-size: 1.2rem;
          margin-bottom: 2.5rem;
          max-width: 700px;
          margin-left: auto;
          margin-right: auto;
        }

        .cta-buttons {
          display: flex;
          gap: 1.5rem;
          justify-content: center;
          flex-wrap: wrap;
        }

        .cta-btn-primary {
          display: inline-block;
          padding: 1rem 2rem;
          background: white;
          color: #4ECADB;
          font-weight: 600;
          border-radius: 30px;
          text-decoration: none;
          box-shadow: 0 5px 15px rgba(255, 255, 255, 0.2);
          transition: all 0.3s ease;
        }

        .cta-btn-primary:hover {
          transform: translateY(-3px);
          box-shadow: 0 8px 20px rgba(255, 255, 255, 0.3);
          background: #f5f5f5;
        }

        .cta-btn-outline {
          display: inline-block;
          padding: 1rem 2rem;
          background: transparent;
          color: white;
          font-weight: 600;
          border: 2px solid white;
          border-radius: 30px;
          text-decoration: none;
          transition: all 0.3s ease;
        }

        .cta-btn-outline:hover {
          transform: translateY(-3px);
          background: rgba(255, 255, 255, 0.1);
        }

        .cta-bg-shape {
          position: absolute;
          top: 0;
          right: 0;
          width: 60%;
          height: 100%;
          background: url("data:image/svg+xml,%3Csvg width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cdefs%3E%3Cpattern id='pattern' width='40' height='40' viewBox='0 0 40 40' patternUnits='userSpaceOnUse' patternTransform='rotate(45)'%3E%3Crect width='100%25' height='100%25' fill='rgba(255,255,255,0.05)'/%3E%3Ccircle cx='20' cy='20' r='2' fill='rgba(255,255,255,0.1)'/%3E%3C/pattern%3E%3C/defs%3E%3Crect width='100%25' height='100%25' fill='url(%23pattern)'/%3E%3C/svg%3E");
          opacity: 0.8;
          z-index: 1;
        }

        /* Stats Section */
        .stats-section {
          padding: 5rem 0;
          background-color: #fff;
        }

        .stats-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          display: flex;
          justify-content: space-between;
          flex-wrap: wrap;
          gap: 2rem;
        }

        .stats-item {
          flex: 1;
          min-width: 200px;
          text-align: center;
          padding: 2rem;
          background-color: #E9F7FB;
          border-radius: 16px;
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
          transition: all 0.3s ease;
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
          border-bottom: 4px solid #4ECADB;
        }

        .stats-item:hover {
          transform: translateY(-5px);
          box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1);
        }

        .stats-number {
          font-size: 2.5rem;
          font-weight: 700;
          color: #4ECADB;
        }

        .stats-label {
          font-size: 1rem;
          color: #175873;
        }

        /* Footer */
        .footer {
          background-color: #175873;
          color: white;
          padding: 5rem 0 2rem;
        }

        .footer-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 3rem;
        }

        .footer-title {
          font-size: 1.8rem;
          font-weight: 700;
          margin-bottom: 1.5rem;
          color: #4ECADB;
        }

        .footer-description {
          color: rgba(255, 255, 255, 0.7);
          line-height: 1.6;
        }

        .footer-subtitle {
          font-size: 1.2rem;
          font-weight: 600;
          margin-bottom: 1rem;
          color: #4ECADB;
        }

        .footer-menu {
          list-style: none;
          padding: 0;
          margin: 0;
          margin-bottom: 1.5rem;
        }

        .footer-menu li {
          margin-bottom: 0.8rem;
        }

        .footer-link {
          color: rgba(255, 255, 255, 0.7);
          text-decoration: none;
          transition: all 0.3s ease;
        }

        .footer-link:hover {
          color: #4ECADB;
          text-decoration: none;
        }

        /* Nouveaux styles pour les réseaux sociaux */
        .social-icons {
          display: flex;
          gap: 1rem;
          margin-bottom: 1.5rem;
        }

        .social-icon-link {
          text-decoration: none;
        }

        .social-icon {
          width: 42px;
          height: 42px;
          border-radius: 50%;
          background-color: rgba(255, 255, 255, 0.1);
          display: flex;
          align-items: center;
          justify-content: center;
          transition: all 0.3s ease;
        }

        .social-icon svg {
          width: 20px;
          height: 20px;
          color: white;
          transition: all 0.3s ease;
        }

        .facebook-icon:hover {
          background-color: #1877F2;
          transform: translateY(-5px);
          box-shadow: 0 5px 15px rgba(24, 119, 242, 0.4);
        }

        .instagram-icon:hover {
          background: linear-gradient(45deg, #f09433 0%, #e6683c 25%, #dc2743 50%, #cc2366 75%, #bc1888 100%);
          transform: translateY(-5px);
          box-shadow: 0 5px 15px rgba(220, 39, 67, 0.4);
        }

        .linkedin-icon:hover {
          background-color: #0A66C2;
          transform: translateY(-5px);
          box-shadow: 0 5px 15px rgba(10, 102, 194, 0.4);
        }

        .contact-info {
          margin-bottom: 0.8rem;
          color: rgba(255, 255, 255, 0.7);
        }

        .contact-label {
          font-weight: 600;
          color: white;
          margin-right: 0.5rem;
        }

        .contact-link {
          color: rgba(255, 255, 255, 0.7);
          text-decoration: none;
          transition: all 0.3s ease;
        }

        .contact-link:hover {
          color: #4ECADB;
          text-decoration: none;
        }

        .footer-bottom {
          max-width: 1200px;
          margin: 0 auto;
          padding: 2rem 2rem 0;
          border-top: 1px solid rgba(78, 202, 219, 0.2);
          margin-top: 3rem;
          text-align: center;
        }

        .copyright {
          color: rgba(255, 255, 255, 0.5);
          font-size: 0.9rem;
        }

        /* Responsive Styles */
        @media (max-width: 768px) {
          .hero-content {
            margin-top: 8rem;
            padding: 0 1rem;
          }

          .hero-title {
            font-size: 3.5rem;
          }

          .cta-title {
            font-size: 2rem;
          }

          .cta-description {
            font-size: 1rem;
          }

          .stats-container {
            justify-content: center;
          }

          .stats-item {
            min-width: 250px;
          }
        }

        @media (max-width: 480px) {
          .hero-title {
            font-size: 2.8rem;
          }

          .hero-description {
            font-size: 1.1rem;
          }

          .action-buttons {
            flex-direction: column;
            width: 100%;
          }

          .btn-primary, .btn-secondary {
            width: 100%;
            text-align: center;
          }

          .cta-buttons {
            flex-direction: column;
            width: 100%;
          }

          .cta-btn-primary, .cta-btn-outline {
            width: 100%;
            text-align: center;
          }

          .stats-item {
            min-width: 100%;
          }
        }
      `}</style>
    </>
  );
}
