import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X, Calendar, Clock, Mail } from "lucide-react";
import axios from "axios";

// CSS encapsulé avec préfixe "gc-" (Gestion Congés)
const encapsulatedStyles = `
  .gc-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    position: relative;
    width: 100%;
  }
  
  .gc-header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .gc-title {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
    color: #1f2937;
  }
  
  .gc-search-container {
    position: relative;
    margin-bottom: 1rem;
  }
  
  .gc-search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .gc-search-input {
    width: 100%;
    padding: 0.5rem 0.75rem 0.5rem 2.5rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
    font-size: 0.875rem;
  }
  
  .gc-search-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gc-table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  .gc-table {
    width: 100%;
    border-collapse: collapse;
  }
  
  .gc-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gc-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gc-tr {
    transition: background-color 0.2s ease;
  }
  
  .gc-tr:hover {
    background-color: #f9fafb;
  }
  
  .gc-nom {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gc-prenom {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gc-motif {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
    max-width: 250px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
  
  .gc-email {
    font-size: 0.75rem;
    color: #6b7280;
    display: flex;
    align-items: center;
    gap: 0.25rem;
  }
  
  .gc-date {
    font-size: 0.75rem;
    color: #6b7280;
    margin-top: 0.25rem;
  }
  
  .gc-status-badge {
    display: flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
    width: fit-content;
  }
  
  .gc-status-accepte {
    background-color: #dcfce7;
    color: #166534;
  }
  
  .gc-status-en-cours {
    background-color: #fef3c7;
    color: #92400e;
  }
  
  .gc-status-refuse {
    background-color: #fee2e2;
    color: #b91c1c;
  }
  
  .gc-footer {
    padding: 1rem;
    background-color: #f9fafb;
    border-top: 1px solid #e5e7eb;
    font-size: 0.875rem;
    color: #6b7280;
  }
  
  /* Styles pour les actions */
  .gc-actions {
    display: flex;
    gap: 0.5rem;
  }
  
  .gc-action-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 1.75rem;
    height: 1.75rem;
    border-radius: 0.375rem;
    color: #6b7280;
    background-color: white;
    border: 1px solid #e5e7eb;
    cursor: pointer;
    transition: all 0.2s ease;
  }
  
  .gc-action-button:hover {
    background-color: #f9fafb;
    color: #111827;
    border-color: #d1d5db;
  }
  
  .gc-action-button-accepter:hover {
    color: #047857;
    background-color: #ecfdf5;
    border-color: #a7f3d0;
  }
  
  .gc-action-button-accepter {
    color: #047857;
  }
  
  .gc-action-button-refuser {
    color: #b91c1c;
  }
  
  .gc-action-button-refuser:hover {
    color: #dc2626;
    background-color: #fef2f2;
    border-color: #fecaca;
  }
  
  .gc-loading {
    text-align: center;
    padding: 1rem;
    color: #6b7280;
  }
  
  .gc-date-container {
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }
  
  .gc-duree {
    font-size: 0.75rem;
    color: #6b7280;
    display: flex;
    align-items: center;
    gap: 0.25rem;
  }
`;

export default function GestionConges() {
  // Données statiques pour les demandes de congé
  const staticConges = [
    {
      _id: "1",
      nom: "Dupont",
      prenom: "Jean",
      email: "jean.dupont@entreprise.com",
      motif: "Vacances en famille du 15 au 18 juin",
      statut: "en_cours",
      dateDebut: "2023-06-15T00:00:00Z",
      dateFin: "2023-06-18T00:00:00Z",
      duree: "4 jours"
    },
    {
      _id: "2",
      nom: "Martin",
      prenom: "Sophie",
      email: "sophie.martin@entreprise.com",
      motif: "Maladie avec certificat médical",
      statut: "accepte",
      dateDebut: "2023-05-10T00:00:00Z",
      dateFin: "2023-05-12T00:00:00Z",
      duree: "3 jours"
    },
    {
      _id: "3",
      nom: "Bernard",
      prenom: "Pierre",
      email: "pierre.bernard@entreprise.com",
      motif: "Départ anticipé pour raison personnelle",
      statut: "refuse",
      dateDebut: "2023-07-05T00:00:00Z",
      dateFin: "2023-07-07T00:00:00Z",
      duree: "3 jours"
    },
    {
      _id: "4",
      nom: "Petit",
      prenom: "Marie",
      email: "marie.petit@entreprise.com",
      motif: "Congé parental jusqu'au 20 août",
      statut: "en_cours",
      dateDebut: "2023-06-20T00:00:00Z",
      dateFin: "2023-08-20T00:00:00Z",
      duree: "2 mois"
    },
    {
      _id: "5",
      nom: "Leroy",
      prenom: "Thomas",
      email: "thomas.leroy@entreprise.com",
      motif: "Mariage - absence exceptionnelle",
      statut: "accepte",
      dateDebut: "2023-05-22T00:00:00Z",
      dateFin: "2023-05-24T00:00:00Z",
      duree: "3 jours"
    }
  ];

  const [conges, setConges] = useState(staticConges);
  const [congesFiltres, setCongesFiltres] = useState(staticConges);
  const [recherche, setRecherche] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Formater la date pour l'affichage
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR');
  };

  // Filtrage des congés basé sur la recherche
  const filtrerConges = (terme) => {
    setRecherche(terme);
    if (!terme.trim()) {
      setCongesFiltres(conges);
      return;
    }
    
    const termeLower = terme.toLowerCase();
    const resultats = conges.filter(conge => {
      return (
        conge.nom.toLowerCase().includes(termeLower) ||
        conge.prenom.toLowerCase().includes(termeLower) ||
        conge.motif.toLowerCase().includes(termeLower) ||
        conge.email.toLowerCase().includes(termeLower)
      );
    });
    
    setCongesFiltres(resultats);
  };

  // Mapping des statuts vers les statuts d'affichage
  const mapStatus = (status) => {
    switch (status) {
      case 'accepte':
        return 'Accepté';
      case 'en_cours':
        return 'En cours';
      case 'refuse':
        return 'Refusé';
      default:
        return status;
    }
  };

  // Actions des boutons 
  const handleAccepter = (id) => {
    try {
      // Mise à jour de l'état local
      const congesUpdated = conges.map(conge => {
        if (conge._id === id) {
          return {
            ...conge, 
            statut: 'accepte'
          };
        }
        return conge;
      });
      
      setConges(congesUpdated);
      setCongesFiltres(
        congesUpdated.filter(conge => {
          if (recherche.trim() === "") return true;
          
          const termeLower = recherche.toLowerCase();
          return (
            conge.nom.toLowerCase().includes(termeLower) ||
            conge.prenom.toLowerCase().includes(termeLower) ||
            conge.motif.toLowerCase().includes(termeLower) ||
            conge.email.toLowerCase().includes(termeLower)
          );
        })
      );
      
      alert(`La demande de congé a été acceptée.`);
    } catch (err) {
      console.error("Erreur lors de la mise à jour:", err);
      alert("Erreur lors de la mise à jour de la demande.");
    }
  };

  const handleRefuser = (id) => {
    try {
      // Mise à jour de l'état local
      const congesUpdated = conges.map(conge => {
        if (conge._id === id) {
          return {
            ...conge, 
            statut: 'refuse'
          };
        }
        return conge;
      });
      
      setConges(congesUpdated);
      setCongesFiltres(
        congesUpdated.filter(conge => {
          if (recherche.trim() === "") return true;
          
          const termeLower = recherche.toLowerCase();
          return (
            conge.nom.toLowerCase().includes(termeLower) ||
            conge.prenom.toLowerCase().includes(termeLower) ||
            conge.motif.toLowerCase().includes(termeLower) ||
            conge.email.toLowerCase().includes(termeLower)
          );
        })
      );
      
      alert(`La demande de congé a été refusée.`);
    } catch (err) {
      console.error("Erreur lors du refus:", err);
      alert("Erreur lors du refus de la demande.");
    }
  };

  // Rendu du statut avec l'icône appropriée
  const renderStatus = (status) => {
    const displayStatus = mapStatus(status);
    
    switch (status) {
      case "accepte":
        return (
          <div className="gc-status-badge gc-status-accepte">
            <Check size={14} />
            <span>{displayStatus}</span>
          </div>
        );
      case "en_cours":
        return (
          <div className="gc-status-badge gc-status-en-cours">
            <AlertCircle size={14} />
            <span>{displayStatus}</span>
          </div>
        );
      case "refuse":
        return (
          <div className="gc-status-badge gc-status-refuse">
            <X size={14} />
            <span>{displayStatus}</span>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <>
      {/* Injection du CSS encapsulé */}
      <style>{encapsulatedStyles}</style>
      
      <div className="gc-container">
        {/* En-tête et recherche */}
        <div className="gc-header-section">
          <h1 className="gc-title">
            Gestion des demandes de congé
          </h1>
          
          {/* Barre de recherche */}
          <div className="gc-search-container">
            <div className="gc-search-icon">
              <Search size={18} />
            </div>
            <input
              type="text"
              className="gc-search-input"
              placeholder="Rechercher par nom, prénom, email ou motif..."
              value={recherche}
              onChange={(e) => filtrerConges(e.target.value)}
            />
          </div>
        </div>

        {/* Affichage des erreurs */}
        {error && (
          <div className="gc-error">
            {error}
          </div>
        )}

        {/* Tableau des congés */}
        <div className="gc-table-container">
          <table className="gc-table">
            <thead>
              <tr>
                <th className="gc-th">Nom</th>
                <th className="gc-th">Prénom</th>
                <th className="gc-th">Email</th>
                <th className="gc-th">Motif (inclut période)</th>
                <th className="gc-th">Durée</th>
                <th className="gc-th">Statut</th>
                <th className="gc-th">Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan="7" className="gc-td gc-loading">
                    Chargement en cours...
                  </td>
                </tr>
              ) : congesFiltres.length === 0 ? (
                <tr>
                  <td colSpan="7" className="gc-td" style={{ textAlign: 'center' }}>
                    {recherche ? "Aucune demande ne correspond à votre recherche" : "Aucune demande de congé trouvée"}
                  </td>
                </tr>
              ) : (
                congesFiltres.map((conge) => (
                  <tr key={conge._id} className="gc-tr">
                    <td className="gc-td">
                      <div className="gc-nom">{conge.nom}</div>
                    </td>
                    <td className="gc-td">
                      <div className="gc-prenom">{conge.prenom}</div>
                    </td>
                    <td className="gc-td">
                      <div className="gc-email">
                        <Mail size={14} />
                        {conge.email}
                      </div>
                    </td>
                    <td className="gc-td">
                      <div className="gc-motif" title={conge.motif}>
                        {conge.motif}
                      </div>
                    </td>
                    <td className="gc-td">
                      <div className="gc-duree">
                        <Clock size={14} />
                        <span>{conge.duree}</span>
                      </div>
                    </td>
                    <td className="gc-td">
                      {renderStatus(conge.statut)}
                    </td>
                    <td className="gc-td">
                      <div className="gc-actions">
                        {conge.statut === "en_cours" && (
                          <>
                            <button 
                              className="gc-action-button gc-action-button-accepter" 
                              onClick={() => handleAccepter(conge._id)}
                              title="Accepter"
                            >
                              <Check size={16} />
                            </button>
                            <button 
                              className="gc-action-button gc-action-button-refuser" 
                              onClick={() => handleRefuser(conge._id)}
                              title="Refuser"
                            >
                              <X size={16} />
                            </button>
                          </>
                        )}
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
        
        {/* Information sur les résultats */}
        <div className="gc-footer">
          Affichage de {congesFiltres.length} sur {conges.length} demandes
        </div>
      </div>
    </>
  );
}
