import React, { useState } from 'react';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }

      // Simuler l'envoi de la demande - À remplacer par votre appel API réel
      // Pour l'exemple, nous utilisons un timeout pour simuler un appel réseau
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Exemple de comment vous pourriez faire votre appel API réel:
      // const response = await axios.post(
      //   '/api/demandes/avance',
      //   formData,
      //   {
      //     headers: {
      //       'Content-Type': 'application/json',
      //       'Authorization': `Bearer ${localStorage.getItem('token')}`
      //     }
      //   }
      // );

      // Succès simulé
      setSuccess(true);
      // Réinitialiser le formulaire après succès
      setFormData({
        montant: '',
        motif: '',
        type: 'standard'
      });
    } catch (err) {
      setError(err.message || 'Une erreur est survenue');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-w-lg mx-auto p-5 bg-gray-50 font-sans">
      <div className="text-center mb-5 py-5">
        <h1 className="text-2xl font-bold text-blue-800 mb-2 relative inline-block pb-4">
          Demande d'avance sur salaire
          <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
        </h1>
        <p className="text-base text-gray-500 mt-2 tracking-wide">Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {error && <div className="text-red-600 mb-4 text-sm">{error}</div>}
      {success && <div className="text-green-600 mb-4 text-sm">Votre demande a été soumise avec succès!</div>}

      <form className="bg-white rounded-lg p-6 shadow-sm" onSubmit={handleSubmit}>
        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="montant">
            Montant demandé (TND)
          </label>
          <input
            type="number"
            id="montant"
            name="montant"
            className="w-full p-3 border border-gray-200 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
          />
          <p className="text-xs text-gray-500 mt-1 italic">Le montant maximum est de 30% de votre salaire mensuel</p>
        </div>

        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="type">
            Type de demande
          </label>
          <select
            id="type"
            name="type"
            className="w-full p-3 border border-gray-200 rounded text-sm bg-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div className="mb-5">
          <label className="block mb-2 text-sm font-medium text-gray-700" htmlFor="motif">
            Motif de la demande
          </label>
          <textarea
            id="motif"
            name="motif"
            className="w-full p-3 border border-gray-200 rounded text-sm min-h-24 resize-y focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          className={`w-full py-3 px-4 bg-blue-600 text-white rounded font-medium text-base transition-colors ${loading ? 'opacity-75 cursor-not-allowed' : 'hover:bg-blue-700'}`}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
