import React, { useState, useEffect } from "react";
import Navbar from "components/Navbars/IndexNavbar";
import axios from "axios";
import mongoose from 'mongoose'; // Import mongoose pour la conversion ObjectId

const Postule = () => {
  // States initiaux
  const [jobListings, setJobListings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [fileName, setFileName] = useState("Aucun fichier sélectionné");
  const [fileError, setFileError] = useState("");

  // État du formulaire amélioré
  const [formData, setFormData] = useState({
    prenom: "",
    nom: "",
    adresse: "",
    email: "",
    tel: "",
    cv: null,
    posteId: "",
    posteTitle: "",
    rawPosteId: null // Nouveau champ pour stocker l'ObjectId
  });

  // Fonction pour ouvrir la modal avec conversion de l'ID
  const openApplicationModal = (job) => {
    setSelectedJob(job);
    setIsModalOpen(true);
    setFileError("");
    
    // Conversion de l'ID en ObjectId
    let objectId;
    try {
      objectId = new mongoose.Types.ObjectId(job.id);
    } catch (e) {
      console.error("Erreur de conversion ObjectId:", e);
      objectId = job.id; // Fallback au string si échec
    }

    setFormData({
      prenom: "",
      nom: "",
      adresse: "",
      email: "",
      tel: "",
      cv: null,
      posteId: job.id,
      posteTitle: job.title,
      rawPosteId: objectId
    });
    
    setSubmitSuccess(false);
    setSubmitError("");
  };

  // Nouvelle fonction handleSubmit corrigée
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.cv) {
      setFileError("Erreur: Veuillez télécharger votre CV (PDF uniquement).");
      return;
    }

    try {
      setIsSubmitting(true);
      setSubmitError("");
      
      const formDataToSend = new FormData();
      formDataToSend.append("nom", formData.nom);
      formDataToSend.append("prenom", formData.prenom);
      formDataToSend.append("adresse", formData.adresse);
      formDataToSend.append("email", formData.email);
      formDataToSend.append("tel", formData.tel);
      
      // Utilisation de rawPosteId si disponible
      formDataToSend.append("posteId", formData.rawPosteId || formData.posteId);
      formDataToSend.append("posteTitle", formData.posteTitle);
      formDataToSend.append("cv", formData.cv);

      // Debug: Affiche les données envoyées
      for (let [key, value] of formDataToSend.entries()) {
        console.log(key, value);
      }

      const response = await axios.post('/addcondidature', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setSubmitSuccess(true);
      setTimeout(() => closeModal(), 2000);
      
    } catch (err) {
      console.error("Erreur complète:", err);
      
      if (err.response) {
        // Erreur 400 - Validation
        if (err.response.status === 400) {
          const backendMessage = err.response.data.message || "";
          
          if (backendMessage.includes("déjà existante")) {
            setSubmitError("Vous avez déjà postulé à cette offre spécifique.");
          } else {
            setSubmitError("Erreur de validation: " + backendMessage);
          }
        }
        // Erreur 500
        else if (err.response.status === 500) {
          setSubmitError("Erreur serveur. Contactez l'administrateur.");
        }
      } else {
        setSubmitError("Erreur réseau. Vérifiez votre connexion.");
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="careers-page">
      <Navbar />
      
      {/* Banner and search bar */}
      <div className="banner">
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <div className="search-bar">
              <div className="search-input-container">
                <span className="search-icon">🔍</span>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Saisissez un titre de poste ou un mot-clé"
                  value={searchKeyword}
                  onChange={(e) => setSearchKeyword(e.target.value)}
                />
              </div>

              <button type="submit" className="search-button">
                Rechercher
              </button>
            </div>
          </form>
        </div>
      </div>

      <div className="content-container">
        <div className="filters-container">
          {/* Filters section */}
          <div className="filters-header">
            <h2>Filtres</h2>
            <button className="reset-button" onClick={resetFilters}>
              Réinitialiser
            </button>
          </div>

          <div className="filter-section">
            <div className="filter-header" onClick={toggleDateSection}>
              <h3>Date de publication</h3>
              <span className={`arrow ${isDateExpanded ? "expanded" : ""}`}>
                ▼
              </span>
            </div>

            {isDateExpanded && (
              <div className="filter-options">
                <label className="filter-radio">
                  <input
                    type="radio"
                    name="dateFilter"
                    checked={dateFilter === "Sous 30 jours"}
                    onChange={() => handleDateFilterChange("Sous 30 jours")}
                  />
                  Sous 30 jours ({jobsWithin30DaysCount})
                </label>
                <label className="filter-radio">
                  <input
                    type="radio"
                    name="dateFilter"
                    checked={dateFilter === "N'importe quand"}
                    onChange={() => handleDateFilterChange("N'importe quand")}
                  />
                  N'importe quand ({jobListings.length})
                </label>
              </div>
            )}
          </div>
        </div>

        <div className="job-listings-container">
          <h1>Postes à pourvoir actuellement</h1>

          {isLoading ? (
            <div className="loading-state">
              <p>Chargement des offres d'emploi...</p>
            </div>
          ) : error ? (
            <div className="error-state">
              <p>{error}</p>
              <button 
                className="retry-button"
                onClick={() => window.location.reload()}
              >
                Réessayer
              </button>
            </div>
          ) : (
            <>
              {hasSearched && (
                <div className="search-results-info">
                  <p>
                    {filteredJobs.length} offre(s) d'emploi trouvée(s){" "}
                    {searchKeyword && `pour "${searchKeyword}"`}
                    {dateFilter === "Sous 30 jours" && " des 30 derniers jours"}
                  </p>
                  {filteredJobs.length === 0 && (
                    <p className="no-results">
                      Aucun résultat trouvé. Veuillez essayer d'autres critères de
                      recherche.
                    </p>
                  )}
                </div>
              )}

              <div className="job-cards">
                {displayedJobs.length === 0 && !isLoading ? (
                  <p className="no-jobs">Aucune offre d'emploi disponible pour le moment.</p>
                ) : (
                  displayedJobs.map((job) => (
                    <div className="job-card" key={job.id}>
                      <div className="job-card-content">
                        <h3 className="job-title">{job.title}</h3>
                        <div className="job-details">
                          <p className="job-location">{job.location}</p>
                          <p className="job-date">{job.date}</p>
                        </div>
                      </div>
                      <button 
                        className="postuler-button"
                        onClick={() => openApplicationModal(job)}
                      >
                        Postuler
                      </button>
                    </div>
                  ))
                )}
              </div>
            </>
          )}
        </div>
      </div>

      {/* Modal for job application */}
      {isModalOpen && selectedJob && (
        <div className="modal-overlay" onClick={closeModal}>
          <div 
            className="modal-content" 
            onClick={(e) => e.stopPropagation()}
          >
            <button className="modal-close" onClick={closeModal}>×</button>
            <div className="modal-title">
              <h2>Candidature pour {selectedJob.title}</h2>
            </div>
            
            {submitSuccess ? (
              <div className="success-message">
                <p>Votre candidature a été envoyée avec succès!</p>
              </div>
            ) : (
              <form onSubmit={handleSubmit}>
                {submitError && (
                  <div className="submit-error">
                    <p>{submitError}</p>
                  </div>
                )}
                
                <div className="form-section">
                  <label>Nom complet</label> 
                  <div className="name-inputs">
                    <input 
                      type="text" 
                      name="prenom" 
                      placeholder="Prénom" 
                      value={formData.prenom}
                      onChange={handleInputChange}
                      required 
                    />
                    <input 
                      type="text" 
                      name="nom" 
                      placeholder="Nom de famille" 
                      value={formData.nom}
                      onChange={handleInputChange}
                      required 
                    />
                  </div>
                </div>

                <div className="form-section">
                  <label>Adresse actuelle</label>
                  <input 
                    type="text" 
                    name="adresse" 
                    placeholder="Adresse" 
                    className="mb-3" 
                    value={formData.adresse}
                    onChange={handleInputChange}
                    required 
                  />
                </div>

                <div className="form-section">
                  <label>Adresse E-mail</label>
                  <input 
                    type="email" 
                    name="email" 
                    placeholder="Prénom@gmail.com" 
                    value={formData.email}
                    onChange={handleInputChange}
                    required 
                  />
                </div>

                <div className="form-section">
                  <label>Numéro de téléphone</label>
                  <div className="phone-input">
                    <input 
                      type="text" 
                      name="tel" 
                      placeholder="Numéro de téléphone" 
                      value={formData.tel}
                      onChange={handleInputChange}
                      required 
                    />
                  </div>
                </div>

                <div className="form-section">
                  <label>Déposez votre CV  (format PDF uniquement)</label>
                  <div className={`file-upload ${fileError ? "file-error" : ""}`} onClick={triggerFileInput}>
                    <input 
                      type="file" 
                      id="file-upload-input" 
                      accept=".pdf,application/pdf" 
                      onChange={handleFileChange}
                    />
                    <div className="file-upload-content">
                      <span className="upload-icon">📁</span>
                      <span className="browse-text">Parcourir les fichiers PDF</span>
                      <p className="selected-files">{fileName}</p>
                      {fileError && <p className="error-message">{fileError}</p>}
                    </div>
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="submit-application" 
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Envoi en cours..." : "Postuler"}
                </button>
              </form>
            )}
          </div>
        </div>
