import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X } from "lucide-react";
import axios from "axios";

// Styles encapsulés avec préfixe "df-"
const styles = `
.df-container {
  background: white;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
}

.df-header {
  padding: 1.5rem;
  border-bottom: 1px solid #e2e8f0;
}

.df-title {
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  color: #1f2937;
}

.df-search-container {
  position: relative;
  margin-bottom: 1rem;
}

.df-search-icon {
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  color: #9ca3af;
}

.df-search-input {
  width: 100%;
  max-width: 400px;
  padding: 0.5rem 0.75rem 0.5rem 2.5rem;
  border: 1px solid #d1d5db;
  border-radius: 0.375rem;
  transition: all 0.2s ease;
  font-size: 0.875rem;
}

.df-search-input:focus {
  outline: none;
  border-color: #14b8a6;
  box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
}

.df-table-container {
  width: 100%;
  overflow-x: auto;
}

.df-table {
  width: 100%;
  border-collapse: collapse;
}

.df-th {
  text-align: left;
  padding: 0.75rem 1rem;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  color: #6b7280;
  background-color: #f9fafb;
  border-bottom: 1px solid #e5e7eb;
}

.df-td {
  padding: 1rem;
  vertical-align: middle;
  color: #374151;
  border-bottom: 1px solid #f3f4f6;
}

.df-tr:hover {
  background-color: #f9fafb;
}

.df-text {
  font-size: 0.875rem;
}

.df-text-bold {
  font-weight: 500;
  color: #111827;
}

.df-date {
  font-size: 0.75rem;
  color: #6b7280;
}

.df-status-badge {
  display: flex;
  align-items: center;
  gap: 0.25rem;
  padding: 0.25rem 0.5rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 500;
  width: fit-content;
}

.df-status-accepte {
  background-color: #dcfce7;
  color: #166534;
}

.df-status-en-cours {
  background-color: #fef3c7;
  color: #92400e;
}

.df-status-refuse {
  background-color: #fee2e2;
  color: #b91c1c;
}

.df-action-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.375rem 0.75rem;
  border-radius: 0.375rem;
  font-size: 0.75rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  margin-right: 0.5rem;
}

.df-action-accept {
  color: #047857;
  background-color: #ecfdf5;
  border: 1px solid #a7f3d0;
}

.df-action-accept:hover {
  background-color: #d1fae5;
}

.df-action-reject {
  color: #b91c1c;
  background-color: #fef2f2;
  border: 1px solid #fecaca;
}

.df-action-reject:hover {
  background-color: #fee2e2;
}

.df-footer {
  padding: 1rem;
  background-color: #f9fafb;
  border-top: 1px solid #e5e7eb;
  font-size: 0.875rem;
  color: #6b7280;
}

.df-loading {
  text-align: center;
  padding: 2rem;
  color: #6b7280;
}

.df-error {
  text-align: center;
  padding: 2rem;
  color: #b91c1c;
}
`;

const GestionFormations = () => {
  const [demandes, setDemandes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Chargement initial des données
  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const response = await axios.get('/alldemandeformation');
        setDemandes(response.data);
      } catch (err) {
        setError("Erreur lors du chargement des demandes");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchDemandes();
  }, []);

  // Filtrage des demandes
  const filteredDemandes = demandes.filter(demande => {
    const searchLower = searchTerm.toLowerCase();
    return (
      demande.nom.toLowerCase().includes(searchLower) ||
      demande.prenom.toLowerCase().includes(searchLower) ||
      demande.nomFormation.toLowerCase().includes(searchLower) ||
      demande._id.toLowerCase().includes(searchLower)
    );
  });

  // Mise à jour du statut
  const updateStatut = async (_id, newStatus) => {
    try {
      // Conversion des statuts frontend → backend
      const statusMap = {
        accepte: 'Approuvée',
        refuse: 'Rejetée',
        en_cours: 'En attente'
      };

      const backendStatus = statusMap[newStatus];
      if (!backendStatus) throw new Error("Statut invalide");

      // Appel API pour mettre à jour le statut
      await axios.put(`/updatedemandeformation/${_id}`, { statut: backendStatus });

      // Mise à jour locale optimiste
      setDemandes(prev => prev.map(item => 
        item._id === _id ? { ...item, statut: backendStatus } : item
      ));

      alert(`Statut mis à jour avec succès !`);
    } catch (err) {
      console.error("Erreur de mise à jour:", err);
      alert("Échec de la mise à jour: " + (err.response?.data?.message || err.message));
    }
  };

  // Formatage de date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR');
  };

  // Rendu du badge de statut
  const renderStatusBadge = (status) => {
    switch (status) {
      case 'Approuvée':
        return (
          <div className="df-status-badge df-status-accepte">
            <Check size={14} />
            <span>Accepté</span>
          </div>
        );
      case 'En attente':
        return (
          <div className="df-status-badge df-status-en-cours">
            <AlertCircle size={14} />
            <span>En cours</span>
          </div>
        );
      case 'Rejetée':
        return (
          <div className="df-status-badge df-status-refuse">
            <X size={14} />
            <span>Refusé</span>
          </div>
        );
      default:
        return <div>{status}</div>;
    }
  };

  return (
    <>
      <style>{styles}</style>
      
      <div className="df-container">
        <div className="df-header">
          <h1 className="df-title">Gestion des Demandes de Formation</h1>
          
          <div className="df-search-container">
            <div className="df-search-icon">
              <Search size={18} />
            </div>
            <input
              type="text"
              className="df-search-input"
              placeholder="Rechercher par ID, nom, prénom ou formation..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        {loading ? (
          <div className="df-loading">Chargement en cours...</div>
        ) : error ? (
          <div className="df-error">{error}</div>
        ) : (
          <div className="df-table-container">
            <table className="df-table">
              <thead>
                <tr>
                  <th className="df-th">ID</th>
                  <th className="df-th">Nom</th>
                  <th className="df-th">Prénom</th>
                  <th className="df-th">Formation</th>
                  <th className="df-th">Statut</th>
                  <th className="df-th">Date</th>
                  <th className="df-th">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredDemandes.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="df-td" style={{ textAlign: 'center' }}>
                      Aucune demande trouvée
                    </td>
                  </tr>
                ) : (
                  filteredDemandes.map(demande => (
                    <tr key={demande._id} className="df-tr">
                      <td className="df-td">
                        <div className="df-text" title={demande._id}>
                          {demande._id.substring(0, 6)}...
                        </div>
                      </td>
                      <td className="df-td">
                        <div className="df-text df-text-bold">{demande.nom}</div>
                      </td>
                      <td className="df-td">
                        <div className="df-text df-text-bold">{demande.prenom}</div>
                      </td>
                      <td className="df-td">
                        <div className="df-text df-text-bold">{demande.nomFormation}</div>
                      </td>
                      <td className="df-td">
                        {renderStatusBadge(demande.statut)}
                      </td>
                      <td className="df-td">
                        <div className="df-date">{formatDate(demande.dateDemande)}</div>
                      </td>
                      <td className="df-td">
                        {demande.statut === 'En attente' && (
                          <div>
                            <button
                              className="df-action-btn df-action-accept"
                              onClick={() => updateStatut(demande._id, 'accepte')}
                            >
                              <Check size={14} className="mr-1" /> Accepter
                            </button>
                            <button
                              className="df-action-btn df-action-reject"
                              onClick={() => updateStatut(demande._id, 'refuse')}
                            >
                              <X size={14} className="mr-1" /> Rejeter
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}
        
        <div className="df-footer">
          {!loading && !error && (
            <div>
              Affichage de {filteredDemandes.length} demandes sur {demandes.length}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default GestionFormations;
