import React, { useState, useEffect } from "react";
import Navbar from "components/Navbars/IndexNavbar";
import axios from "axios";

const Postule = () => {
  // State for job listings and search
  const [jobListings, setJobListings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userApplications, setUserApplications] = useState([]);

  // State for modal and selected job
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [fileName, setFileName] = useState("Aucun fichier s√©lectionn√©");
  const [fileError, setFileError] = useState("");

  // Form data state
  const [formData, setFormData] = useState({
    prenom: "",
    nom: "",
    adresse: "",
    email: "",
    tel: "",
    cv: null,
    posteId: "",
    posteTitle: ""
  });

  // Search and filter states
  const [dateFilter, setDateFilter] = useState("N'importe quand");
  const [isDateExpanded, setIsDateExpanded] = useState(true);
  const [searchKeyword, setSearchKeyword] = useState("");
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [submitError, setSubmitError] = useState("");

  // Fetch job listings from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        
        // Fetch job listings
        const jobsResponse = await axios.get('/alloffre');
        const formattedJobs = jobsResponse.data.map(job => ({
          id: job._id,
          title: job.titre,
          location: job.lieu,
          date: formatDate(new Date(job.date))
        }));
        setJobListings(formattedJobs);
        
        // Fetch user applications if email is available (from localStorage or context)
        const userEmail = localStorage.getItem('userEmail'); // Adapt this to your auth system
        if (userEmail) {
          const appsResponse = await axios.get('/user-applications', {
            params: { email: userEmail }
          });
          setUserApplications(appsResponse.data);
        }
        
        setError(null);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Une erreur s'est produite lors du chargement des donn√©es.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // Helper function to format date as DD/MM/YYYY
  const formatDate = (date) => {
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  };

  // Function to toggle date filter section
  const toggleDateSection = () => {
    setIsDateExpanded(!isDateExpanded);
  };

  // Reset all filters
  const resetFilters = () => {
    setDateFilter("N'importe quand");
    setSearchKeyword("");
    setFilteredJobs([]);
    setHasSearched(false);
  };

  // Function for exact match search
  const exactMatch = (text, query) => {
    if (!query || query.trim() === "") return true;

    const formattedText = text.toLowerCase();
    const formattedQuery = query.toLowerCase().trim();

    const queryWords = formattedQuery.split(/\s+/);

    return queryWords.every((word) => {
      const regex = new RegExp(`\\b${word}\\b`, "i");
      return regex.test(formattedText);
    });
  };

  // Apply filters function
  const applyFilters = () => {
    let results = [...jobListings];
    
    if (searchKeyword && searchKeyword.trim() !== "") {
      results = results.filter((job) => exactMatch(job.title, searchKeyword));
    }

    if (dateFilter === "Sous 30 jours") {
      const today = new Date();
      const thirtyDaysAgo = new Date(today.getFullYear(), today.getMonth(), today.getDate() - 30);

      results = results.filter((job) => {
        const [day, month, year] = job.date.split("/");
        const jobDate = new Date(year, month - 1, day);

        return jobDate >= thirtyDaysAgo && jobDate <= today;
      });
    }

    setFilteredJobs(results);
    setHasSearched(true);
  };

  // Handle search form submission
  const handleSearch = (e) => {
    e.preventDefault();
    applyFilters();
  };

  // Apply filters when dateFilter changes
  useEffect(() => {
    if (jobListings.length > 0) {
      applyFilters();
    }
  }, [dateFilter, jobListings]);

  // Open application modal
  const openApplicationModal = (job) => {
    setSelectedJob(job);
    setIsModalOpen(true);
    setFileError("");
    setFormData({
      prenom: "",
      nom: "",
      adresse: "",
      email: localStorage.getItem('userEmail') || "", // Pre-fill if available
      tel: "",
      cv: null,
      posteId: job.id,
      posteTitle: job.title
    });
    setSubmitSuccess(false);
    setSubmitError("");
  };

  // Close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedJob(null);
    setFileName("Aucun fichier s√©lectionn√©");
    setFileError("");
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle file upload with PDF validation
  const handleFileChange = (e) => {
    setFileError("");
    
    if (e.target.files.length > 0) {
      const file = e.target.files[0];
      
      // Check if file is PDF
      if (file.type !== 'application/pdf') {
        setFileError("Erreur: Veuillez ne s√©lectionner que des fichiers PDF.");
        e.target.value = '';
        setFileName("Aucun fichier s√©lectionn√©");
        setFormData({
          ...formData,
          cv: null
        });
        return;
      }
      
      setFileName(file.name);
      setFormData({
        ...formData,
        cv: file
      });
    } else {
      setFileName("Aucun fichier s√©lectionn√©");
      setFormData({
        ...formData,
        cv: null
      });
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.cv) {
      setFileError("Erreur: Veuillez t√©l√©charger votre CV et lettre de motivation.");
      return;
    }
  
    try {
      setIsSubmitting(true);
      
      const formDataToSend = new FormData();
      formDataToSend.append("nom", formData.nom);
      formDataToSend.append("prenom", formData.prenom);
      formDataToSend.append("adresse", formData.adresse);
      formDataToSend.append("email", formData.email);
      formDataToSend.append("tel", formData.tel);
      formDataToSend.append("posteId", formData.posteId);
      formDataToSend.append("posteTitle", formData.posteTitle);
      formDataToSend.append("cv", formData.cv);
      
      const response = await axios.post('/addcondidature', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      // Update user applications after successful submission
      setUserApplications(prev => [...prev, {
        posteId: formData.posteId,
        status: 'pending'
      }]);
      
      setSubmitSuccess(true);
      
      setTimeout(() => {
        closeModal();
      }, 2000);
      
    } catch (err) {
      console.error("Error submitting application:", err);
      if (err.response && err.response.status === 400) {
        setSubmitError(err.response.data.message);
      } else {
        setSubmitError("Une erreur s'est produite lors de l'envoi de votre candidature. Veuillez r√©essayer.");
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  // Trigger the file input when clicking on the upload area
  const triggerFileInput = () => {
    document.getElementById("file-upload-input").click();
  };

  const displayedJobs = hasSearched ? filteredJobs : jobListings;

  // Count jobs that are within 30 days
  const countJobsWithin30Days = () => {
    const today = new Date();
    const thirtyDaysAgo = new Date(today.getFullYear(), today.getMonth(), today.getDate() - 30);

    return jobListings.filter((job) => {
      const [day, month, year] = job.date.split("/");
      const jobDate = new Date(year, month - 1, day);
      return jobDate >= thirtyDaysAgo && jobDate <= today;
    }).length;
  };

  const jobsWithin30DaysCount = countJobsWithin30Days();

  // Check if user has already applied to a job
  const hasUserApplied = (jobId) => {
    return userApplications.some(app => app.posteId === jobId && app.status !== 'rejected');
  };

  // Get application status for a job
  const getApplicationStatus = (jobId) => {
    const application = userApplications.find(app => app.posteId === jobId);
    return application ? application.status : null;
  };

  return (
    <div className="careers-page">
      <Navbar />
      
      {/* Banner and search bar */}
      <div className="banner">
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <div className="search-bar">
              <div className="search-input-container">
                <span className="search-icon">üîç</span>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Saisissez un titre de poste ou un mot-cl√©"
                  value={searchKeyword}
                  onChange={(e) => setSearchKeyword(e.target.value)}
                />
              </div>

              <button type="submit" className="search-button">
                Rechercher
              </button>
            </div>
          </form>
        </div>
      </div>

      <div className="content-container">
        <div className="filters-container">
          {/* Filters section */}
          <div className="filters-header">
            <h2>Filtres</h2>
            <button className="reset-button" onClick={resetFilters}>
              R√©initialiser
            </button>
          </div>

          <div className="filter-section">
            <div className="filter-header" onClick={toggleDateSection}>
              <h3>Date de publication</h3>
              <span className={`arrow ${isDateExpanded ? "expanded" : ""}`}>
                ‚ñº
              </span>
            </div>

            {isDateExpanded && (
              <div className="filter-options">
                <label className="filter-radio">
                  <input
                    type="radio"
                    name="dateFilter"
                    checked={dateFilter === "Sous 30 jours"}
                    onChange={() => handleDateFilterChange("Sous 30 jours")}
                  />
                  Sous 30 jours ({jobsWithin30DaysCount})
                </label>
                <label className="filter-radio">
                  <input
                    type="radio"
                    name="dateFilter"
                    checked={dateFilter === "N'importe quand"}
                    onChange={() => handleDateFilterChange("N'importe quand")}
                  />
                  N'importe quand ({jobListings.length})
                </label>
              </div>
            )}
          </div>
        </div>

        <div className="job-listings-container">
          <h1>Postes √† pourvoir actuellement</h1>

          {isLoading ? (
            <div className="loading-state">
              <p>Chargement des offres d'emploi...</p>
            </div>
          ) : error ? (
            <div className="error-state">
              <p>{error}</p>
              <button 
                className="retry-button"
                onClick={() => window.location.reload()}
              >
                R√©essayer
              </button>
            </div>
          ) : (
            <>
              {hasSearched && (
                <div className="search-results-info">
                  <p>
                    {filteredJobs.length} offre(s) d'emploi trouv√©e(s){" "}
                    {searchKeyword && `pour "${searchKeyword}"`}
                    {dateFilter === "Sous 30 jours" && " des 30 derniers jours"}
                  </p>
                  {filteredJobs.length === 0 && (
                    <p className="no-results">
                      Aucun r√©sultat trouv√©. Veuillez essayer d'autres crit√®res de
                      recherche.
                    </p>
                  )}
                </div>
              )}

              <div className="job-cards">
                {displayedJobs.length === 0 && !isLoading ? (
                  <p className="no-jobs">Aucune offre d'emploi disponible pour le moment.</p>
                ) : (
                  displayedJobs.map((job) => {
                    const hasApplied = hasUserApplied(job.id);
                    const applicationStatus = getApplicationStatus(job.id);
                    
                    return (
                      <div className="job-card" key={job.id}>
                        <div className="job-card-content">
                          <h3 className="job-title">{job.title}</h3>
                          <div className="job-details">
                            <p className="job-location">{job.location}</p>
                            <p className="job-date">{job.date}</p>
                          </div>
                        </div>
                        
                        {hasApplied ? (
                          <div className="already-applied">
                            {applicationStatus === 'pending' 
                              ? "Votre demande est en attente"
                              : "Votre demande a √©t√© accept√©e"}
                          </div>
                        ) : (
                          <button 
                            className="postuler-button"
                            onClick={() => openApplicationModal(job)}
                          >
                            Postuler
                          </button>
                        )}
                      </div>
                    );
                  })
                )}
              </div>
            </>
          )}
        </div>
      </div>

      {/* Modal for job application */}
      {isModalOpen && selectedJob && (
        <div className="modal-overlay" onClick={closeModal}>
          <div 
            className="modal-content" 
            onClick={(e) => e.stopPropagation()}
          >
            <button className="modal-close" onClick={closeModal}>√ó</button>
            <div className="modal-title">
              <h2>Candidature pour {selectedJob.title}</h2>
            </div>
            
            {submitSuccess ? (
              <div className="success-message">
                <p>Votre candidature a √©t√© envoy√©e avec succ√®s!</p>
              </div>
            ) : (
              <form onSubmit={handleSubmit}>
                {submitError && (
                  <div className="submit-error">
                    <p>{submitError}</p>
                  </div>
                )}
                
                <div className="form-section">
                  <label>Nom complet</label> 
                  <div className="name-inputs">
                    <input 
                      type="text" 
                      name="prenom" 
                      placeholder="Pr√©nom" 
                      value={formData.prenom}
                      onChange={handleInputChange}
                      required 
                    />
                    <input 
                      type="text" 
                      name="nom" 
                      placeholder="Nom de famille" 
                      value={formData.nom}
                      onChange={handleInputChange}
                      required 
                    />
                  </div>
                </div>

                <div className="form-section">
                  <label>Adresse actuelle</label>
                  <input 
                    type="text" 
                    name="adresse" 
                    placeholder="Adresse" 
                    className="mb-3" 
                    value={formData.adresse}
                    onChange={handleInputChange}
                    required 
                  />
                </div>

                <div className="form-section">
                  <label>Adresse E-mail</label>
                  <input 
                    type="email" 
                    name="email" 
                    placeholder="Pr√©nom@gmail.com" 
                    value={formData.email}
                    onChange={handleInputChange}
                    required 
                  />
                </div>

                <div className="form-section">
                  <label>Num√©ro de t√©l√©phone</label>
                  <div className="phone-input">
                    <input 
                      type="text" 
                      name="tel" 
                      placeholder="Num√©ro de t√©l√©phone" 
                      value={formData.tel}
                      onChange={handleInputChange}
                      required 
                    />
                  </div>
                </div>

                <div className="form-section">
                  <label>D√©posez votre CV (format PDF uniquement)</label>
                  <div className={`file-upload ${fileError ? "file-error" : ""}`} onClick={triggerFileInput}>
                    <input 
                      type="file" 
                      id="file-upload-input" 
                      accept=".pdf,application/pdf" 
                      onChange={handleFileChange}
                    />
                    <div className="file-upload-content">
                      <span className="upload-icon">üìÅ</span>
                      <span className="browse-text">Parcourir les fichiers PDF</span>
                      <p className="selected-files">{fileName}</p>
                      {fileError && <p className="error-message">{fileError}</p>}
                    </div>
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="submit-application" 
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Envoi en cours..." : "Postuler"}
                </button>
              </form>
            )}
          </div>
        </div>
      )}

      <style jsx>{`
        /* ... (tous vos styles existants restent les m√™mes) ... */
        
        .already-applied {
          padding: 10px 20px;
          background-color: #f0f0f0;
          border-radius: 4px;
          color: #555;
          font-size: 14px;
          text-align: center;
          margin-left: 10px;
        }
        
        .success-message {
          color: #2e7d32;
          text-align: center;
          padding: 20px;
          font-size: 16px;
        }
        
        .submit-error {
          color: #d32f2f;
          margin-bottom: 15px;
          padding: 10px;
          background-color: #ffebee;
          border-radius: 4px;
          text-align: center;
        }
      `}</style>
    </div>
  );
};

export default Postule;
