import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CardDemandeConge = () => {
  // √âtats
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const fileInputRef = useRef(null);

  // R√©cup√©ration des donn√©es utilisateur depuis le token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setUserData({
          nom: decoded.nom,
          prenom: decoded.prenom,
          email: decoded.email,
          soldeConge: decoded.soldeConge || 0
        });
      } catch (error) {
        console.error("Erreur de d√©codage du token", error);
      }
    }
  }, []);

  // Types de cong√©s disponibles
  const congeTypes = [
    { type: 'cong√© pay√©', title: 'Cong√© Pay√©', remainingDays: userData.soldeConge, icon: 'üèñÔ∏è', description: 'Cong√© annuel r√©mun√©r√©' },
    { type: 'rtt', title: 'RTT', remainingDays: 8, icon: '‚è±Ô∏è', description: 'R√©duction du temps de travail' },
    { type: 'cong√© maladie', title: 'Cong√© Maladie', remainingDays: 'Illimit√©', icon: 'üè•', description: 'Absence pour raison m√©dicale' },
    { type: 'cong√© parental', title: 'Cong√© Parental', remainingDays: 'Selon droits', icon: 'üë∂', description: 'Cong√© pour enfant' },
    { type: 'cong√© familial', title: '√âv√©nement Familial', remainingDays: 'Selon √©v√©nement', icon: 'üë™', description: 'Mariage, naissance, d√©c√®s' },
    { type: 'cong√© sans solde', title: 'Cong√© Sans Solde', remainingDays: 'Illimit√©', icon: 'üìÖ', description: 'Absence non r√©mun√©r√©e' },
    { type: 't√©l√©travail', title: 'T√©l√©travail', remainingDays: 'Selon accord', icon: 'üè†', description: 'Travail √† distance exceptionnel' }
  ];

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Gestion des fichiers
  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };

  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Calcul du nombre de jours de cong√©
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Validations
    const daysRequested = calculateDays();
    
    if (daysRequested <= 0) {
      toast.error('La date de fin doit √™tre post√©rieure √† la date de d√©but');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      toast.error('Veuillez joindre votre justificatif m√©dical');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'cong√© pay√©' && daysRequested > userData.soldeConge) {
      toast.error(`Solde insuffisant. Vous avez ${userData.soldeConge} jours disponibles.`);
      setIsSubmitting(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const formDataToSend = new FormData();
      
      // Ajout des donn√©es au FormData
      Object.entries(formData).forEach(([key, value]) => {
        if (value !== null && key !== 'medicalFile') {
          formDataToSend.append(key, value);
        }
      });
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      // Envoi de la requ√™te
      const response = await axios.post(
        'http://localhost:5000/api/adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succ√®s
      toast.success(`Demande de ${formData.type} enregistr√©e !`);
      
      // R√©initialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');
      
      // Mise √† jour du solde si cong√© pay√©
      if (formData.type === 'cong√© pay√©') {
        setUserData(prev => ({
          ...prev,
          soldeConge: prev.soldeConge - daysRequested
        }));
      }

    } catch (error) {
      console.error('Erreur:', error);
      toast.error(error.response?.data?.message || 'Erreur lors de la soumission');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Variables d√©riv√©es
  const selectedCongeType = congeTypes.find(c => c.type === formData.type);
  const isMaladieType = formData.type === 'cong√© maladie';
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  const daysRequested = calculateDays();
  const remainingDays = formData.type === 'cong√© pay√©' ? userData.soldeConge - daysRequested : null;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-md">
      {/* En-t√™te */}
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-blue-800 mb-1">Demande de Cong√©</h1>
        <p className="text-gray-600">Bonjour {userData.prenom} {userData.nom}</p>
      </div>

      {/* Carte d'information */}
      <div className="bg-blue-50 p-4 rounded-lg mb-6 border border-blue-100">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="font-medium text-blue-800">Vos droits √† cong√©</h3>
            <p className="text-sm text-gray-600">Solde actuel: {userData.soldeConge} jours</p>
          </div>
          {formData.type === 'cong√© pay√©' && daysRequested > 0 && (
            <div className="bg-blue-100 px-3 py-1 rounded-full">
              <span className="text-blue-800 font-medium">
                Solde apr√®s demande: {remainingDays >= 0 ? remainingDays : 0} jours
              </span>
            </div>
          )}
        </div>
      </div>

      {/* Formulaire */}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Type de cong√© */}
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Type de cong√©</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            {congeTypes.map((conge, index) => (
              <option key={index} value={conge.type}>
                {conge.icon} {conge.title} - {conge.description}
              </option>
            ))}
          </select>
          <p className="mt-1 text-sm text-gray-500">
            {selectedCongeType?.remainingDays !== 'Illimit√©' && selectedCongeType?.remainingDays !== 'Selon droits' && selectedCongeType?.remainingDays !== 'Selon √©v√©nement' && selectedCongeType?.remainingDays !== 'Selon accord'
              ? `Jours disponibles: ${selectedCongeType?.remainingDays}`
              : selectedCongeType?.description}
          </p>
        </div>

        {/* Dates */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Date de d√©but</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-2">Date de fin</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
              min={formData.startDate || new Date().toISOString().split('T')[0]}
            />
          </div>
        </div>

        {/* R√©sum√© de la demande */}
        {formData.startDate && formData.endDate && (
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="flex justify-between items-center">
              <div>
                <h4 className="font-medium text-blue-800">R√©sum√© de votre demande</h4>
                <p className="text-sm text-gray-600">
                  Du {new Date(formData.startDate).toLocaleDateString()} au {new Date(formData.endDate).toLocaleDateString()}
                </p>
              </div>
              <div className="bg-blue-100 px-3 py-1 rounded-full">
                <span className="font-medium text-blue-800">{daysRequested} jours</span>
              </div>
            </div>
          </div>
        )}

        {/* Motif */}
        {!isMaladieType && (
          <div>
            <label className="block text-gray-700 font-medium mb-2">
              Motif {needsJustification && '*'}
            </label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder={needsJustification ? "Pr√©cisez le motif (obligatoire)" : "Pr√©cisez le motif (facultatif)"}
              required={needsJustification}
            />
          </div>
        )}

        {/* Justificatif m√©dical */}
        {isMaladieType && (
          <div>
            <label className="block text-gray-700 font-medium mb-2">Justificatif m√©dical *</label>
            <div className="border-2 border-dashed border-blue-300 rounded-lg p-4 text-center bg-blue-50">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                className="hidden"
                id="medicalFile"
                accept=".pdf,.jpg,.jpeg,.png"
                required
              />
              <label htmlFor="medicalFile" className="cursor-pointer">
                {fileName ? (
                  <div className="flex items-center justify-between bg-white p-3 rounded-lg border">
                    <div className="flex items-center">
                      <span className="text-blue-600 mr-2">üìÑ</span>
                      <span className="truncate max-w-xs">{fileName}</span>
                    </div>
                    <button 
                      type="button" 
                      onClick={removeFile}
                      className="text-red-500 hover:text-red-700 ml-2"
                    >
                      ‚úï
                    </button>
                  </div>
                ) : (
                  <div className="py-6">
                    <div className="mx-auto w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mb-2">
                      <span className="text-blue-600 text-xl">üìé</span>
                    </div>
                    <p className="text-gray-700">Glissez-d√©posez votre justificatif ou cliquez pour s√©lectionner</p>
                    <p className="text-sm text-gray-500 mt-1">Formats accept√©s: PDF, JPG, PNG (max 5MB)</p>
                  </div>
                )}
              </label>
            </div>
          </div>
        )}

        {/* Notes compl√©mentaires */}
        <div>
          <label className="block text-gray-700 font-medium mb-2">Notes compl√©mentaires</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="3"
            placeholder="Informations additionnelles (facultatif)"
          />
        </div>

        {/* Bouton de soumission */}
        <div className="pt-2">
          <button
            type="submit"
            disabled={isSubmitting}
            className={`w-full py-3 px-4 rounded-lg font-medium text-white transition duration-200 ${
              isSubmitting 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            {isSubmitting ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Envoi en cours...
              </span>
            ) : 'Soumettre la demande'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CardDemandeConge;
