import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';  // Import de useHistory pour React Router v5

const AjouterDemandeConge = () => {
  const [dateDebut, setDateDebut] = useState('');
  const [dateFin, setDateFin] = useState('');
  const [typeConge, setTypeConge] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();  // Remplacer navigate par history

  // Vérifier l'authentification à l'affichage du composant
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/login');  // Rediriger vers la page de login si pas connecté
    }
  }, [history]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    const token = localStorage.getItem('token');
    if (!token) {
      setError('Vous devez être connecté pour faire une demande.');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:5000/adddemandeconge',
        {
          date_debut: dateDebut,
          date_fin: dateFin,
          type_conge: typeConge,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200) {
        setMessage('Demande de congé envoyée avec succès !');
        setDateDebut('');
        setDateFin('');
        setTypeConge('');
      }
    } catch (err) {
      console.error(err);
      setError('Erreur lors de l\'envoi de la demande.');
    }
  };

  return (
    <div className="form-container">
      <h2>Ajouter une demande de congé</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date de début:</label>
          <input
            type="date"
            value={dateDebut}
            onChange={(e) => setDateDebut(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date de fin:</label>
          <input
            type="date"
            value={dateFin}
            onChange={(e) => setDateFin(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Type de congé:</label>
          <select
            value={typeConge}
            onChange={(e) => setTypeConge(e.target.value)}
            required
          >
            <option value="">-- Choisissez --</option>
            <option value="annuel">Annuel</option>
            <option value="maladie">Maladie</option>
            <option value="sans solde">Sans solde</option>
          </select>
        </div>
        <button type="submit">Envoyer la demande</button>
      </form>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default AjouterDemandeConge;
