import React from "react";
import { Link } from "react-router-dom";

import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Signin from "./auth/Signin";
import Postule from "./Postule";

export default function Index() {
  return (
    <>
      <style>
        {`
          /* Global Styles */
          body {
            font-family: 'Poppins', sans-serif;
            color: #333;
          }
          
          /* Hero Section */
          .hero-section {
            background: linear-gradient(135deg, #f5f7fa 0%, #e4edf5 100%);
            min-height: 100vh;
            position: relative;
            overflow: hidden;
          }
          
          .hero-content {
            padding-top: 8rem;
            position: relative;
            z-index: 10;
          }
          
          .hero-title {
            font-size: 3.5rem;
            font-weight: 700;
            color: #00468C;
            margin-bottom: 1.5rem;
            line-height: 1.2;
            position: relative;
          }
          
          .hero-title::after {
            content: "";
            position: absolute;
            height: 4px;
            width: 80px;
            background: #009ED8;
            bottom: -12px;
            left: 0;
          }
          
          .hero-description {
            font-size: 1.25rem;
            line-height: 1.8;
            color: #4A5568;
            margin-bottom: 2.5rem;
            max-width: 560px;
          }
          
          .hero-image-container {
            position: relative;
          }
          
          .hero-image {
            border-radius: 8px;
            box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
            position: relative;
            z-index: 1;
          }
          
          .hero-image::before {
            content: "";
            position: absolute;
            top: -15px;
            right: -15px;
            bottom: -15px;
            left: -15px;
            background: linear-gradient(45deg, #009ED8, #00468C);
            border-radius: 8px;
            z-index: -1;
            opacity: 0.6;
          }
          
          /* Buttons */
          .btn {
            padding: 1rem 2rem;
            font-weight: 600;
            border-radius: 6px;
            letter-spacing: 0.5px;
            transition: all 0.3s ease;
            position: relative;
            overflow: hidden;
            text-transform: uppercase;
            font-size: 0.9rem;
          }
          
          .btn:hover {
            transform: translateY(-3px);
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
          }
          
          .btn-primary {
            background: linear-gradient(45deg, #009ED8, #007bb8);
            color: white;
            border: none;
          }
          
          .btn-secondary {
            background: linear-gradient(45deg, #00468C, #003366);
            color: white;
            border: none;
          }
          
          /* Animation */
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
          
          .animate-fade-in {
            animation: fadeIn 1s ease forwards;
          }
          
          .delay-1 {
            animation-delay: 0.2s;
          }
          
          .delay-2 {
            animation-delay: 0.4s;
          }
          
          .delay-3 {
            animation-delay: 0.6s;
          }
          
          /* Responsive adjustments */
          @media (max-width: 768px) {
            .hero-title {
              font-size: 2.5rem;
            }
            
            .hero-description {
              font-size: 1.1rem;
            }
            
            .btn {
              padding: 0.75rem 1.5rem;
            }
          }
        `}
      </style>
      <IndexNavbar fixed />
      <section className="hero-section relative pt-16 items-center flex">
        <div className="container mx-auto items-center flex flex-wrap">
          <div className="w-full md:w-8/12 lg:w-6/12 xl:w-6/12 px-4">
            <div className="hero-content">
              <h2 className="hero-title animate-fade-in">
                BIENVENUE SUR UBCI HR MANAGER
              </h2>
              <p className="hero-description animate-fade-in delay-1">
                UBCI RH est un site web dédié à la gestion des ressources humaines de la banque UBCI, facilitant le suivi du personnel, la gestion des congés et l'automatisation des tâches administratives.
              </p>
              <div className="mt-12 animate-fade-in delay-2">
                <Link
                  to="/postule"
                  className="btn btn-primary mr-4"
                >
                  Postuler
                </Link>
                <Link
                  to="/signin"
                  className="btn btn-secondary"
                >
                  Mon Espace
                </Link>
              </div>
            </div>
          </div>
        </div>

        <div className="absolute top-0 right-0 pt-16 sm:w-6/12 w-full h-full hero-image-container animate-fade-in delay-3">
          <img
            className="hero-image w-full h-full object-cover"
            src={require("assets/img/ubci.jpg").default}
            alt="UBCI Building"
          />
        </div>
      </section>
    </>
  );
}
