import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // Styles
  const styles = {
    container: {
      maxWidth: '56rem',
      margin: '0 auto',
      padding: '1.5rem',
      backgroundColor: 'white',
      borderRadius: '0.5rem',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
      position: 'relative'
    },
    header: {
      marginBottom: '1.5rem'
    },
    title: {
      fontSize: '1.5rem',
      fontWeight: '700',
      color: '#1e40af',
      marginBottom: '0.25rem'
    },
    userGreeting: {
      color: '#4b5563'
    },
    infoCard: {
      backgroundColor: '#eff6ff',
      padding: '1rem',
      borderRadius: '0.375rem',
      border: '1px solid #dbeafe',
      marginBottom: '1.5rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    infoTitle: {
      fontWeight: '500',
      color: '#1e40af'
    },
    infoText: {
      fontSize: '0.875rem',
      color: '#4b5563'
    },
    balanceBadge: {
      backgroundColor: '#dbeafe',
      padding: '0.25rem 0.75rem',
      borderRadius: '9999px',
      color: '#1e40af',
      fontWeight: '500'
    },
    formGroup: {
      marginBottom: '1rem'
    },
    label: {
      display: 'block',
      color: '#374151',
      fontWeight: '500',
      marginBottom: '0.5rem'
    },
    select: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.375rem',
      outline: 'none',
      focus: {
        ring: '2px',
        ringColor: '#3b82f6'
      }
    },
    dateGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(1, 1fr)',
      gap: '1rem',
      '@media (min-width: 768px)': {
        gridTemplateColumns: 'repeat(2, 1fr)'
      }
    },
    input: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.375rem',
      outline: 'none',
      focus: {
        ring: '2px',
        ringColor: '#3b82f6'
      }
    },
    summaryCard: {
      backgroundColor: '#eff6ff',
      padding: '1rem',
      borderRadius: '0.375rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    fileUpload: {
      border: '2px dashed #93c5fd',
      borderRadius: '0.375rem',
      padding: '1rem',
      textAlign: 'center',
      backgroundColor: '#eff6ff',
      cursor: 'pointer'
    },
    filePreview: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      backgroundColor: 'white',
      padding: '0.75rem',
      borderRadius: '0.375rem',
      border: '1px solid #d1d5db'
    },
    textarea: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.375rem',
      outline: 'none',
      minHeight: '6rem',
      focus: {
        ring: '2px',
        ringColor: '#3b82f6'
      }
    },
    submitButton: {
      width: '100%',
      padding: '0.75rem 1rem',
      borderRadius: '0.375rem',
      fontWeight: '500',
      color: 'white',
      backgroundColor: '#2563eb',
      border: 'none',
      cursor: 'pointer',
      transition: 'background-color 0.2s',
      hover: {
        backgroundColor: '#1d4ed8'
      },
      disabled: {
        backgroundColor: '#9ca3af',
        cursor: 'not-allowed'
      }
    },
    notification: {
      position: 'fixed',
      top: '1rem',
      right: '1rem',
      borderLeft: '4px solid',
      padding: '1rem',
      borderRadius: '0.375rem',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      minWidth: '16rem',
      zIndex: '50',
      error: {
        backgroundColor: '#fee2e2',
        borderColor: '#f87171',
        color: '#b91c1c'
      },
      success: {
        backgroundColor: '#dcfce7',
        borderColor: '#4ade80',
        color: '#166534'
      }
    }
  };

  // √âtats
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldedeconge: 0
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [notification, setNotification] = useState(null);
  const fileInputRef = useRef(null);

  // Afficher une notification
  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 5000);
  };

  // R√©cup√©ration des donn√©es utilisateur depuis le token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setUserData({
          nom: decoded.nom,
          prenom: decoded.prenom,
          email: decoded.email,
          soldedeconge: decoded.soldedeconge || 0
        });
      } catch (error) {
        console.error("Erreur de d√©codage du token", error);
      }
    }
  }, []);

  // Types de cong√©s disponibles
  const congeTypes = [
    { type: 'cong√© pay√©', title: 'Cong√© Pay√©', remainingDays: userData.soldedeconge, icon: 'üèñÔ∏è', description: 'Cong√© annuel r√©mun√©r√©' },
    { type: 'rtt', title: 'RTT', remainingDays: 8, icon: '‚è±Ô∏è', description: 'R√©duction du temps de travail' },
    { type: 'cong√© maladie', title: 'Cong√© Maladie', remainingDays: 'Illimit√©', icon: 'üè•', description: 'Absence pour raison m√©dicale' },
    { type: 'cong√© parental', title: 'Cong√© Parental', remainingDays: 'Selon droits', icon: 'üë∂', description: 'Cong√© pour enfant' },
    { type: 'cong√© familial', title: '√âv√©nement Familial', remainingDays: 'Selon √©v√©nement', icon: 'üë™', description: 'Mariage, naissance, d√©c√®s' },
    { type: 'cong√© sans solde', title: 'Cong√© Sans Solde', remainingDays: 'Illimit√©', icon: 'üìÖ', description: 'Absence non r√©mun√©r√©e' },
    { type: 't√©l√©travail', title: 'T√©l√©travail', remainingDays: 'Selon accord', icon: 'üè†', description: 'Travail √† distance exceptionnel' }
  ];

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Gestion des fichiers
  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };

  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Calcul du nombre de jours de cong√©
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Validations
    const daysRequested = calculateDays();
    
    if (daysRequested <= 0) {
      showNotification('La date de fin doit √™tre post√©rieure √† la date de d√©but', 'error');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      showNotification('Veuillez joindre votre justificatif m√©dical', 'error');
      setIsSubmitting(false);
      return;
    }

    if (formData.type === 'cong√© pay√©' && daysRequested > userData.soldedeconge) {
      showNotification(`Solde insuffisant. Vous avez ${userData.soldedeconge} jours disponibles.`, 'error');
      setIsSubmitting(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const formDataToSend = new FormData();
      
      // Ajout des donn√©es au FormData
      Object.entries(formData).forEach(([key, value]) => {
        if (value !== null && key !== 'medicalFile') {
          formDataToSend.append(key, value);
        }
      });
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      // Envoi de la requ√™te
      const response = await axios.post(
        'http://localapihost:5000//adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succ√®s
      showNotification(`Demande de ${formData.type} enregistr√©e avec succ√®s!`, 'success');
      
      // R√©initialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');
      
      // Mise √† jour du solde si cong√© pay√©
      if (formData.type === 'cong√© pay√©') {
        setUserData(prev => ({
          ...prev,
          soldedeconge: prev.soldedeconge - daysRequested
        }));
      }

    } catch (error) {
      console.error('Erreur:', error);
      showNotification(error.response?.data?.message || 'Erreur lors de la soumission', 'error');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Variables d√©riv√©es
  const selectedCongeType = congeTypes.find(c => c.type === formData.type);
  const isMaladieType = formData.type === 'cong√© maladie';
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  const daysRequested = calculateDays();
  const remainingDays = formData.type === 'cong√© pay√©' ? userData.soldedeconge - daysRequested : null;

  return (
    <div style={styles.container}>
      {/* Notification */}
      {notification && (
        <div style={{
          ...styles.notification,
          ...(notification.type === 'error' ? styles.notification.error : styles.notification.success)
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span>{notification.message}</span>
            <button onClick={() => setNotification(null)} style={{ marginLeft: '1rem', fontSize: '1.25rem' }}>
              &times;
            </button>
          </div>
        </div>
      )}

      {/* En-t√™te */}
      <div style={styles.header}>
        <h1 style={styles.title}>Demande de Cong√©</h1>
        <p style={styles.userGreeting}>Bonjour {userData.prenom} {userData.nom}</p>
      </div>

      {/* Carte d'information */}
      <div style={styles.infoCard}>
        <div>
          <h3 style={styles.infoTitle}>Vos droits √† cong√©</h3>
          <p style={styles.infoText}>Solde actuel: {userData.soldedeconge} jours</p>
        </div>
        {formData.type === 'cong√© pay√©' && daysRequested > 0 && (
          <div style={styles.balanceBadge}>
            Solde apr√®s demande: {remainingDays >= 0 ? remainingDays : 0} jours
          </div>
        )}
      </div>

      {/* Formulaire */}
      <form onSubmit={handleSubmit} style={{ marginTop: '1rem' }}>
        {/* Type de cong√© */}
        <div style={styles.formGroup}>
          <label style={styles.label}>Type de cong√©</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            style={styles.select}
            required
          >
            {congeTypes.map((conge, index) => (
              <option key={index} value={conge.type}>
                {conge.icon} {conge.title} - {conge.description}
              </option>
            ))}
          </select>
          <p style={{ marginTop: '0.25rem', fontSize: '0.875rem', color: '#6b7280' }}>
            {selectedCongeType?.remainingDays !== 'Illimit√©' && selectedCongeType?.remainingDays !== 'Selon droits' && selectedCongeType?.remainingDays !== 'Selon √©v√©nement' && selectedCongeType?.remainingDays !== 'Selon accord'
              ? `Jours disponibles: ${selectedCongeType?.remainingDays}`
              : selectedCongeType?.description}
          </p>
        </div>

        {/* Dates */}
        <div style={{ ...styles.dateGrid, marginBottom: '1rem' }}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Date de d√©but</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              style={styles.input}
              required
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div style={styles.formGroup}>
            <label style={styles.label}>Date de fin</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              style={styles.input}
              required
              min={formData.startDate || new Date().toISOString().split('T')[0]}
            />
          </div>
        </div>

        {/* R√©sum√© de la demande */}
        {formData.startDate && formData.endDate && (
          <div style={styles.summaryCard}>
            <div>
              <h4 style={{ fontWeight: '500', color: '#1e40af' }}>R√©sum√© de votre demande</h4>
              <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                Du {new Date(formData.startDate).toLocaleDateString()} au {new Date(formData.endDate).toLocaleDateString()}
              </p>
            </div>
            <div style={styles.balanceBadge}>
              {daysRequested} jours
            </div>
          </div>
        )}

        {/* Motif */}
        {!isMaladieType && (
          <div style={styles.formGroup}>
            <label style={styles.label}>
              Motif {needsJustification && '*'}
            </label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              style={styles.input}
              placeholder={needsJustification ? "Pr√©cisez le motif (obligatoire)" : "Pr√©cisez le motif (facultatif)"}
              required={needsJustification}
            />
          </div>
        )}

        {/* Justificatif m√©dical */}
        {isMaladieType && (
          <div style={styles.formGroup}>
            <label style={styles.label}>Justificatif m√©dical *</label>
            <div style={styles.fileUpload}>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                style={{ display: 'none' }}
                id="medicalFile"
                accept=".pdf,.jpg,.jpeg,.png"
                required
              />
              <label htmlFor="medicalFile" style={{ cursor: 'pointer' }}>
                {fileName ? (
                  <div style={styles.filePreview}>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ color: '#3b82f6', marginRight: '0.5rem' }}>üìÑ</span>
                      <span style={{ overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', maxWidth: '16rem' }}>
                        {fileName}
                      </span>
                    </div>
                    <button 
                      type="button" 
                      onClick={removeFile}
                      style={{ color: '#ef4444', marginLeft: '0.5rem', fontSize: '1.25rem' }}
                    >
                      ‚úï
                    </button>
                  </div>
                ) : (
                  <div style={{ padding: '1.5rem 0' }}>
                    <div style={{ 
                      width: '3rem', 
                      height: '3rem', 
                      backgroundColor: '#dbeafe', 
                      borderRadius: '9999px', 
                      margin: '0 auto 0.5rem', 
                      display: 'flex', 
                      alignItems: 'center', 
                      justifyContent: 'center' 
                    }}>
                      <span style={{ color: '#3b82f6', fontSize: '1.25rem' }}>üìé</span>
                    </div>
                    <p style={{ color: '#374151' }}>Glissez-d√©posez votre justificatif ou cliquez pour s√©lectionner</p>
                    <p style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>
                      Formats accept√©s: PDF, JPG, PNG (max 5MB)
                    </p>
                  </div>
                )}
              </label>
            </div>
          </div>
        )}

        {/* Notes compl√©mentaires */}
        <div style={styles.formGroup}>
          <label style={styles.label}>Notes compl√©mentaires</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            style={styles.textarea}
            placeholder="Informations additionnelles (facultatif)"
          />
        </div>

        {/* Bouton de soumission */}
        <div style={{ marginTop: '0.5rem' }}>
          <button
            type="submit"
            disabled={isSubmitting}
            style={{
              ...styles.submitButton,
              ...(isSubmitting ? styles.submitButton.disabled : {}),
              ':hover': !isSubmitting ? styles.submitButton.hover : {}
            }}
          >
            {isSubmitting ? (
              <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <svg 
                  style={{ 
                    animation: 'spin 1s linear infinite',
                    marginRight: '0.5rem',
                    width: '1rem',
                    height: '1rem'
                  }} 
                  xmlns="http://www.w3.org/2000/svg" 
                  fill="none" 
