import { useState, useEffect } from 'react';
import axios from 'axios';

function CardDemandeFormation() {
  // État pour stocker les formations
  const [formations, setFormations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // États pour le filtrage et la pagination
  const [filteredFormations, setFilteredFormations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(3);
  
  // État pour afficher la confirmation
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [demandes, setDemandes] = useState([]);
  
  // État pour suivre les formations déjà demandées
  const [requestedFormations, setRequestedFormations] = useState([]);
  
  // État pour stocker les informations de l'utilisateur connecté
  const [userInfo, setUserInfo] = useState(null);

  // Récupérer les informations de l'utilisateur connecté
  useEffect(() => {
    const userData = localStorage.getItem('userData');
    if (userData) {
      setUserInfo(JSON.parse(userData));
    }
  }, []);

  // Récupérer les formations depuis l'API
  useEffect(() => {
    const fetchFormations = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/getformation');
        
        // Transformer les données pour correspondre à la structure attendue
        const formattedFormations = response.data.map(formation => ({
          id: formation._id,
          titre: formation.nom,
          description: formation.description,
          date: new Date(formation.date).toISOString().split('T')[0],
          duree: formation.durée,
          places: formation.nbplaces,
          inscrits: formation.nbinscrits,
          statut: formation.statut,
          placesRestantes: formation.nbplaces - formation.nbinscrits
        }));
        
        setFormations(formattedFormations);
        setLoading(false);
      } catch (err) {
        setError("Erreur lors du chargement des formations");
        setLoading(false);
        console.error("Erreur lors de la récupération des formations:", err);
      }
    };

    fetchFormations();
  }, []);

  // Récupérer les demandes de l'utilisateur
  useEffect(() => {
    if (userInfo && userInfo.email) {
      const fetchUserDemandes = async () => {
        try {
          const response = await axios.get(`/getdemandesbyemail?email=${userInfo.email}`);
          const userDemandes = response.data;
          
          // Mettre à jour les formations déjà demandées
          const requestedIds = userDemandes.map(d => d.nomFormation);
          setRequestedFormations(requestedIds);
          
          // Mettre à jour l'historique des demandes
          setDemandes(userDemandes.map(demande => ({
            id: demande._id,
            formation: { titre: demande.nomFormation },
            status: demande.statut,
            dateCreation: demande.dateDemande
          })));
        } catch (err) {
          console.error("Erreur lors de la récupération des demandes:", err);
        }
      };
      
      fetchUserDemandes();
    }
  }, [userInfo]);

  // Effet pour filtrer les formations
  useEffect(() => {
    let result = formations.filter(formation => 
      // Ne montrer que les formations programmées (pas complètes ou terminées)
      formation.statut === 'Programmée' && 
      // Vérifier qu'il reste des places
      formation.inscrits < formation.places
    );
    
    // Filtrer par terme de recherche
    if (searchTerm) {
      result = result.filter(formation => 
        formation.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        formation.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    // Filtrer par catégorie
    if (selectedCategory !== 'all') {
      result = result.filter(formation => {
        // Exemple de catégorisation basée sur les titres, peut être remplacé par une catégorisation réelle
        const title = formation.titre.toLowerCase();
        if (selectedCategory === 'tech') {
          return title.includes('react') || title.includes('excel') || title.includes('développement');
        }
        if (selectedCategory === 'management') {
          return title.includes('leadership') || title.includes('management');
        }
        if (selectedCategory === 'soft') {
          return title.includes('communication') || title.includes('rgpd');
        }
        return true;
      });
    }
    
    setFilteredFormations(result);
  }, [formations, searchTerm, selectedCategory]);

  // Calcul de la pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentFormations = filteredFormations.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredFormations.length / itemsPerPage);

  // Formatage de la date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  // Vérifier si une formation a déjà été demandée
  const isFormationRequested = (formationId) => {
    return requestedFormations.includes(formationId);
  };

  // Gérer la demande d'inscription à une formation
  const handleRequestFormation = async (formation) => {
    // Vérifier si l'utilisateur est connecté
    if (!userInfo) {
      setError("Vous devez être connecté pour demander une formation");
      setShowConfirmation(true);
      setTimeout(() => setShowConfirmation(false), 3000);
      return;
    }
    
    // Vérifier si la formation a déjà été demandée
    if (isFormationRequested(formation.id)) {
      setError("Vous avez déjà demandé cette formation");
      setShowConfirmation(true);
      setTimeout(() => setShowConfirmation(false), 3000);
      return;
    }
    
    try {
      // Créer l'objet demande
      const demandeData = {
        nom: userInfo.nom,
        prenom: userInfo.prenom,
        email: userInfo.email,
        nomFormation: formation.titre,
        statut: 'En attente'
      };
      
      // Envoyer la demande au backend
      const response = await axios.post('/adddemandeformation', demandeData);
      
      // Mettre à jour l'état local
      setRequestedFormations([...requestedFormations, formation.id]);
      
      // Ajouter la demande à l'historique
      const newDemande = {
        id: response.data._id,
        formation: { titre: formation.titre },
        status: 'En attente',
        dateCreation: new Date().toISOString()
      };
      setDemandes([...demandes, newDemande]);
      
      // Afficher le message de confirmation
      setConfirmationMessage(`Votre demande pour la formation "${formation.titre}" a été envoyée avec succès!`);
      setShowConfirmation(true);
      
      // Fermer la confirmation après 3 secondes
      setTimeout(() => {
        setShowConfirmation(false);
      }, 3000);
      
    } catch (err) {
      console.error("Erreur lors de l'envoi de la demande:", err);
      setError("Une erreur est survenue lors de l'envoi de votre demande");
      setShowConfirmation(true);
      setTimeout(() => setShowConfirmation(false), 3000);
    }
  };

  // Afficher un message de chargement pendant la récupération des données
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement des formations...</p>
      </div>
    );
  }

  // Afficher un message d'erreur si la récupération a échoué
  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={() => window.location.reload()} className="retry-btn">
          Réessayer
        </button>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Header */}
      <div className="header">
        <h1>Catalogue de formations</h1>
        <p>Découvrez et inscrivez-vous aux formations disponibles</p>
      </div>

      {/* Filtres */}
      <div className="filters">
        <div className="search-container">
          <input
            type="text"
            placeholder="Rechercher une formation..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <div className="search-icon">🔍</div>
        </div>
      </div>

      {/* Liste des formations */}
      <div className="formations-grid">
        {currentFormations.length > 0 ? (
          currentFormations.map(formation => (
            <div key={formation.id} className="formation-card">
              <div className="formation-header">
                <h3>{formation.titre}</h3>
                <span className={`places-badge ${formation.places - formation.inscrits <= 3 ? 'low-places' : ''}`}>
                  {formation.places - formation.inscrits} places restantes
                </span>
              </div>
              
              <div className="formation-content">
                <p className="formation-description">{formation.description}</p>
                
                <div className="formation-details">
                  <div className="detail-item">
                    <span className="detail-icon">📅</span>
                    <span>{formatDate(formation.date)}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-icon">⏱️</span>
                    <span>{formation.duree} heures</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-icon">👥</span>
                    <span>{formation.inscrits}/{formation.places} participants</span>
                  </div>
                </div>
              </div>
              
              <div className="formation-footer">
                <button 
                  className={`request-btn ${isFormationRequested(formation.id) ? 'disabled' : ''}`}
                  onClick={() => handleRequestFormation(formation)}
                  disabled={isFormationRequested(formation.id)}
                >
                  {isFormationRequested(formation.id) ? 'Formation demandée' : 'Demander cette formation'}
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-formations">
            <p>Aucune formation disponible ne correspond à vos critères.</p>
          </div>
        )}
      </div>

      {/* Pagination */}
      {filteredFormations.length > 0 && (
        <div className="pagination">
          <button 
            className="page-btn"
            disabled={currentPage === 1}
            onClick={() => setCurrentPage(currentPage - 1)}
          >
            Précédent
          </button>
          
          <div className="page-info">
            Page {currentPage} sur {totalPages}
          </div>
          
          <button 
            className="page-btn"
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage(currentPage + 1)}
          >
            Suivant
          </button>
        </div>
      )}

      {/* Toast de confirmation */}
      {showConfirmation && (
        <div className={`toast-message ${error ? 'error' : 'success'}`}>
          <div className="toast-content">
            <span className="toast-icon">{error ? '⚠️' : '✓'}</span>
            <p>{error || confirmationMessage}</p>
          </div>
        </div>
      )}

      {/* Historique des demandes */}
      {demandes.length > 0 && (
        <div className="demandes-section">
          <h2>Mes demandes de formation</h2>
          <div className="demandes-list">
            <table>
              <thead>
                <tr>
                  <th>Formation</th>
                  <th>Date de la demande</th>
                  <th>Statut</th>
                </tr>
              </thead>
              <tbody>
                {demandes.map(demande => (
                  <tr key={demande.id}>
                    <td>{demande.formation.titre}</td>
                    <td>{new Date(demande.dateCreation).toLocaleDateString('fr-FR')}</td>
                    <td>
                      <span className={`status-badge ${demande.status.toLowerCase().replace(' ', '-')}`}>
                        {demande.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <style>
        {`
        /* Styles généraux */
        .container {
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          max-width: 1200px;
          margin: 0 auto;
          padding: 20px;
          background-color: '#334155';
          min-height: 100vh;
        }

        /* Header */
        .header {
          text-align: center;
          margin-bottom: 40px;
          padding: 20px 0;
        }

        .header h1 {
          color: #1e40af;
          font-size: 2.2rem;
          margin-bottom: 10px;
          position: relative;
          display: inline-block;
          padding-bottom: 15px;
        }

        .header h1::after {
          content: "";
          position: absolute;
          bottom: 0;
          left: 50%;
          transform: translateX(-50%);
          width: 60%;
          height: 4px;
          background: linear-gradient(90deg, #1e40af, #3b82f6);
          border-radius: 2px;
        }

        .header p {
          color: #6b7280;
          font-size: 1.1rem;
        }

        /* Filtres */
        .filters {
          background-color: white;
          border-radius: 12px;
          padding: 20px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
          margin-bottom: 30px;
          display: flex;
          flex-wrap: wrap;
          gap: 20px;
          align-items: center;
        }

        .search-container {
          flex: 1;
          min-width: 200px;
          position: relative;
        }

        .search-input {
          width: 100%;
          padding: 12px 20px 12px 45px;
          border: 1px solid #d1d5db;
          border-radius: 8px;
          font-size: 1rem;
          transition: all 0.3s ease;
        }

        .search-input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.3);
        }

        .search-icon {
          position: absolute;
          left: 15px;
          top: 50%;
          transform: translateY(-50%);
          color: #9ca3af;
        }

        /* Grille de formations */
        .formations-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
          gap: 25px;
          margin-bottom: 30px;
        }

        .formation-card {
          background-color: white;
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          display: flex;
          flex-direction: column;
        }

        .formation-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
