        .formation-header {
          padding: 20px;
          background-color: #ebf5ff;
          border-bottom: 1px solid #dbeafe;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .formation-header h3 {
          margin: 0;
          color: #1e40af;
          font-size: 1.2rem;
        }

        .places-badge {
          background-color: #bfdbfe;
          color: #1e40af;
          padding: 4px 8px;
          border-radius: 999px;
          font-size: 0.8rem;
          font-weight: 500;
        }

