          transform: translateX(-50%);
          border-radius: 12px;
          padding: 5px;
          box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
          z-index: 1000;
          animation: slideUp 0.3s ease, fadeOut 0.5s ease 2.5s;
          min-width: 300px;
          max-width: 600px;
        }

        .toast-message.success {
          background-color: #34d399;
          color: white;
        }

        .toast-message.error {
          background-color: #f87171;
          color: white;
        }

        .toast-content {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 15px 20px;
        }

        .toast-icon {
          font-size: 1.2rem;
          background-color: white;
          width: 30px;
          height: 30px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
        }

        .toast-message.success .toast-icon {
          color: #34d399;
        }

        .toast-message.error .toast-icon {
          color: #f87171;
        }

        .toast-content p {
          margin: 0;
          flex-grow: 1;
        }

        /* Historique des demandes */
        .demandes-section {
          background-color: white;
          border-radius: 12px;
          padding: 20px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          margin-top: 40px;
        }

        .demandes-section h2 {
          color: #1e40af;
          margin-top: 0;
          margin-bottom: 20px;
          font-size: 1.5rem;
        }

        .demandes-list table {
          width: 100%;
          border-collapse: collapse;
        }

        .demandes-list th {
          text-align: left;
          padding: 12px;
          border-bottom: 2px solid #e5e7eb;
          color: #4b5563;
          font-weight: 600;
        }

        .demandes-list td {
          padding: 12px;
          border-bottom: 1px solid #e5e7eb;
        }

        .status-badge {
          display: inline-block;
          padding: 4px 8px;
          border-radius: 999px;
          font-size: 0.8rem;
          font-weight: 500;
        }

        .status-badge.en-attente {
          background-color: #fef3c7;
          color: #92400e;
        }

        .status-badge.approuvée {
          background-color: #d1fae5;
          color: #065f46;
        }

        .status-badge.rejetée {
          background-color: #fee2e2;
          color: #b91c1c;
        }

        /* Animations */
        @keyframes slideUp {
          from {
            transform: translate(-50%, 100%);
            opacity: 0;
          }
          to {
            transform: translate(-50%, 0);
            opacity: 1;
          }
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }

        /* Responsive */
        @media (max-width: 768px) {
          .formations-grid {
            grid-template-columns: 1fr;
          }

          .filters {
            flex-direction: column;
            align-items: stretch;
          }
          
          .toast-message {
            width: 90%;
          }
        }
        `}
      </style>
    </div>
  );
}

export default CardDemandeFormation;
